import { Request, Response, NextFunction } from 'express';
import jwt from 'jsonwebtoken';
import crypto from 'crypto';

export interface AuthConfig {
  method: 'api_key' | 'jwt_token' | 'local_only' | 'oauth2';
  apiKeys: string[];
  jwtSecret: string;
  localOnly: boolean;
  oauth2: {
    providers: string[];
    clientId?: string;
    clientSecret?: string;
    redirectUri?: string;
    scope?: string;
  };
  roles: {
    admin: string[];
    agent: string[];
    readonly: string[];
    limited: string[];
  };
  quotas: {
    admin: {
      requestsPerMinute: number;
      maxConcurrentSessions: number;
    };
    agent: {
      requestsPerMinute: number;
      maxConcurrentSessions: number;
    };
    readonly: {
      requestsPerMinute: number;
      maxConcurrentSessions: number;
    };
    limited: {
      requestsPerMinute: number;
      maxConcurrentSessions: number;
    };
  };
}

export interface AuthenticatedUser {
  id: string;
  role: string;
  permissions: string[];
  method: string;
}

export interface AuthenticatedRequest extends Request {
  user?: AuthenticatedUser;
}

/**
 * Authentication middleware factory
 */
export function authMiddleware(config: AuthConfig) {
  return async (req: AuthenticatedRequest, res: Response, next: NextFunction) => {
    try {
      // Skip authentication for health check and documentation endpoints
      if (req.path === '/health' || req.path === '/api' || req.path === '/api/docs') {
        return next();
      }

      // Local-only mode: restrict to localhost
      if (config.localOnly || config.method === 'local_only') {
        const clientIP = req.ip || req.connection?.remoteAddress || '';
        const isLocalhost =
          ['127.0.0.1', '::1', '::ffff:127.0.0.1'].includes(clientIP) ||
          clientIP.startsWith('127.') ||
          clientIP === 'localhost';

        if (isLocalhost) {
          // Grant admin permissions for localhost in local-only mode
          req.user = {
            id: 'localhost',
            role: 'admin',
            permissions: ['*'],
            method: 'local_only',
          };
          return next();
        } else if (config.localOnly) {
          return res.status(403).json({
            error: 'Forbidden',
            message: 'API access restricted to localhost only',
          });
        }
      }

      // Try different authentication methods
      let user: AuthenticatedUser | null = null;

      // 1. Try API Key authentication
      if (config.method === 'api_key') {
        user = await tryApiKeyAuth(req, config);
        if (user) {
          req.user = user;
          return next();
        }
      }

      // 2. Try JWT authentication
      if (config.method === 'jwt_token') {
        user = await tryJwtAuth(req, config);
        if (user) {
          req.user = user;
          return next();
        }
      }

      // 3. Try OAuth2 authentication
      if (config.method === 'oauth2') {
        user = await tryOAuth2Auth(req, config);
        if (user) {
          req.user = user;
          return next();
        }
      }

      // No valid authentication found
      return res.status(401).json({
        error: 'Unauthorized',
        message: 'Valid authentication required',
        supportedMethods: [config.method],
        hints: {
          api_key: 'Include X-API-Key header',
          jwt_token: 'Include Authorization: Bearer <token> header',
          local_only: 'Access from localhost only',
        },
      });
    } catch (error: unknown) {
      console.error('[AuthMiddleware] Authentication error:', error);
      return res.status(500).json({
        error: 'Internal Server Error',
        message: 'Authentication system error',
      });
    }
  };
}

/**
 * Try API Key authentication
 */
async function tryApiKeyAuth(req: Request, config: AuthConfig): Promise<AuthenticatedUser | null> {
  const apiKey = req.headers['x-api-key'] as string;
  if (!apiKey) return null;

  // Check against configured API keys
  if (config.apiKeys.includes(apiKey)) {
    return {
      id: `api_key_${apiKey.substring(0, 8)}`,
      role: 'agent', // Default role for API key users
      permissions: getDefaultPermissions('agent'),
      method: 'api_key',
    };
  }

  return null;
}

/**
 * Try JWT authentication
 */
async function tryJwtAuth(req: Request, config: AuthConfig): Promise<AuthenticatedUser | null> {
  const authHeader = req.headers.authorization;
  if (!authHeader || !authHeader.startsWith('Bearer ')) return null;

  const token = authHeader.substring(7);

  try {
    const decoded = jwt.verify(token, config.jwtSecret) as Record<string, unknown>;

    return {
      id: (decoded.sub as string) || (decoded.id as string) || 'jwt_user',
      role: (decoded.role as string) || 'agent',
      permissions:
        (decoded.permissions as string[]) ||
        getDefaultPermissions((decoded.role as string) || 'agent'),
      method: 'jwt_token',
    };
  } catch (error: unknown) {
    console.error('[AuthMiddleware] JWT verification failed:', error);
    return null;
  }
}

/**
 * Try OAuth2 authentication (placeholder implementation)
 */
async function tryOAuth2Auth(req: Request, config: AuthConfig): Promise<AuthenticatedUser | null> {
  // This is a placeholder for OAuth2 implementation
  // In a full implementation, this would:
  // 1. Extract the OAuth2 token from the Authorization header
  // 2. Validate the token with the OAuth2 provider
  // 3. Extract user information from the token or provider API
  // 4. Map provider roles to internal permissions

  const authHeader = req.headers.authorization;
  if (!authHeader || !authHeader.startsWith('Bearer ')) return null;

  // For now, return null to indicate OAuth2 is not implemented
  return null;
}

/**
 * Get default permissions for a role
 */
function getDefaultPermissions(role: string): string[] {
  const rolePermissions: { [key: string]: string[] } = {
    admin: ['*'],
    agent: [
      'query:execute',
      'batch:execute',
      'session:create',
      'session:read',
      'session:delete_own',
      'reasoning:*',
      'history:read_own',
    ],
    readonly: ['query:execute_readonly', 'session:read', 'history:read_own', 'status:read'],
    limited: ['query:execute_simple', 'session:read_own'],
  };

  return rolePermissions[role] || rolePermissions['limited'] || [];
}

/**
 * Generate a new API key
 */
export function generateApiKey(): string {
  return crypto.randomBytes(32).toString('hex');
}

/**
 * Hash an API key for secure storage
 */
export function hashApiKey(apiKey: string): string {
  return crypto.createHash('sha256').update(apiKey).digest('hex');
}

/**
 * Generate a JWT token
 */
export function generateJwtToken(payload: any, config: AuthConfig): string {
  return jwt.sign(payload, config.jwtSecret, {
    expiresIn: '1h',
    issuer: 'vscode-prolog-toolkit',
    audience: 'ai-agents',
  });
}

/**
 * Verify JWT token
 */
export function verifyJwtToken(token: string, config: AuthConfig): Record<string, unknown> {
  const decoded = jwt.verify(token, config.jwtSecret);
  if (typeof decoded === 'string') {
    throw new Error('Invalid JWT token format');
  }
  return decoded as Record<string, unknown>;
}

/**
 * Permission checking utility
 */
export function hasPermission(user: AuthenticatedUser | undefined, permission: string): boolean {
  if (!user) return false;

  // Admin role has all permissions
  if (user.role === 'admin' || user.permissions.includes('*')) return true;

  // Check exact permission
  if (user.permissions.includes(permission)) return true;

  // Check wildcard permissions (e.g., 'reasoning:*' matches 'reasoning:clp')
  const wildcardPermissions = user.permissions.filter(p => p.endsWith(':*'));
  for (const wildcardPerm of wildcardPermissions) {
    const prefix = wildcardPerm.slice(0, -1); // Remove '*'
    if (permission.startsWith(prefix)) return true;
  }

  return false;
}

/**
 * Resource quota middleware
 */
export function resourceQuotaMiddleware(quotas: {
  [role: string]: { requests_per_minute?: number; maxConcurrentSessions?: number };
}) {
  return (req: AuthenticatedRequest, res: Response, next: NextFunction) => {
    const user = req.user;
    if (!user) return next();

    const userQuota = quotas[user.role] || quotas['default'];
    if (!userQuota) return next();

    // Add quota information to request for later use
    (req as AuthenticatedRequest & { quota?: typeof userQuota }).quota = userQuota;

    // Add quota headers to response
    res.set({
      'X-Rate-Limit-Limit': userQuota.requests_per_minute?.toString() || '60',
      'X-Rate-Limit-Remaining': '60', // This would be calculated based on actual usage
      'X-Rate-Limit-Reset': (Date.now() + 60000).toString(),
    });

    next();
  };
}

/**
 * Audit logging middleware
 */
export function auditMiddleware() {
  return (req: AuthenticatedRequest, res: Response, next: NextFunction) => {
    const start = Date.now();

    // Log request
    console.log(
      `[Audit] ${req.method} ${req.path} - User: ${req.user?.id || 'anonymous'} (${req.user?.role || 'none'})`
    );

    // Log response when finished
    res.on('finish', () => {
      const duration = Date.now() - start;
      const logLevel = res.statusCode >= 400 ? 'WARN' : 'INFO';

      console.log(
        `[Audit] ${logLevel} ${req.method} ${req.path} - ${res.statusCode} (${duration}ms) - User: ${req.user?.id || 'anonymous'}`
      );

      // Log security events
      if (res.statusCode === 401) {
        console.log(`[Security] Authentication failed - ${req.ip} - ${req.method} ${req.path}`);
      } else if (res.statusCode === 403) {
        console.log(
          `[Security] Authorization denied - User: ${req.user?.id} - ${req.method} ${req.path}`
        );
      } else if (res.statusCode === 429) {
        console.log(`[Security] Rate limit exceeded - User: ${req.user?.id} - ${req.ip}`);
      }
    });

    next();
  };
}

import { Router, Request, Response } from 'express';
import { PrologBackend } from '../prologBackend';
import { v4 as uuidv4 } from 'uuid';

export interface AuthenticatedRequest extends Request {
  user?: {
    id: string;
    role: string;
    permissions: string[];
  };
}

/**
 * Create API routes for Prolog operations
 */
export function apiRoutes(prologBackend: PrologBackend): Router {
  const router = Router();

  // Query execution endpoint
  router.post('/query', async (req: AuthenticatedRequest, res: Response): Promise<void> => {
    try {
      const { query, session_id, options = {} } = req.body;

      if (!query) {
        res.status(400).json({
          error: 'Bad Request',
          message: 'Query parameter is required',
        });
        return;
      }

      // Check permissions
      if (!hasPermission(req.user, 'query:execute')) {
        res.status(403).json({
          error: 'Forbidden',
          message: 'Insufficient permissions for query execution',
        });
        return;
      }

      const queryId = uuidv4();
      const params = {
        goal: query,
        timeoutMs: options.timeout || 30000,
        max_results: options.max_results || 100,
        streaming: options.stream || false,
        session_id: session_id || 'default',
        ...options,
      };

      // Execute query with notifications if callback provided
      const result = await prologBackend.sendRequestWithNotifications('query', params, {
        onProgress: status => {
          // In a full implementation, this would use WebSocket to notify client
          console.log(`[ApiRoutes] Query ${queryId} progress: ${status.progress}%`);
        },
        onComplete: status => {
          console.log(`[ApiRoutes] Query ${queryId} completed`);
        },
        onError: status => {
          console.error(`[ApiRoutes] Query ${queryId} error:`, status.error);
        },
      });

      res.json({
        query_id: queryId,
        success: result.status === 'ok',
        results: result.results || [],
        execution_time: result.execution_time || 0,
        more_available: result.streaming_info?.has_more || false,
        cursor: result.streaming_info?.next_offset || null,
        streaming_info: result.streaming_info,
      });
    } catch (error: unknown) {
      console.error('[ApiRoutes] Query execution error:', error);
      res.status(500).json({
        error: 'Internal Server Error',
        message: error instanceof Error ? error.message : 'Query execution failed',
      });
    }
  });

  // Batch query execution endpoint
  router.post('/batch', async (req: AuthenticatedRequest, res: Response): Promise<void> => {
    try {
      const { queries, session_id, batch_options = {} } = req.body;

      if (!Array.isArray(queries) || queries.length === 0) {
        res.status(400).json({
          error: 'Bad Request',
          message: 'Queries array is required and must not be empty',
        });
        return;
      }

      // Check permissions
      if (!hasPermission(req.user, 'batch:execute')) {
        res.status(403).json({
          error: 'Forbidden',
          message: 'Insufficient permissions for batch execution',
        });
        return;
      }

      const batchId = uuidv4();
      const batchRequests = queries.map((query: { query?: string; timeout?: number } | string) => ({
        cmd: 'query',
        params: {
          goal: typeof query === 'string' ? query : query.query || query,
          timeoutMs:
            typeof query === 'string'
              ? batch_options.timeout || 30000
              : query.timeout || batch_options.timeout || 30000,
          session_id: session_id || 'default',
        },
      }));

      const results = await prologBackend.sendRequest(batchRequests);

      res.json({
        batch_id: batchId,
        results: results.map(
          (result: { status: string; results?: unknown[]; error?: string }, index: number) => ({
            query_index: index,
            success: result.status === 'ok',
            results: result.results || [],
            error: result.error || null,
          })
        ),
        total_queries: queries.length,
        successful_queries: results.filter((r: { status: string }) => r.status === 'ok').length,
      });
    } catch (error: unknown) {
      console.error('[ApiRoutes] Batch execution error:', error);
      res.status(500).json({
        error: 'Internal Server Error',
        message: error instanceof Error ? error.message : 'Batch execution failed',
      });
    }
  });

  // Session management endpoints
  router.get('/sessions', async (req: AuthenticatedRequest, res: Response): Promise<void> => {
    try {
      if (!hasPermission(req.user, 'session:read')) {
        res.status(403).json({
          error: 'Forbidden',
          message: 'Insufficient permissions to list sessions',
        });
        return;
      }

      const listOptions: {
        userId?: string;
        agentId?: string;
        isActive?: boolean;
        includeInactive?: boolean;
      } = {
        includeInactive: req.query.include_inactive === 'true',
      };

      if (req.user?.id) {
        listOptions.userId = req.user.id;
      }

      const sessions = prologBackend.listSessions(listOptions);

      res.json({
        sessions: sessions.map(session => ({
          session_id: session.sessionId,
          name: session.config.name,
          description: session.config.description,
          created_at: session.config.createdAt,
          is_active: session.config.isActive,
          user_id: session.config.userId,
        })),
        total: sessions.length,
      });
    } catch (error: unknown) {
      console.error('[ApiRoutes] Session list error:', error);
      res.status(500).json({
        error: 'Internal Server Error',
        message: error instanceof Error ? error.message : 'Failed to list sessions',
      });
    }
  });

  router.post('/sessions', async (req: AuthenticatedRequest, res: Response): Promise<void> => {
    try {
      const { name, description, config = {} } = req.body;

      if (!name) {
        res.status(400).json({
          error: 'Bad Request',
          message: 'Session name is required',
        });
        return;
      }

      if (!hasPermission(req.user, 'session:create')) {
        res.status(403).json({
          error: 'Forbidden',
          message: 'Insufficient permissions to create sessions',
        });
        return;
      }

      const sessionOptions: Record<string, unknown> = {
        ...config,
      };

      if (description) {
        sessionOptions.description = description;
      }

      if (req.user?.id) {
        sessionOptions.userId = req.user.id;
      }

      if (req.headers['user-agent']) {
        sessionOptions.agentId = req.headers['user-agent'];
      }

      const sessionId = await prologBackend.createSession(name, sessionOptions);

      res.status(201).json({
        session_id: sessionId,
        name,
        description,
        created_at: new Date().toISOString(),
        message: 'Session created successfully',
      });
    } catch (error: unknown) {
      console.error('[ApiRoutes] Session creation error:', error);
      res.status(500).json({
        error: 'Internal Server Error',
        message: error instanceof Error ? error.message : 'Failed to create session',
      });
    }
  });

  router.get(
    '/sessions/:session_id',
    async (req: AuthenticatedRequest, res: Response): Promise<void> => {
      try {
        const { session_id } = req.params;

        if (!hasPermission(req.user, 'session:read')) {
          res.status(403).json({
            error: 'Forbidden',
            message: 'Insufficient permissions to read session details',
          });
          return;
        }

        if (!session_id) {
          res.status(400).json({
            error: 'Bad Request',
            message: 'Session ID is required',
          });
          return;
        }

        const session = prologBackend.getSession(session_id);
        if (!session) {
          res.status(404).json({
            error: 'Not Found',
            message: 'Session not found',
          });
          return;
        }

        res.json({
          session_id,
          config: session.config,
          state: session.state,
          statistics: await prologBackend.getSessionStatistics(session_id),
        });
      } catch (error: unknown) {
        console.error('[ApiRoutes] Session details error:', error);
        res.status(500).json({
          error: 'Internal Server Error',
          message: error instanceof Error ? error.message : 'Failed to get session details',
        });
      }
    }
  );

  router.delete(
    '/sessions/:session_id',
    async (req: AuthenticatedRequest, res: Response): Promise<void> => {
      try {
        const { session_id } = req.params;

        if (!hasPermission(req.user, 'session:delete')) {
          res.status(403).json({
            error: 'Forbidden',
            message: 'Insufficient permissions to delete sessions',
          });
          return;
        }

        if (!session_id) {
          res.status(400).json({
            error: 'Bad Request',
            message: 'Session ID is required',
          });
          return;
        }

        const deleted = await prologBackend.deleteSession(session_id);
        if (!deleted) {
          res.status(404).json({
            error: 'Not Found',
            message: 'Session not found',
          });
          return;
        }

        res.json({
          session_id,
          message: 'Session deleted successfully',
        });
      } catch (error: unknown) {
        console.error('[ApiRoutes] Session deletion error:', error);
        res.status(500).json({
          error: 'Internal Server Error',
          message: error instanceof Error ? error.message : 'Failed to delete session',
        });
      }
    }
  );

  // Session state management
  router.get(
    '/sessions/:session_id/state',
    async (req: AuthenticatedRequest, res: Response): Promise<void> => {
      try {
        const { session_id } = req.params;

        if (!session_id) {
          res.status(400).json({
            error: 'Bad Request',
            message: 'Session ID is required',
          });
          return;
        }

        if (!hasPermission(req.user, 'session:read')) {
          res.status(403).json({
            error: 'Forbidden',
            message: 'Insufficient permissions to read session state',
          });
          return;
        }

        const session = prologBackend.getSession(session_id);
        if (!session) {
          res.status(404).json({
            error: 'Not Found',
            message: 'Session not found',
          });
          return;
        }

        res.json({
          session_id,
          state: session.state,
          exported_at: new Date().toISOString(),
        });
      } catch (error: unknown) {
        console.error('[ApiRoutes] Session state export error:', error);
        res.status(500).json({
          error: 'Internal Server Error',
          message: error instanceof Error ? error.message : 'Failed to export session state',
        });
      }
    }
  );

  router.post(
    '/sessions/:session_id/state',
    async (req: AuthenticatedRequest, res: Response): Promise<void> => {
      try {
        const { session_id } = req.params;
        const { state } = req.body;

        if (!session_id) {
          res.status(400).json({
            error: 'Bad Request',
            message: 'Session ID is required',
          });
          return;
        }

        if (!hasPermission(req.user, 'session:write')) {
          res.status(403).json({
            error: 'Forbidden',
            message: 'Insufficient permissions to modify session state',
          });
          return;
        }

        await prologBackend.saveSessionState(session_id, state);

        res.json({
          session_id,
          message: 'Session state imported successfully',
          imported_at: new Date().toISOString(),
        });
      } catch (error: unknown) {
        console.error('[ApiRoutes] Session state import error:', error);
        res.status(500).json({
          error: 'Internal Server Error',
          message: error instanceof Error ? error.message : 'Failed to import session state',
        });
      }
    }
  );

  // Advanced reasoning endpoints
  router.post('/reasoning/clp', async (req: AuthenticatedRequest, res: Response): Promise<void> => {
    try {
      const { constraints, domain = 'fd', variables } = req.body;

      if (!constraints || !variables) {
        res.status(400).json({
          error: 'Bad Request',
          message: 'Constraints and variables are required',
        });
        return;
      }

      if (!hasPermission(req.user, 'reasoning:clp')) {
        res.status(403).json({
          error: 'Forbidden',
          message: 'Insufficient permissions for CLP reasoning',
        });
        return;
      }

      const result = await prologBackend.sendRequest('clp_solve', {
        domain,
        variables,
        constraints,
      });

      res.json({
        success: result.status === 'ok',
        domain,
        solution: result.solution || [],
        error: result.error || null,
      });
    } catch (error: unknown) {
      console.error('[ApiRoutes] CLP reasoning error:', error);
      res.status(500).json({
        error: 'Internal Server Error',
        message: error instanceof Error ? error.message : 'CLP reasoning failed',
      });
    }
  });

  router.post(
    '/reasoning/probabilistic',
    async (req: AuthenticatedRequest, res: Response): Promise<void> => {
      try {
        const { facts, query, samples = 1000 } = req.body;

        if (!query) {
          res.status(400).json({
            error: 'Bad Request',
            message: 'Query is required for probabilistic inference',
          });
          return;
        }

        if (!hasPermission(req.user, 'reasoning:probabilistic')) {
          res.status(403).json({
            error: 'Forbidden',
            message: 'Insufficient permissions for probabilistic reasoning',
          });
          return;
        }

        // Add probabilistic facts if provided
        if (facts && Array.isArray(facts)) {
          for (const fact of facts) {
            await prologBackend.sendRequest('probabilistic_fact', {
              fact: fact.fact,
              probability: fact.probability,
            });
          }
        }

        const result = await prologBackend.sendRequest('probabilistic_query', {
          goal: query,
          samples,
          method: 'monte_carlo',
        });

        res.json({
          success: result.status === 'ok',
          query,
          probability: result.probability || 0,
          evidence: result.evidence || {},
          samples,
          error: result.error || null,
        });
      } catch (error: unknown) {
        console.error('[ApiRoutes] Probabilistic reasoning error:', error);
        res.status(500).json({
          error: 'Internal Server Error',
          message: error instanceof Error ? error.message : 'Probabilistic reasoning failed',
        });
      }
    }
  );

  router.post('/reasoning/n3', async (req: AuthenticatedRequest, res: Response): Promise<void> => {
    try {
      const { rules, data, query } = req.body;

      if (!query) {
        res.status(400).json({
          error: 'Bad Request',
          message: 'Query is required for N3 reasoning',
        });
        return;
      }

      if (!hasPermission(req.user, 'reasoning:n3')) {
        res.status(403).json({
          error: 'Forbidden',
          message: 'Insufficient permissions for N3 reasoning',
        });
        return;
      }

      // Load N3 data if provided
      if (data) {
        await prologBackend.sendRequest('n3_load', {
          content: data,
        });
      }

      const result = await prologBackend.sendRequest('n3_reason', {
        goal: query,
      });

      res.json({
        success: result.status === 'ok',
        query,
        results: result.results || [],
        inferred_triples: result.inferred_triples || [],
        count: result.count || 0,
        error: result.error || null,
      });
    } catch (error: unknown) {
      console.error('[ApiRoutes] N3 reasoning error:', error);
      res.status(500).json({
        error: 'Internal Server Error',
        message: error instanceof Error ? error.message : 'N3 reasoning failed',
      });
    }
  });

  // Query history endpoint
  router.get('/history', async (req: AuthenticatedRequest, res: Response): Promise<void> => {
    try {
      if (!hasPermission(req.user, 'history:read')) {
        res.status(403).json({
          error: 'Forbidden',
          message: 'Insufficient permissions to read query history',
        });
        return;
      }

      const { session_id, limit = 50, offset = 0, status } = req.query;

      const filter: Record<string, unknown> = {
        limit: parseInt(limit as string, 10),
        offset: parseInt(offset as string, 10),
      };

      if (session_id) filter.session_id = session_id;
      if (status) filter.status = status;
      if (req.user?.id) filter.user_id = req.user.id;

      const history = await prologBackend.getQueryHistory(filter);

      res.json({
        queries: history.queries || [],
        total: history.total || 0,
        limit: filter.limit,
        offset: filter.offset,
        has_more: (history.total || 0) > (filter.offset as number) + (filter.limit as number),
      });
    } catch (error: unknown) {
      console.error('[ApiRoutes] Query history error:', error);
      res.status(500).json({
        error: 'Internal Server Error',
        message: error instanceof Error ? error.message : 'Failed to retrieve query history',
      });
    }
  });

  // System status endpoint
  router.get('/status', async (req: AuthenticatedRequest, res: Response): Promise<void> => {
    try {
      if (!hasPermission(req.user, 'status:read')) {
        res.status(403).json({
          error: 'Forbidden',
          message: 'Insufficient permissions to read system status',
        });
        return;
      }

      const backendStatus = prologBackend.getConcurrencyStatus();
      const queryStats = prologBackend.getQueryStatistics();
      const schedulerStats = prologBackend.getSchedulerStatistics();

      res.json({
        backend: {
          running: prologBackend.isRunning(),
          active_queries: queryStats.running || 0,
          active_sessions: prologBackend.listSessions().length,
          resource_usage: backendStatus.resourceUsage || {},
        },
        scheduler: schedulerStats,
        query_statistics: queryStats,
        timestamp: new Date().toISOString(),
      });
    } catch (error: unknown) {
      console.error('[ApiRoutes] Status endpoint error:', error);
      res.status(500).json({
        error: 'Internal Server Error',
        message: error instanceof Error ? error.message : 'Failed to retrieve system status',
      });
    }
  });

  return router;
}

/**
 * Check if user has required permission
 */
function hasPermission(
  user: { role?: string; permissions?: string[] } | undefined,
  permission: string
): boolean {
  if (!user) return false;

  // Admin role has all permissions
  if (user.role === 'admin') return true;

  // Check specific permissions
  return user.permissions?.includes(permission) ?? false;
}

import express, { Express, Request, Response, NextFunction } from 'express';
import cors from 'cors';
import helmet from 'helmet';
import rateLimit from 'express-rate-limit';
import { Server } from 'http';
import { PrologBackend } from '../prologBackend';
import { apiRoutes } from './apiRoutes';
import { authMiddleware, AuthConfig } from './apiMiddleware';

export interface ApiServerConfig {
  enabled: boolean;
  port: number;
  host: string;
  corsOrigins: string[];
  maxConnections: number;
  requestTimeout: number;
  rateLimiting: {
    enabled: boolean;
    requestsPerMinute: number;
    burstLimit: number;
  };
  auth: AuthConfig;
}

export interface ApiServerOptions {
  config: ApiServerConfig;
  prologBackend: PrologBackend;
}

/**
 * Express.js-based HTTP API server for external AI agent access
 * Provides RESTful endpoints for all Prolog operations with security middleware
 */
export class ApiServer {
  private app: Express;
  private server: Server | null = null;
  private config: ApiServerConfig;
  private prologBackend: PrologBackend;
  private isRunning: boolean = false;

  constructor(options: ApiServerOptions) {
    this.config = options.config;
    this.prologBackend = options.prologBackend;
    this.app = express();
    this.setupMiddleware();
    this.setupRoutes();
    this.setupErrorHandling();
  }

  /**
   * Set up Express middleware stack
   */
  private setupMiddleware(): void {
    // Security middleware
    this.app.use(
      helmet({
        contentSecurityPolicy: {
          directives: {
            defaultSrc: ["'self'"],
            scriptSrc: ["'self'"],
            styleSrc: ["'self'", "'unsafe-inline'"],
            imgSrc: ["'self'", 'data:', 'https:'],
          },
        },
        crossOriginEmbedderPolicy: false, // Allow WebSocket connections
      })
    );

    // CORS configuration
    this.app.use(
      cors({
        origin: (origin, callback) => {
          // Allow requests with no origin (like mobile apps or curl requests)
          if (!origin) return callback(null, true);

          if (this.config.corsOrigins.includes('*') || this.config.corsOrigins.includes(origin)) {
            return callback(null, true);
          }

          callback(new Error('Not allowed by CORS'));
        },
        credentials: true,
        methods: ['GET', 'POST', 'PUT', 'DELETE', 'OPTIONS'],
        allowedHeaders: ['Content-Type', 'Authorization', 'X-API-Key'],
      })
    );

    // Rate limiting
    if (this.config.rateLimiting.enabled) {
      const limiter = rateLimit({
        windowMs: 60 * 1000, // 1 minute
        max: this.config.rateLimiting.requestsPerMinute,
        message: {
          error: 'Too many requests',
          message: `Rate limit exceeded. Maximum ${this.config.rateLimiting.requestsPerMinute} requests per minute.`,
          retryAfter: 60,
        },
        standardHeaders: true,
        legacyHeaders: false,
        // Allow burst requests up to the burst limit
        skip: req => {
          const burstKey = `burst_${req.ip}`;
          // Simple burst tracking (in production, use Redis or similar)
          return false; // For now, apply rate limiting to all requests
        },
      });
      this.app.use('/api/', limiter);
    }

    // Request parsing
    this.app.use(express.json({ limit: '10mb' }));
    this.app.use(express.urlencoded({ extended: true, limit: '10mb' }));

    // Request timeout
    this.app.use((req: Request, res: Response, next: NextFunction) => {
      req.setTimeout(this.config.requestTimeout, () => {
        res.status(408).json({
          error: 'Request timeout',
          message: `Request exceeded ${this.config.requestTimeout}ms timeout`,
        });
      });
      next();
    });

    // Authentication middleware
    this.app.use('/api/', authMiddleware(this.config.auth));

    // Request logging
    this.app.use((req: Request, res: Response, next: NextFunction) => {
      const start = Date.now();
      console.log(`[ApiServer] ${req.method} ${req.path} - ${req.ip}`);

      res.on('finish', () => {
        const duration = Date.now() - start;
        console.log(`[ApiServer] ${req.method} ${req.path} - ${res.statusCode} (${duration}ms)`);
      });

      next();
    });
  }

  /**
   * Set up API routes
   */
  private setupRoutes(): void {
    // Health check endpoint (no auth required)
    this.app.get('/health', (req: Request, res: Response) => {
      res.json({
        status: 'healthy',
        timestamp: new Date().toISOString(),
        version: '1.0.0',
        backend: {
          running: this.prologBackend.isRunning(),
          port: this.prologBackend['port'] || 3060,
        },
      });
    });

    // API documentation endpoint
    this.app.get('/api', (req: Request, res: Response) => {
      res.json({
        name: 'VSCode Prolog Toolkit API',
        version: '1.0.0',
        description: 'RESTful API for Prolog operations and AI agent integration',
        documentation: '/api/docs',
        endpoints: {
          query: 'POST /api/v1/query',
          batch: 'POST /api/v1/batch',
          sessions: 'GET|POST /api/v1/sessions',
          reasoning: {
            clp: 'POST /api/v1/reasoning/clp',
            probabilistic: 'POST /api/v1/reasoning/probabilistic',
            n3: 'POST /api/v1/reasoning/n3',
          },
          history: 'GET /api/v1/history',
          status: 'GET /api/v1/status',
        },
      });
    });

    // Mount API routes
    this.app.use('/api/v1', apiRoutes(this.prologBackend));
  }

  /**
   * Set up error handling middleware
   */
  private setupErrorHandling(): void {
    // 404 handler
    this.app.use('*', (req: Request, res: Response) => {
      res.status(404).json({
        error: 'Not Found',
        message: `Endpoint ${req.method} ${req.originalUrl} not found`,
        availableEndpoints: [
          'GET /health',
          'GET /api',
          'POST /api/v1/query',
          'POST /api/v1/batch',
          'GET /api/v1/sessions',
          'POST /api/v1/sessions',
          'GET /api/v1/status',
        ],
      });
    });

    // Global error handler
    this.app.use((error: Error, req: Request, res: Response, next: NextFunction) => {
      console.error(`[ApiServer] Error in ${req.method} ${req.path}:`, error);

      // Don't send error details in production
      const isDevelopment = process.env.NODE_ENV !== 'production';

      res.status(500).json({
        error: 'Internal Server Error',
        message: isDevelopment ? error.message : 'An unexpected error occurred',
        ...(isDevelopment && { stack: error.stack }),
      });
    });
  }

  /**
   * Start the API server
   */
  async start(): Promise<void> {
    if (this.isRunning) {
      throw new Error('API server is already running');
    }

    if (!this.config.enabled) {
      console.log('[ApiServer] API server is disabled in configuration');
      return;
    }

    return new Promise((resolve, reject) => {
      try {
        this.server = this.app.listen(this.config.port, this.config.host, () => {
          this.isRunning = true;
          console.log(
            `[ApiServer] HTTP API server started on ${this.config.host}:${this.config.port}`
          );
          console.log(
            `[ApiServer] Health check: http://${this.config.host}:${this.config.port}/health`
          );
          console.log(
            `[ApiServer] API documentation: http://${this.config.host}:${this.config.port}/api`
          );
          resolve();
        });

        this.server.on('error', (error: unknown) => {
          console.error('[ApiServer] Server error:', error);
          reject(error);
        });

        // Handle connection limits
        this.server.maxConnections = this.config.maxConnections;
      } catch (error: unknown) {
        reject(error);
      }
    });
  }

  /**
   * Stop the API server
   */
  async stop(): Promise<void> {
    if (!this.isRunning || !this.server) {
      return;
    }

    return new Promise((resolve, reject) => {
      this.server!.close(error => {
        if (error) {
          console.error('[ApiServer] Error stopping server:', error);
          reject(error);
        } else {
          this.isRunning = false;
          this.server = null;
          console.log('[ApiServer] HTTP API server stopped');
          resolve();
        }
      });
    });
  }

  /**
   * Get server status
   */
  getStatus(): {
    running: boolean;
    port: number;
    host: string;
    connections: number;
  } {
    return {
      running: this.isRunning,
      port: this.config.port,
      host: this.config.host,
      connections: (this.server as any)?.connections || 0,
    };
  }

  /**
   * Get Express app instance (for testing)
   */
  getApp(): Express {
    return this.app;
  }
}

import * as vscode from 'vscode';
import { InstallationChecker } from './installationChecker';

export interface MigrationResult {
  migrated: boolean;
  oldPath?: string;
  newPath?: string;
  backupCreated?: boolean;
  issues?: string[];
}

export interface ConfigurationBackup {
  timestamp: string;
  configuration: any;
  reason: string;
}

export class ConfigurationMigration {
  private static instance: ConfigurationMigration;
  private installationChecker: InstallationChecker;

  private constructor() {
    this.installationChecker = InstallationChecker.getInstance();
  }

  public static getInstance(): ConfigurationMigration {
    if (!ConfigurationMigration.instance) {
      ConfigurationMigration.instance = new ConfigurationMigration();
    }
    return ConfigurationMigration.instance;
  }

  /**
   * Perform automatic configuration migration
   */
  public async performMigration(): Promise<MigrationResult> {
    const config = vscode.workspace.getConfiguration('prolog');
    const currentPath = config.get<string>('executablePath', 'swipl');

    // Check if current path is valid
    const isCurrentValid = await this.installationChecker.validateSwiplPath(currentPath);

    if (isCurrentValid) {
      return {
        migrated: false,
        issues: [],
      };
    }

    // Current path is invalid, try to find a valid one
    const foundPath = await this.installationChecker.findSwiplExecutable();

    if (!foundPath) {
      return {
        migrated: false,
        oldPath: currentPath,
        issues: ['No valid SWI-Prolog installation found for migration'],
      };
    }

    // Create backup before migration
    const backupResult = await this.createConfigurationBackup('automatic_migration');

    try {
      // Update configuration
      await config.update('executablePath', foundPath, vscode.ConfigurationTarget.Global);

      return {
        migrated: true,
        oldPath: currentPath,
        newPath: foundPath,
        backupCreated: backupResult,
        issues: [],
      };
    } catch (error) {
      return {
        migrated: false,
        oldPath: currentPath,
        backupCreated: backupResult,
        issues: [`Failed to update configuration: ${error}`],
      };
    }
  }

  /**
   * Detect and handle outdated or invalid paths
   */
  public async detectOutdatedPaths(): Promise<{
    hasOutdatedPaths: boolean;
    invalidPaths: string[];
    suggestions: Array<{ path: string; version?: string }>;
  }> {
    const config = vscode.workspace.getConfiguration('prolog');
    const currentPath = config.get<string>('executablePath', 'swipl');
    const invalidPaths: string[] = [];
    const suggestions: Array<{ path: string; version?: string }> = [];

    // Check current executable path
    const isCurrentValid = await this.installationChecker.validateSwiplPath(currentPath);
    if (!isCurrentValid) {
      invalidPaths.push(currentPath);
    }

    // Check for common outdated paths
    const commonOutdatedPaths = this.getCommonOutdatedPaths();
    for (const outdatedPath of commonOutdatedPaths) {
      if (currentPath.includes(outdatedPath)) {
        invalidPaths.push(currentPath);
        break;
      }
    }

    // Find valid alternatives
    const foundPath = await this.installationChecker.findSwiplExecutable();
    if (foundPath) {
      const version = await this.installationChecker.getSwiplVersion(foundPath);
      suggestions.push({ path: foundPath, version });
    }

    return {
      hasOutdatedPaths: invalidPaths.length > 0,
      invalidPaths,
      suggestions,
    };
  }

  /**
   * Attempt to find new valid paths for invalid configurations
   */
  public async findNewValidPaths(): Promise<Array<{ path: string; version?: string }>> {
    const validPaths: Array<{ path: string; version?: string }> = [];
    const commonPaths = this.installationChecker.detectCommonInstallPaths();

    for (const path of commonPaths) {
      const isValid = await this.installationChecker.validateSwiplPath(path);
      if (isValid) {
        const version = await this.installationChecker.getSwiplVersion(path);
        validPaths.push({ path, version });
      }
    }

    return validPaths;
  }

  /**
   * Create a backup of current configuration
   */
  public async createConfigurationBackup(reason: string): Promise<boolean> {
    try {
      const config = vscode.workspace.getConfiguration('prolog');
      const backup: ConfigurationBackup = {
        timestamp: new Date().toISOString(),
        reason,
        configuration: {
          executablePath: config.get('executablePath'),
          dialect: config.get('dialect'),
          'linter.run': config.get('linter.run'),
          'linter.delay': config.get('linter.delay'),
          'linter.enableMsgInOutput': config.get('linter.enableMsgInOutput'),
          'format.addSpace': config.get('format.addSpace'),
          'terminal.runtimeArgs': config.get('terminal.runtimeArgs'),
          'telemetry.enabled': config.get('telemetry.enabled'),
        },
      };

      // Store backup in global state (VS Code's storage)
      const context = this.getExtensionContext();
      if (context) {
        const existingBackups = context.globalState.get<ConfigurationBackup[]>(
          'prologConfigBackups',
          []
        );
        existingBackups.push(backup);

        // Keep only last 10 backups
        if (existingBackups.length > 10) {
          existingBackups.splice(0, existingBackups.length - 10);
        }

        await context.globalState.update('prologConfigBackups', existingBackups);
        return true;
      }

      return false;
    } catch (error) {
      console.error('Failed to create configuration backup:', error);
      return false;
    }
  }

  /**
   * Restore configuration from backup
   */
  public async restoreConfigurationBackup(backupIndex: number = 0): Promise<boolean> {
    try {
      const context = this.getExtensionContext();
      if (!context) {
        return false;
      }

      const backups = context.globalState.get<ConfigurationBackup[]>('prologConfigBackups', []);
      if (backups.length === 0 || backupIndex >= backups.length) {
        return false;
      }

      const backup = backups[backups.length - 1 - backupIndex]; // Most recent first
      const config = vscode.workspace.getConfiguration('prolog');

      // Restore each configuration value
      for (const [key, value] of Object.entries(backup.configuration)) {
        if (value !== undefined) {
          await config.update(key, value, vscode.ConfigurationTarget.Global);
        }
      }

      return true;
    } catch (error) {
      console.error('Failed to restore configuration backup:', error);
      return false;
    }
  }

  /**
   * Get list of available backups
   */
  public getConfigurationBackups(): ConfigurationBackup[] {
    const context = this.getExtensionContext();
    if (!context) {
      return [];
    }

    return context.globalState.get<ConfigurationBackup[]>('prologConfigBackups', []);
  }

  /**
   * Handle migration of different SWI-Prolog versions
   */
  public async handleVersionMigration(
    oldVersion: string,
    newVersion: string
  ): Promise<{
    compatibilityIssues: string[];
    recommendations: string[];
  }> {
    const compatibilityIssues: string[] = [];
    const recommendations: string[] = [];

    try {
      const oldVersionParts = oldVersion.split('.').map(v => parseInt(v, 10));
      const newVersionParts = newVersion.split('.').map(v => parseInt(v, 10));

      // Check for major version changes
      if (oldVersionParts[0] !== newVersionParts[0]) {
        compatibilityIssues.push(
          `Major version change from ${oldVersionParts[0]} to ${newVersionParts[0]} may affect compatibility`
        );
        recommendations.push(
          'Review your Prolog code for compatibility with the new major version'
        );
      }

      // Check for specific version-related issues
      if (oldVersionParts[0] < 8 && newVersionParts[0] >= 8) {
        recommendations.push('SWI-Prolog 8.x introduced new features and some syntax changes');
        recommendations.push('Consider updating your code to use new string syntax if applicable');
      }

      if (oldVersionParts[0] < 9 && newVersionParts[0] >= 9) {
        recommendations.push('SWI-Prolog 9.x has improved performance and new built-in predicates');
      }
    } catch (_error) {
      compatibilityIssues.push('Unable to parse version numbers for compatibility check');
    }

    return {
      compatibilityIssues,
      recommendations,
    };
  }

  /**
   * Preserve user customizations during migration
   */
  public async preserveUserCustomizations(): Promise<{
    preserved: string[];
    issues: string[];
  }> {
    const preserved: string[] = [];
    const issues: string[] = [];

    try {
      const config = vscode.workspace.getConfiguration('prolog');

      // List of settings to preserve (non-path related)
      const settingsToPreserve = [
        'dialect',
        'linter.run',
        'linter.delay',
        'linter.enableMsgInOutput',
        'format.addSpace',
        'terminal.runtimeArgs',
        'telemetry.enabled',
      ];

      for (const setting of settingsToPreserve) {
        const value = config.get(setting);
        if (value !== undefined) {
          preserved.push(`${setting}: ${JSON.stringify(value)}`);
        }
      }
    } catch (error) {
      issues.push(`Failed to preserve customizations: ${error}`);
    }

    return {
      preserved,
      issues,
    };
  }

  /**
   * Show migration dialog to user
   */
  public async showMigrationDialog(migrationResult: MigrationResult): Promise<void> {
    if (migrationResult.migrated) {
      const action = await vscode.window.showInformationMessage(
        `Configuration migrated successfully!\n\nOld path: ${migrationResult.oldPath}\nNew path: ${migrationResult.newPath}`,
        'OK',
        'Undo Migration'
      );

      if (action === 'Undo Migration') {
        const restored = await this.restoreConfigurationBackup(0);
        if (restored) {
          vscode.window.showInformationMessage('Configuration restored from backup');
        } else {
          vscode.window.showErrorMessage('Failed to restore configuration backup');
        }
      }
    } else if (migrationResult.issues && migrationResult.issues.length > 0) {
      const issueMessage = migrationResult.issues.join('\n');
      await vscode.window.showWarningMessage(
        `Configuration migration failed:\n\n${issueMessage}`,
        'OK'
      );
    }
  }

  /**
   * Get common outdated paths that should be migrated
   */
  private getCommonOutdatedPaths(): string[] {
    return [
      '/usr/local/bin/pl', // Old SWI-Prolog executable name
      '/usr/bin/pl', // Old SWI-Prolog executable name
      'C:\\pl\\bin\\pl.exe', // Old Windows path
      '/opt/pl/', // Old installation directory
    ];
  }

  /**
   * Get extension context (this would need to be set by the extension)
   */
  private getExtensionContext(): vscode.ExtensionContext | undefined {
    // This would be set by the extension when initializing the migration system
    return (global as any).prologExtensionContext;
  }

  /**
   * Set extension context for storage operations
   */
  public setExtensionContext(context: vscode.ExtensionContext): void {
    (global as any).prologExtensionContext = context;
  }

  /**
   * Perform comprehensive migration check and handle user interaction
   */
  public async performComprehensiveMigration(): Promise<void> {
    try {
      // Check for outdated paths
      const outdatedCheck = await this.detectOutdatedPaths();

      if (outdatedCheck.hasOutdatedPaths) {
        const action = await vscode.window.showWarningMessage(
          `Outdated SWI-Prolog configuration detected.\n\nInvalid paths: ${outdatedCheck.invalidPaths.join(', ')}\n\nWould you like to automatically migrate to a valid installation?`,
          'Migrate Now',
          'Show Suggestions',
          'Skip'
        );

        switch (action) {
          case 'Migrate Now': {
            const migrationResult = await this.performMigration();
            await this.showMigrationDialog(migrationResult);
            break;
          }

          case 'Show Suggestions':
            await this.showSuggestionsDialog(outdatedCheck.suggestions);
            break;
        }
      }
    } catch (error) {
      console.error('Error during comprehensive migration:', error);
      vscode.window.showErrorMessage(`Migration check failed: ${error}`);
    }
  }

  /**
   * Show suggestions dialog for manual path selection
   */
  private async showSuggestionsDialog(
    suggestions: Array<{ path: string; version?: string }>
  ): Promise<void> {
    if (suggestions.length === 0) {
      vscode.window.showWarningMessage('No valid SWI-Prolog installations found for migration.');
      return;
    }

    const items = suggestions.map(suggestion => ({
      label: suggestion.path,
      description: suggestion.version ? `Version ${suggestion.version}` : 'Version unknown',
      path: suggestion.path,
    }));

    const selected = await vscode.window.showQuickPick(items, {
      placeHolder: 'Select a SWI-Prolog installation to use',
      ignoreFocusOut: true,
    });

    if (selected) {
      const config = vscode.workspace.getConfiguration('prolog');
      await this.createConfigurationBackup('manual_path_selection');
      await config.update('executablePath', selected.path, vscode.ConfigurationTarget.Global);
      vscode.window.showInformationMessage(`Configuration updated to use: ${selected.path}`);
    }
  }
}

import WebSocket from 'ws';
import { EventEmitter } from 'events';
import { QueryNotificationManager } from './queryNotificationManager';
import { AuthConfig, verifyJwtToken, hasPermission } from './apiMiddleware';
import { IncomingMessage } from 'http';
import { URL } from 'url';

export interface ExternalWebSocketConfig {
  enabled: boolean;
  port: number;
  maxConnections: number;
  heartbeatInterval: number;
  auth: AuthConfig;
}

export interface WebSocketClient {
  id: string;
  ws: WebSocket;
  user?: {
    id: string;
    role: string;
    permissions: string[];
    method: string;
  };
  subscriptions: Set<string>;
  lastHeartbeat: number;
  isAlive: boolean;
}

/**
 * External WebSocket manager for AI agent real-time notifications
 * Extends the existing QueryNotificationManager with external access capabilities
 */
export class ExternalWebSocketManager extends EventEmitter {
  private server: WebSocket.Server | null = null;
  private clients: Map<string, WebSocketClient> = new Map();
  private config: ExternalWebSocketConfig;
  private queryNotificationManager: QueryNotificationManager;
  private heartbeatInterval: ReturnType<typeof setInterval> | null = null;
  private isRunning: boolean = false;

  constructor(config: ExternalWebSocketConfig, queryNotificationManager: QueryNotificationManager) {
    super();
    this.config = config;
    this.queryNotificationManager = queryNotificationManager;
    this.setupQueryNotificationForwarding();
  }

  /**
   * Start the external WebSocket server
   */
  async start(): Promise<void> {
    if (this.isRunning) {
      throw new Error('External WebSocket server is already running');
    }

    if (!this.config.enabled) {
      console.log('[ExternalWebSocketManager] External WebSocket server is disabled');
      return;
    }

    return new Promise((resolve, reject) => {
      try {
        this.server = new WebSocket.Server({
          port: this.config.port,
          perMessageDeflate: false,
          maxPayload: 1024 * 1024, // 1MB max message size
          verifyClient: (info: { origin: string; secure: boolean; req: IncomingMessage }) =>
            this.verifyClient(info),
        });

        this.server.on('connection', (ws: WebSocket, request: IncomingMessage) => {
          this.handleConnection(ws, request);
        });

        this.server.on('error', error => {
          console.error('[ExternalWebSocketManager] Server error:', error);
          this.emit('error', error);
        });

        this.server.on('listening', () => {
          this.isRunning = true;
          this.startHeartbeat();
          console.log(
            `[ExternalWebSocketManager] External WebSocket server started on port ${this.config.port}`
          );
          resolve();
        });
      } catch (error: unknown) {
        reject(error);
      }
    });
  }

  /**
   * Stop the external WebSocket server
   */
  async stop(): Promise<void> {
    if (!this.isRunning) return;

    return new Promise(resolve => {
      // Stop heartbeat
      if (this.heartbeatInterval) {
        clearInterval(this.heartbeatInterval);
        this.heartbeatInterval = null;
      }

      // Close all client connections
      this.clients.forEach(client => {
        client.ws.close(1001, 'Server shutting down');
      });
      this.clients.clear();

      // Close server
      if (this.server) {
        this.server.close(() => {
          this.isRunning = false;
          console.log('[ExternalWebSocketManager] External WebSocket server stopped');
          resolve();
        });
      } else {
        resolve();
      }
    });
  }

  /**
   * Verify client connection
   */
  private verifyClient(info: { origin: string; secure: boolean; req: IncomingMessage }): boolean {
    // Check connection limits
    if (this.clients.size >= this.config.maxConnections) {
      console.log('[ExternalWebSocketManager] Connection rejected: max connections reached');
      return false;
    }

    // In local-only mode, only allow localhost connections
    if (this.config.auth.localOnly) {
      const clientIP = info.req.socket.remoteAddress || '';
      const isLocalhost =
        ['127.0.0.1', '::1', '::ffff:127.0.0.1'].includes(clientIP) ||
        clientIP.startsWith('127.') ||
        clientIP === 'localhost';

      if (!isLocalhost) {
        console.log(`[ExternalWebSocketManager] Connection rejected: non-localhost IP ${clientIP}`);
        return false;
      }
    }

    return true;
  }

  /**
   * Handle new WebSocket connection
   */
  private async handleConnection(ws: WebSocket, request: IncomingMessage): Promise<void> {
    const clientId = this.generateClientId();
    const client: WebSocketClient = {
      id: clientId,
      ws,
      subscriptions: new Set(),
      lastHeartbeat: Date.now(),
      isAlive: true,
    };

    // Authenticate the client
    try {
      const user = await this.authenticateClient(request);
      client.user = user;
    } catch (error: unknown) {
      console.log(
        `[ExternalWebSocketManager] Authentication failed for client ${clientId}:`,
        error
      );
      ws.close(1008, 'Authentication failed');
      return;
    }

    this.clients.set(clientId, client);
    console.log(
      `[ExternalWebSocketManager] Client ${clientId} connected (user: ${client.user?.id || 'anonymous'})`
    );

    // Set up client event handlers
    ws.on('message', data => {
      this.handleClientMessage(clientId, data);
    });

    ws.on('close', (code, reason) => {
      console.log(
        `[ExternalWebSocketManager] Client ${clientId} disconnected (${code}: ${reason})`
      );
      this.clients.delete(clientId);
      this.emit('clientDisconnected', clientId);
    });

    ws.on('error', error => {
      console.error(`[ExternalWebSocketManager] Client ${clientId} error:`, error);
      this.clients.delete(clientId);
    });

    ws.on('pong', () => {
      const client = this.clients.get(clientId);
      if (client) {
        client.isAlive = true;
        client.lastHeartbeat = Date.now();
      }
    });

    // Send welcome message
    this.sendToClient(clientId, {
      type: 'welcome',
      client_id: clientId,
      server_time: new Date().toISOString(),
      capabilities: ['query_notifications', 'session_events', 'system_status', 'real_time_updates'],
    });

    this.emit('clientConnected', clientId, client.user);
  }

  /**
   * Authenticate WebSocket client
   */
  private async authenticateClient(
    request: IncomingMessage
  ): Promise<{ id: string; role: string; permissions: string[]; method: string }> {
    const url = new URL(request.url || '', `ws://localhost:${this.config.port}`);

    // Try different authentication methods

    // 1. JWT token from query parameter
    const token = url.searchParams.get('token');
    if (token && this.config.auth.method === 'jwt_token') {
      try {
        const decoded = verifyJwtToken(token, this.config.auth);
        return {
          id: (decoded.sub as string) || (decoded.id as string) || 'jwt_user',
          role: (decoded.role as string) || 'agent',
          permissions:
            (decoded.permissions as string[]) ||
            this.getDefaultPermissions((decoded.role as string) || 'agent'),
          method: 'jwt_token',
        };
      } catch (error: unknown) {
        console.error('[ExternalWebSocketManager] JWT verification failed:', error);
      }
    }

    // 2. API key from query parameter
    const apiKey = url.searchParams.get('api_key');
    if (apiKey && this.config.auth.method === 'api_key') {
      const isValidKey = this.config.auth.apiKeys.includes(apiKey);
      if (isValidKey) {
        return {
          id: `api_key_${apiKey.substring(0, 8)}`,
          role: 'agent',
          permissions: this.getDefaultPermissions('agent'),
          method: 'api_key',
        };
      }
    }

    // 3. Local-only mode
    if (this.config.auth.localOnly || this.config.auth.method === 'local_only') {
      const clientIP = request.socket.remoteAddress || '';
      const isLocalhost =
        ['127.0.0.1', '::1', '::ffff:127.0.0.1'].includes(clientIP) ||
        clientIP.startsWith('127.') ||
        clientIP === 'localhost';

      if (isLocalhost) {
        return {
          id: 'localhost',
          role: 'admin',
          permissions: ['*'],
          method: 'local_only',
        };
      }
    }

    throw new Error('Authentication required');
  }

  /**
   * Handle client message
   */
  private handleClientMessage(clientId: string, data: WebSocket.Data): void {
    const client = this.clients.get(clientId);
    if (!client) return;

    try {
      const message = JSON.parse(data.toString());
      console.log(`[ExternalWebSocketManager] Message from ${clientId}:`, message.type);

      switch (message.type) {
        case 'subscribe':
          this.handleSubscribe(clientId, message);
          break;
        case 'unsubscribe':
          this.handleUnsubscribe(clientId, message);
          break;
        case 'cancel_query':
          this.handleCancelQuery(clientId, message);
          break;
        case 'get_query_status':
          this.handleGetQueryStatus(clientId, message);
          break;
        case 'get_system_status':
          this.handleGetSystemStatus(clientId, message);
          break;
        case 'ping':
          this.sendToClient(clientId, { type: 'pong', timestamp: Date.now() });
          break;
        default:
          this.sendToClient(clientId, {
            type: 'error',
            message: `Unknown message type: ${message.type}`,
          });
      }
    } catch (error: unknown) {
      console.error(`[ExternalWebSocketManager] Error parsing message from ${clientId}:`, error);
      this.sendToClient(clientId, {
        type: 'error',
        message: 'Invalid message format',
      });
    }
  }

  /**
   * Handle subscription request
   */
  private handleSubscribe(
    clientId: string,
    message: { query_id?: string; session_id?: string; event_types?: string[] }
  ): void {
    const client = this.clients.get(clientId);
    if (!client) return;

    const {
      query_id,
      session_id,
      event_types = ['progress', 'complete', 'error', 'cancel'],
    } = message;

    // Check permissions
    if (!hasPermission(client.user ?? undefined, 'notifications:subscribe')) {
      this.sendToClient(clientId, {
        type: 'error',
        message: 'Insufficient permissions to subscribe to notifications',
      });
      return;
    }

    // Add subscriptions
    if (query_id) {
      client.subscriptions.add(`query:${query_id}`);
    }
    if (session_id) {
      client.subscriptions.add(`session:${session_id}`);
    }

    event_types.forEach((eventType: string) => {
      client.subscriptions.add(`event:${eventType}`);
    });

    this.sendToClient(clientId, {
      type: 'subscribed',
      query_id,
      session_id,
      event_types,
      message: 'Successfully subscribed to notifications',
    });
  }

  /**
   * Handle unsubscribe request
   */
  private handleUnsubscribe(
    clientId: string,
    message: { query_id?: string; session_id?: string; event_types?: string[] }
  ): void {
    const client = this.clients.get(clientId);
    if (!client) return;

    const { query_id, session_id, event_types } = message;

    if (query_id) {
      client.subscriptions.delete(`query:${query_id}`);
    }
    if (session_id) {
      client.subscriptions.delete(`session:${session_id}`);
    }
    if (event_types) {
      event_types.forEach((eventType: string) => {
        client.subscriptions.delete(`event:${eventType}`);
      });
    }

    this.sendToClient(clientId, {
      type: 'unsubscribed',
      query_id,
      session_id,
      event_types,
      message: 'Successfully unsubscribed from notifications',
    });
  }

  /**
   * Handle query cancellation request
   */
  private handleCancelQuery(clientId: string, message: { query_id?: string }): void {
    const client = this.clients.get(clientId);
    if (!client || !hasPermission(client.user ?? undefined, 'query:cancel')) {
      this.sendToClient(clientId, {
        type: 'error',
        message: 'Insufficient permissions to cancel queries',
      });
      return;
    }

    const { query_id } = message;
    if (!query_id) {
      this.sendToClient(clientId, {
        type: 'error',
        message: 'Query ID is required for cancellation',
      });
      return;
    }

    // Forward to query notification manager
    const cancelled = this.queryNotificationManager.cancelQuery(query_id);

    this.sendToClient(clientId, {
      type: 'query_cancel_response',
      query_id,
      cancelled,
      message: cancelled ? 'Query cancelled successfully' : 'Query not found or already completed',
    });
  }

  /**
   * Handle query status request
   */
  private handleGetQueryStatus(clientId: string, message: { query_id?: string }): void {
    const client = this.clients.get(clientId);
    if (!client || !hasPermission(client.user ?? undefined, 'query:status')) {
      this.sendToClient(clientId, {
        type: 'error',
        message: 'Insufficient permissions to get query status',
      });
      return;
    }

    const { query_id } = message;
    const status = this.queryNotificationManager.getQueryStatus(query_id || '');

    this.sendToClient(clientId, {
      type: 'query_status_response',
      query_id,
      status: status || null,
      found: !!status,
    });
  }

  /**
   * Handle system status request
   */
  private handleGetSystemStatus(clientId: string, message: Record<string, unknown>): void {
    const client = this.clients.get(clientId);
    if (!client || !hasPermission(client.user ?? undefined, 'status:read')) {
      this.sendToClient(clientId, {
        type: 'error',
        message: 'Insufficient permissions to get system status',
      });
      return;
    }

    const stats = this.queryNotificationManager.getStatistics();

    this.sendToClient(clientId, {
      type: 'system_status_response',
      active_queries: stats.running,
      total_queries: stats.total,
      connected_clients: this.clients.size,
      server_uptime: process.uptime(),
      timestamp: new Date().toISOString(),
    });
  }

  /**
   * Send message to specific client
   */
  private sendToClient(clientId: string, message: Record<string, unknown>): void {
    const client = this.clients.get(clientId);
    if (!client || client.ws.readyState !== client.ws.OPEN) return;

    try {
      client.ws.send(JSON.stringify(message));
    } catch (error: unknown) {
      console.error(`[ExternalWebSocketManager] Error sending message to ${clientId}:`, error);
      this.clients.delete(clientId);
    }
  }

  /**
   * Broadcast message to all subscribed clients
   */
  private broadcast(message: Record<string, unknown>, subscription?: string): void {
    this.clients.forEach((client, clientId) => {
      if (subscription && !client.subscriptions.has(subscription)) return;
      this.sendToClient(clientId, message);
    });
  }

  /**
   * Set up forwarding from QueryNotificationManager
   */
  private setupQueryNotificationForwarding(): void {
    this.queryNotificationManager.on('queryStatusUpdated', status => {
      this.broadcast(
        {
          type: 'query_progress',
          query_id: status.id,
          status: status.status,
          progress: status.progress,
          message: status.message,
          timestamp: new Date().toISOString(),
        },
        `query:${status.id}`
      );

      this.broadcast(
        {
          type: 'query_progress',
          query_id: status.id,
          status: status.status,
          progress: status.progress,
          message: status.message,
          timestamp: new Date().toISOString(),
        },
        `event:${status.status}`
      );
    });

    this.queryNotificationManager.on('query_completed', status => {
      this.broadcast(
        {
          type: 'query_complete',
          query_id: status.id,
          results: status.results,
          execution_time: status.endTime ? status.endTime - status.startTime : 0,
          timestamp: new Date().toISOString(),
        },
        `query:${status.id}`
      );
    });
  }

  /**
   * Start heartbeat mechanism
   */
  private startHeartbeat(): void {
    this.heartbeatInterval = setInterval(() => {
      this.clients.forEach((client, clientId) => {
        if (!client.isAlive) {
          console.log(`[ExternalWebSocketManager] Terminating inactive client ${clientId}`);
          client.ws.terminate();
          this.clients.delete(clientId);
          return;
        }

        client.isAlive = false;
        client.ws.ping();
      });
    }, this.config.heartbeatInterval * 1000);
  }

  /**
   * Generate unique client ID
   */
  private generateClientId(): string {
    return `ws_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  /**
   * Get default permissions for role
   */
  private getDefaultPermissions(role: string): string[] {
    const rolePermissions: { [key: string]: string[] } = {
      admin: ['*'],
      agent: [
        'query:execute',
        'query:cancel',
        'query:status',
        'notifications:subscribe',
        'session:read',
        'status:read',
      ],
      readonly: ['query:status', 'notifications:subscribe', 'status:read'],
      limited: ['query:status'],
    };

    return rolePermissions[role] || rolePermissions['limited'] || [];
  }

  /**
   * Get server status
   */
  getStatus(): {
    running: boolean;
    port: number;
    connectedClients: number;
    maxConnections: number;
  } {
    return {
      running: this.isRunning,
      port: this.config.port,
      connectedClients: this.clients.size,
      maxConnections: this.config.maxConnections,
    };
  }
}

import { exec } from 'child_process';
import * as fs from 'fs';
import * as path from 'path';

/**
 * Multi-IDE Support for Prolog LSP
 * Provides configuration and setup for various IDEs and editors
 */
export class MultiIDESupport {
  private static readonly SUPPORTED_IDES = [
    'vscode',
    'vim',
    'neovim',
    'emacs',
    'sublime',
    'atom',
    'intellij',
    'eclipse',
    'theia',
    'coc-nvim',
  ];

  /**
   * Generate LSP configuration files for different IDEs
   */
  public static async generateIDEConfigurations(workspaceRoot: string): Promise<void> {
    const configs = {
      // VS Code configuration (already handled by extension)
      vscode: this.generateVSCodeConfig(),

      // Vim/Neovim with coc.nvim
      'coc-settings': this.generateCocNvimConfig(),

      // Neovim with built-in LSP
      neovim: this.generateNeovimConfig(),

      // Vim with vim-lsp
      vim: this.generateVimLspConfig(),

      // Emacs with lsp-mode
      emacs: this.generateEmacsConfig(),

      // Sublime Text with LSP package
      sublime: this.generateSublimeConfig(),

      // IntelliJ IDEA configuration
      intellij: this.generateIntelliJConfig(),

      // Eclipse configuration
      eclipse: this.generateEclipseConfig(),

      // Theia configuration
      theia: this.generateTheiaConfig(),
    };

    // Create .lsp directory for configurations
    const lspDir = path.join(workspaceRoot, '.lsp');
    if (!fs.existsSync(lspDir)) {
      fs.mkdirSync(lspDir, { recursive: true });
    }

    // Write configuration files
    for (const [ide, config] of Object.entries(configs)) {
      if (config) {
        const configPath = path.join(lspDir, `${ide}.json`);
        fs.writeFileSync(configPath, JSON.stringify(config, null, 2));
      }
    }

    // Generate setup scripts
    await this.generateSetupScripts(lspDir);

    // Generate documentation
    await this.generateIDEDocumentation(lspDir);
  }

  private static generateVSCodeConfig() {
    return {
      name: 'Prolog LSP for VS Code',
      description: 'Already configured through the VSCode Prolog Toolkit extension',
      status: 'native_support',
      setup: 'Install the VSCode Prolog Toolkit extension from the marketplace',
