        activeQueries.forEach((query, index) => {
          const statusIcon =
            query.status === 'running' ? 'sync' : query.status === 'pending' ? 'clock' : 'question';
          const statusText = query.status.charAt(0).toUpperCase() + query.status.slice(1);

          items.push({
            id: `active-query-${query.id}`,
            label: query.message || `Query ${index + 1}`,
            description: statusText,
            tooltip: `Status: ${statusText}\nStart time: ${new Date(query.startTime).toLocaleString()}`,
            iconPath: new vscode.ThemeIcon(statusIcon),
            contextValue: 'active-query',
          });
        });
      } else {
        items.push({
          id: 'no-active-queries',
          label: 'No Active Queries',
          description: 'All queries completed',
          tooltip: 'No currently running queries',
          iconPath: new vscode.ThemeIcon('check'),
          contextValue: 'no-active-queries',
        });
      }

      // Add recent errors section
      items.push({
        id: 'recent-errors-header',
        label: 'Recent Errors',
        description: `${this.recentErrors.length} errors`,
        tooltip: 'Recently encountered errors',
        iconPath: new vscode.ThemeIcon('error'),
        contextValue: 'recent-errors-header',
        children: [],
      });

      if (this.recentErrors.length > 0) {
        // Show only the most recent 5 errors
        const errorsToShow = this.recentErrors.slice(0, 5);
        errorsToShow.forEach((error, index) => {
          const severityIcon =
            error.severity === 'error'
              ? 'error'
              : error.severity === 'warning'
                ? 'warning'
                : 'info';
          const severityText = error.severity.charAt(0).toUpperCase() + error.severity.slice(1);

          items.push({
            id: `recent-error-${index}`,
            label:
              error.message.length > 50 ? `${error.message.substring(0, 50)}...` : error.message,
            description: severityText,
            tooltip: `Severity: ${severityText}\nType: ${error.type}\nCode: ${error.code}\nMessage: ${error.message}`,
            iconPath: new vscode.ThemeIcon(severityIcon),
            contextValue: 'recent-error',
          });
        });

        if (this.recentErrors.length > 5) {
          items.push({
            id: 'more-errors',
            label: 'More errors...',
            description: `${this.recentErrors.length - 5} more`,
            tooltip: `View all ${this.recentErrors.length} errors`,
            iconPath: new vscode.ThemeIcon('more'),
            contextValue: 'more-errors',
          });
        }
      } else {
        items.push({
          id: 'no-recent-errors',
          label: 'No Recent Errors',
          description: 'All clear',
          tooltip: 'No recent errors to display',
          iconPath: new vscode.ThemeIcon('check'),
          contextValue: 'no-recent-errors',
        });
      }

      // Add statistics
      const stats = this.queryNotificationManager.getStatistics();
      items.push({
        id: 'notification-stats',
        label: 'Statistics',
        description: `${stats.total} total`,
        tooltip: `Total: ${stats.total}\nPending: ${stats.pending}\nRunning: ${stats.running}\nCompleted: ${stats.completed}\nErrors: ${stats.error}\nCancelled: ${stats.cancelled}\nTimeouts: ${stats.timeout}`,
        iconPath: new vscode.ThemeIcon('graph'),
        contextValue: 'notification-stats',
      });
    } catch (_error) {
      items.push({
        id: 'notifications-error',
        label: 'Error loading notifications',
        description: 'Click to retry',
        tooltip: 'Failed to load notifications and errors',
        iconPath: new vscode.ThemeIcon('error'),
        contextValue: 'notifications-error',
      });
    }

    return items;
  }
}

import * as vscode from 'vscode';
import * as path from 'path';
import { InstallationChecker } from './installationChecker';
import { QueryHistoryManager } from './queryHistoryManager';

export class PrologDashboardProvider implements vscode.WebviewViewProvider {
  public static readonly viewType = 'prologDashboard';
  private _view?: vscode.WebviewView;
  private installationChecker: InstallationChecker;
  private queryHistory: QueryHistoryManager;

  constructor(private readonly _extensionUri: vscode.Uri) {
    this.installationChecker = InstallationChecker.getInstance();
    this.queryHistory = QueryHistoryManager.getInstance();
  }

  public resolveWebviewView(
    webviewView: vscode.WebviewView,
    context: vscode.WebviewViewResolveContext,
    _token: vscode.CancellationToken
  ) {
    this._view = webviewView;

    webviewView.webview.options = {
      enableScripts: true,
      localResourceRoots: [this._extensionUri],
    };

    webviewView.webview.html = this._getHtmlForWebview(webviewView.webview);

    // Handle messages from the webview
    webviewView.webview.onDidReceiveMessage(
      async message => {
        switch (message.type) {
          case 'executeQuery':
            await this._executeQuery(message.query);
            break;
          case 'openSettings':
            vscode.commands.executeCommand('prolog.openSettings');
            break;
          case 'setupWizard':
            vscode.commands.executeCommand('prolog.setupWizard');
            break;
          case 'refreshStatus':
            await this._refreshStatus();
            break;
          case 'openFile':
            if (message.filePath) {
              const uri = vscode.Uri.file(message.filePath);
              await vscode.window.showTextDocument(uri);
            }
            break;
          case 'newFile':
            await this._createNewFile();
            break;
          case 'clearHistory':
            await this._clearHistory();
            break;
        }
      },
      undefined,
      []
    );

    // Initial data load
    this._refreshStatus();
  }

  private async _executeQuery(query: string): Promise<void> {
    try {
      await vscode.commands.executeCommand('prolog.lsp.executeQuery', query);
      this._refreshStatus(); // Refresh to update history
    } catch (error) {
      vscode.window.showErrorMessage(`Failed to execute query: ${error}`);
    }
  }

  private async _refreshStatus(): Promise<void> {
    if (!this._view) {
      return;
    }

    try {
      // Get installation status
      const installationStatus = await this.installationChecker.checkSwiplInstallation();

      // Get recent queries
      const recentQueries = await this.queryHistory.getRecentQueries(5);

      // Get workspace Prolog files
      const prologFiles = vscode.workspace.workspaceFolders
        ? await vscode.workspace.findFiles('**/*.{pl,pro,prolog,plt,ecl}', '**/node_modules/**', 10)
        : [];

      // Get debug session status
      const debugSession = vscode.debug.activeDebugSession;
      const isDebugging = debugSession && debugSession.type === 'prolog';

      const statusData = {
        installation: {
          isInstalled: installationStatus.isInstalled,
          version: installationStatus.version || 'Unknown',
          path: installationStatus.path || 'Not found',
          issues: installationStatus.issues || [],
        },
        queries: recentQueries.map(q => ({
          query: q.query,
          success: q.success,
          timestamp: q.timestamp,
          result: q.result,
        })),
        files: prologFiles.map(file => ({
          name: path.basename(file.fsPath),
          path: file.fsPath,
          relativePath: vscode.workspace.asRelativePath(file),
        })),
        debugging: {
          isActive: isDebugging,
          sessionName: debugSession?.name || null,
        },
        workspace: {
          hasWorkspace: !!vscode.workspace.workspaceFolders,
          workspaceName: vscode.workspace.workspaceFolders?.[0]?.name || null,
        },
      };

      this._view.webview.postMessage({
        type: 'statusUpdate',
        data: statusData,
      });
    } catch (error) {
      console.error('Error refreshing dashboard status:', error);
      this._view.webview.postMessage({
        type: 'error',
        message: `Failed to refresh status: ${error}`,
      });
    }
  }

  private async _createNewFile(): Promise<void> {
    try {
      const fileName = await vscode.window.showInputBox({
        prompt: 'Enter the name for the new Prolog file',
        value: 'untitled.pl',
        validateInput: value => {
          if (!value) {
            return 'File name cannot be empty';
          }
          if (!value.match(/\.(pl|pro|prolog|plt|ecl)$/)) {
            return 'File must have a Prolog extension (.pl, .pro, .prolog, .plt, .ecl)';
          }
          return null;
        },
      });

      if (fileName) {
        const workspaceFolder = vscode.workspace.workspaceFolders?.[0];
        if (workspaceFolder) {
          const filePath = vscode.Uri.joinPath(workspaceFolder.uri, fileName);
          const edit = new vscode.WorkspaceEdit();
          edit.createFile(filePath, { ignoreIfExists: false });
          await vscode.workspace.applyEdit(edit);
          await vscode.window.showTextDocument(filePath);
        } else {
          // Create untitled document
          const doc = await vscode.workspace.openTextDocument({
            language: 'prolog',
            content: `% ${fileName}\n% New Prolog file\n\n`,
          });
          await vscode.window.showTextDocument(doc);
        }
      }
    } catch (error) {
      vscode.window.showErrorMessage(`Failed to create new file: ${error}`);
    }
  }

  private async _clearHistory(): Promise<void> {
    try {
      await this.queryHistory.clearHistory();
      vscode.window.showInformationMessage('Query history cleared');
      this._refreshStatus();
    } catch (error) {
      vscode.window.showErrorMessage(`Failed to clear history: ${error}`);
    }
  }

  private _getHtmlForWebview(webview: vscode.Webview): string {
    const _scriptUri = webview.asWebviewUri(
      vscode.Uri.joinPath(this._extensionUri, 'media', 'dashboard.js')
    );
    const styleUri = webview.asWebviewUri(
      vscode.Uri.joinPath(this._extensionUri, 'media', 'dashboard.css')
    );

    return `<!DOCTYPE html>
        <html lang="en">
        <head>
            <meta charset="UTF-8">
            <meta name="viewport" content="width=device-width, initial-scale=1.0">
            <link href="${styleUri}" rel="stylesheet">
            <title>Prolog Dashboard</title>
        </head>
        <body>
            <div class="dashboard-container">
                <div class="dashboard-header">
                    <h1> Prolog Toolkit</h1>
                    <button id="refreshBtn" class="action-btn" title="Refresh Status"></button>
                </div>

                <!-- Installation Status -->
                <div class="dashboard-section">
                    <h2> Installation Status</h2>
                    <div id="installationStatus" class="status-card">
                        <div class="status-indicator loading">
                            <span class="status-icon"></span>
                            <span class="status-text">Checking installation...</span>
                        </div>
                        <div class="status-details" id="installationDetails" style="display: none;">
                            <div class="detail-item">
                                <strong>Version:</strong> <span id="installationVersion">-</span>
                            </div>
                            <div class="detail-item">
                                <strong>Path:</strong> <span id="installationPath">-</span>
                            </div>
                        </div>
                        <div class="status-actions">
                            <button class="action-btn" id="setupWizardBtn"> Setup Wizard</button>
                            <button class="action-btn" id="openSettingsBtn"> Settings</button>
                        </div>
                    </div>
                </div>

                <!-- Quick Query -->
                <div class="dashboard-section">
                    <h2> Quick Query</h2>
                    <div class="query-card">
                        <div class="query-input-container">
                            <input type="text" id="queryInput" placeholder="Enter Prolog query (e.g., member(X, [1,2,3]))" />
                            <button id="executeBtn" class="action-btn primary"> Execute</button>
                        </div>
                        <div class="query-examples">
                            <span class="example-label">Examples:</span>
                            <button class="example-btn" data-query="member(X, [1,2,3])">member(X, [1,2,3])</button>
                            <button class="example-btn" data-query="append([1,2], [3,4], X)">append([1,2], [3,4], X)</button>
                            <button class="example-btn" data-query="length([a,b,c], X)">length([a,b,c], X)</button>
                        </div>
                    </div>
                </div>

                <!-- Recent Queries -->
                <div class="dashboard-section">
                    <h2> Recent Queries</h2>
                    <div id="recentQueries" class="queries-card">
                        <div class="loading-message">Loading recent queries...</div>
                    </div>
                    <div class="queries-actions">
                        <button class="action-btn" id="clearHistoryBtn"> Clear History</button>
                    </div>
                </div>

                <!-- Workspace Files -->
                <div class="dashboard-section">
                    <h2> Prolog Files</h2>
                    <div id="workspaceFiles" class="files-card">
                        <div class="loading-message">Loading workspace files...</div>
                    </div>
                    <div class="files-actions">
                        <button class="action-btn" id="newFileBtn"> New File</button>
                    </div>
                </div>

                <!-- Debug Status -->
                <div class="dashboard-section">
                    <h2> Debug Status</h2>
                    <div id="debugStatus" class="debug-card">
                        <div class="status-indicator">
                            <span class="status-icon"></span>
                            <span class="status-text">No active debug session</span>
                        </div>
                    </div>
                </div>

                <!-- Quick Actions -->
                <div class="dashboard-section">
                    <h2> Quick Actions</h2>
                    <div class="actions-grid">
                        <button class="quick-action-btn" onclick="vscode.postMessage({type: 'openSettings'})">
                            <span class="action-icon"></span>
                            <span class="action-label">Settings</span>
                        </button>
                        <button class="quick-action-btn" onclick="vscode.postMessage({type: 'setupWizard'})">
                            <span class="action-icon"></span>
                            <span class="action-label">Setup</span>
                        </button>
                        <button class="quick-action-btn" onclick="vscode.postMessage({type: 'newFile'})">
                            <span class="action-icon"></span>
                            <span class="action-label">New File</span>
                        </button>
                        <button class="quick-action-btn" onclick="window.open('https://github.com/mediaprophet/VSCode-Prolog-Toolkit')">
                            <span class="action-icon"></span>
                            <span class="action-label">Docs</span>
                        </button>
                    </div>
                </div>
            </div>

            <script>
                const vscode = acquireVsCodeApi();

                // Event listeners
                document.getElementById('refreshBtn').addEventListener('click', () => {
                    vscode.postMessage({ type: 'refreshStatus' });
                });

                document.getElementById('executeBtn').addEventListener('click', () => {
                    const query = document.getElementById('queryInput').value.trim();
                    if (query) {
                        vscode.postMessage({ type: 'executeQuery', query: query });
                        document.getElementById('queryInput').value = '';
                    }
                });

                document.getElementById('queryInput').addEventListener('keypress', (e) => {
                    if (e.key === 'Enter') {
                        document.getElementById('executeBtn').click();
                    }
                });

                document.getElementById('setupWizardBtn').addEventListener('click', () => {
                    vscode.postMessage({ type: 'setupWizard' });
                });

                document.getElementById('openSettingsBtn').addEventListener('click', () => {
                    vscode.postMessage({ type: 'openSettings' });
                });

                document.getElementById('newFileBtn').addEventListener('click', () => {
                    vscode.postMessage({ type: 'newFile' });
                });

                document.getElementById('clearHistoryBtn').addEventListener('click', () => {
                    vscode.postMessage({ type: 'clearHistory' });
                });

                // Example query buttons
                document.querySelectorAll('.example-btn').forEach(btn => {
                    btn.addEventListener('click', () => {
                        const query = btn.getAttribute('data-query');
                        document.getElementById('queryInput').value = query;
                    });
                });

                // Handle messages from extension
                window.addEventListener('message', event => {
                    const message = event.data;

                    switch (message.type) {
                        case 'statusUpdate':
                            updateDashboard(message.data);
                            break;
                        case 'error':
                            showError(message.message);
                            break;
                    }
                });

                function updateDashboard(data) {
                    // Update installation status
                    const installationStatus = document.getElementById('installationStatus');
                    const statusIndicator = installationStatus.querySelector('.status-indicator');
                    const statusDetails = document.getElementById('installationDetails');

                    if (data.installation.isInstalled) {
                        statusIndicator.className = 'status-indicator success';
                        statusIndicator.innerHTML = '<span class="status-icon"></span><span class="status-text">SWI-Prolog Installed</span>';
                        document.getElementById('installationVersion').textContent = data.installation.version;
                        document.getElementById('installationPath').textContent = data.installation.path;
                        statusDetails.style.display = 'block';
                    } else {
                        statusIndicator.className = 'status-indicator error';
                        statusIndicator.innerHTML = '<span class="status-icon"></span><span class="status-text">SWI-Prolog Not Found</span>';
                        statusDetails.style.display = 'none';
                    }

                    // Update recent queries
                    const queriesContainer = document.getElementById('recentQueries');
                    if (data.queries.length === 0) {
                        queriesContainer.innerHTML = '<div class="empty-message">No recent queries</div>';
                    } else {
                        queriesContainer.innerHTML = data.queries.map(query => 
                            '<div class="query-item">' +
                            '<div class="query-text">' + escapeHtml(query.query) + '</div>' +
                            '<div class="query-status ' + (query.success ? 'success' : 'error') + '">' +
                            (query.success ? '' : '') +
                            '</div>' +
                            '</div>'
                        ).join('');
                    }

                    // Update workspace files
                    const filesContainer = document.getElementById('workspaceFiles');
                    if (!data.workspace.hasWorkspace) {
                        filesContainer.innerHTML = '<div class="empty-message">No workspace open</div>';
                    } else if (data.files.length === 0) {
                        filesContainer.innerHTML = '<div class="empty-message">No Prolog files found</div>';
                    } else {
                        filesContainer.innerHTML = data.files.map(file => 
                            '<div class="file-item" onclick="vscode.postMessage({type: 'openFile', filePath: '' + file.path + ''})">' +
                            '<span class="file-icon"></span>' +
                            '<span class="file-name">' + escapeHtml(file.name) + '</span>' +
                            '<span class="file-path">' + escapeHtml(file.relativePath) + '</span>' +
                            '</div>'
                        ).join('');
                    }

                    // Update debug status
                    const debugStatus = document.getElementById('debugStatus');
                    const debugIndicator = debugStatus.querySelector('.status-indicator');
                    if (data.debugging.isActive) {
                        debugIndicator.innerHTML = '<span class="status-icon"></span><span class="status-text">Debugging: ' + escapeHtml(data.debugging.sessionName) + '</span>';
                    } else {
                        debugIndicator.innerHTML = '<span class="status-icon"></span><span class="status-text">No active debug session</span>';
                    }
                }

                function showError(message) {
                    // Simple error display - could be enhanced with a proper notification system
                    console.error('Dashboard error:', message);
                }

                function escapeHtml(text) {
                    const div = document.createElement('div');
                    div.textContent = text;
                    return div.innerHTML;
                }

                // Request initial status
                vscode.postMessage({ type: 'refreshStatus' });
            </script>
        </body>
        </html>`;
  }
}

import {
  DebugSession,
  ErrorDestination,
  OutputEvent,
  InitializedEvent,
  StackFrame,
  Source,
  Scope,
  Thread,
} from '@vscode/debugadapter';
import { DebugProtocol } from '@vscode/debugprotocol';
import { ITraceCmds, LaunchRequestArguments, PrologDebugger } from './prologDebugger';
import * as path from 'path';
import { spawn, SpawnOptions } from 'process-promises';

// Extends the DebugSession class, providing the implementation for a Prolog debugger
export class PrologDebugSession extends DebugSession {
  private static SCOPEREF = 1;
  public static THREAD_ID = 100;
  private _prologDebugger!: PrologDebugger;
  private _runtimeExecutable!: string;
  // private _runtimeArgs: string[];
  private _startupQuery!: string;
  private _startFile!: string;
  private _cwd!: string;
  private _stopOnEntry!: boolean;
  private _traceCmds!: ITraceCmds;
  private _currentVariables: DebugProtocol.Variable[] = [];
  private _stackFrames: DebugProtocol.StackFrame[] = [];
  private _debugging!: boolean;

  public constructor() {
    super();
    this.setDebuggerColumnsStartAt1(true);
    this.setDebuggerLinesStartAt1(true);
    this.setDebuggerPathFormat('native');
  }
  // Implements the initialization logic for the debugger
  protected override initializeRequest(
    response: DebugProtocol.InitializeResponse,
    _args: DebugProtocol.InitializeRequestArguments
  ): void {
    // Configure the response body with supported features and options
    response.body = {
      supportsConfigurationDoneRequest: true,
      supportTerminateDebuggee: true,
      supportsConditionalBreakpoints: true,
      supportsHitConditionalBreakpoints: true,
      supportsFunctionBreakpoints: true,
      supportsEvaluateForHovers: true,
      supportsExceptionOptions: true,
      supportsExceptionInfoRequest: true,
      // Define exception breakpoint filters with associated labels
      exceptionBreakpointFilters: [
        {
          filter: 'Notice',
          label: 'Notices',
        },
        {
          filter: 'Warning',
          label: 'Warnings',
        },
        {
          filter: 'Error',
          label: 'Errors',
        },
        {
          filter: 'Exception',
          label: 'Exceptions',
        },
        {
          filter: '*',
          label: 'Everything',
          default: true,
        },
      ],
    };
    // Send the initialized response back to the client
    this.sendResponse(response);
  }
  //Handles the 'attach' request from the debugger client
  protected override attachRequest(
    response: DebugProtocol.AttachResponse,
    _args: DebugProtocol.AttachRequestArguments
  ) {
    // Send an error response indicating that attach requests are not supported
    this.sendErrorResponse(response, new Error('Attach requests are not supported'));
    // Shutdown the debugger session
    this.shutdown();
  }

  // Adds a stack frame to the list of stack frames during debugging
  public addStackFrame(frame: {
    id: number;
    level: number;
    name: string;
    file: string;
    line: number;
    column: number;
  }) {
    // Construct a new StackFrame object with information from the provided frame
    if (frame.file && frame.name) {
      this._stackFrames.unshift(
        new StackFrame(
          frame.id,
          `(${frame.level})${frame.name}`,
          new Source(
            path.basename(frame.file), // Extract the base name of the file
            frame.file
          ),
          this.convertDebuggerLineToClient(frame.line), // Convert debugger line to client line
          this.convertDebuggerColumnToClient(frame.column) // Convert debugger column to client column
        )
      );
    }
  }

  //Sets the current variables in the debugger session
  public setCurrentVariables(vars: DebugProtocol.Variable[]) {
    this._currentVariables = []; // Clear existing variables in _currentVariables array
    // Pop elements from the provided vars array and push them into _currentVariables
    while (vars.length > 0) {
      const variable = vars.pop();
      if (variable?.name) {
        this._currentVariables.push({ ...variable, variablesReference: 0 });
      }
    }
  }

  // Handles the 'launch' request from the debugger client
  protected override launchRequest(
    response: DebugProtocol.LaunchResponse,
    args: DebugProtocol.LaunchRequestArguments,
    _request?: DebugProtocol.Request
  ) {
    const richArgs = args as LaunchRequestArguments; // Cast arguments to LaunchRequestArguments for access to specific properties
    // Set various parameters from the launch arguments or use defaults
    this._startupQuery = richArgs.startupQuery || 'start';
    this._startFile = richArgs.program ? path.resolve(richArgs.program) : '';
    this._cwd = richArgs.cwd;
    this._runtimeExecutable = richArgs.runtimeExecutable || 'swipl';
    // this._runtimeArgs = args.runtimeArgs || null;
    this._stopOnEntry = typeof richArgs.stopOnEntry === 'boolean' ? richArgs.stopOnEntry : true;
    this._traceCmds = richArgs.traceCmds || ({} as ITraceCmds);
    this._prologDebugger = new PrologDebugger(richArgs, this); // Initialize the Prolog debugger
    // Add listeners for breakpoint responses
    this._prologDebugger.addListener(
      'responseBreakpoints',
      (bps: DebugProtocol.SetBreakpointsResponse) => {
        this.sendResponse(bps);
      }
    );
    this._prologDebugger.addListener(
      'responseFunctionBreakpoints',
      (fbps: DebugProtocol.SetFunctionBreakpointsResponse) => {
        this.sendResponse(fbps);
      }
    );
    this.sendResponse(response); // Send the launch response back to the client
    this.sendEvent(new InitializedEvent()); // Send an 'Initialized' event to the client, indicating that the debugger is ready
  }

  // Handles the 'threads' request from the debugger client
  protected override threadsRequest(response: DebugProtocol.ThreadsResponse): void {
    // Configure the response body with information about the threads
    response.body = {
      threads: [new Thread(PrologDebugSession.THREAD_ID, 'thread 1')],
    };
    this.sendResponse(response); // Send the threads response back to the client
  }

  // Handles the 'setBreakpoints' request from the debugger client
  protected override setBreakPointsRequest(
    response: DebugProtocol.SetBreakpointsResponse,
    args: DebugProtocol.SetBreakpointsArguments
  ) {
    // Check if the debugger is currently in a debugging session
    if (this._debugging) {
      // Display a message indicating that breakpoints set during debugging will take effect in the next debugging process
      this.debugOutput(
        'Breakpoints set during debugging would take effect in next debugging process.'
      );
      return; // Return without setting breakpoints during an active debugging session
    }
    this._prologDebugger.setBreakpoints(args, response); // Delegate the task of setting breakpoints to the Prolog debugger
  }

  // Handles the 'setExceptionBreakpoints' request from the debugger client
  protected override setExceptionBreakPointsRequest(
    response: DebugProtocol.SetExceptionBreakpointsResponse,
    _args: DebugProtocol.SetExceptionBreakpointsArguments
  ): void {
    // Send a response back to the client without modifying exception breakpoints
    this.sendResponse(response);
  }

  // Handles the 'setFunctionBreakpoints' request from the debugger client
  protected override setFunctionBreakPointsRequest(
    response: DebugProtocol.SetFunctionBreakpointsResponse,
    _args: DebugProtocol.SetFunctionBreakpointsArguments
  ): void {
    // Delegate the task of setting function breakpoints to the Prolog debugger
    if (this._prologDebugger) {
      this._prologDebugger.setFunctionBreakpoints(args, response);
    }
  }

  // Handles the 'configurationDone' request from the debugger client
  protected override configurationDoneRequest(
    response: DebugProtocol.ConfigurationDoneResponse,
    _args: DebugProtocol.ConfigurationDoneArguments
  ): void {
    this.sendResponse(response); // Send a response back to the client
    if (this._prologDebugger) {
      this._prologDebugger.startup(`${this._startupQuery}`); // Start the Prolog debugger and execute startup commands
      // If not stopping on entry, continue the execution
      if (!this._stopOnEntry && this._traceCmds?.continue?.[1]) {
        this._prologDebugger.query(`cmd:${this._traceCmds.continue[1]}\n`);
      }
      if (this._traceCmds?.stepinto?.[1]) {
        this._prologDebugger.query(`cmd:${this._traceCmds.stepinto[1]}\n`); // Issue a step into command to initiate the debugging process
      }
    }
    this._debugging = true; // Set the debugging flag to true
  }

  // Evaluates the expression to retrieve the value of a variable
  private evaluateExpression(exp: string) {
    const vars = this._currentVariables;
    // Iterate through the current variables
    for (let i = 0; i < vars.length; i++) {
      // Check if the variable name matches the provided expression
      if (vars[i]?.name === exp) {
        return vars[i]?.value; // Return the value of the variable
      }
    }
    return null; // Return null if the variable is not found
  }

  // Handles the 'evaluate' request from the debugger client
  protected override evaluateRequest(
    response: DebugProtocol.EvaluateResponse,
    args: DebugProtocol.EvaluateArguments
  ): void {
    const val = this.evaluateExpression(args.expression); // Evaluate the expression to retrieve the value
    // If a value is found, send the result back to the client
    if (val) {
      response.body = {
        result: val,
        variablesReference: 0,
      };
      this.sendResponse(response);
      return;
    } else {
      if (args.context === 'repl') {
        const vars = this._currentVariables;
        let exp = args.expression.trim();
        if (exp.startsWith(':')) {
          // Workaround for input from stdin
          const input = 'input' + args.expression;
          this._prologDebugger.query(input + '\n');
        } else {
          // Replace variable references in the expression with their values
          for (let i = 0; i < vars.length; i++) {
            if (vars[i]?.name && vars[i]?.value) {
              const re = new RegExp('\\b' + vars[i].name + '\\b', 'g');
              exp = exp.replace(re, vars[i].value || '');
            }
          }
          this.debugOutput(args.expression);
          this.evaluate(exp); // Evaluate the modified expression
        }
        response.body = { result: '', variablesReference: 0 };
      }
      this.sendResponse(response); // Send the response back to the client
      return;
    }
  }

  // Handles the 'stackTrace' request from the debugger client
  protected override stackTraceRequest(
    response: DebugProtocol.StackTraceResponse,
    _args: DebugProtocol.StackTraceArguments
  ): void {
    // Configure the response body with information about the stack frames
    response.body = {
      stackFrames: this._stackFrames,
    };
    this.sendResponse(response); // Send the stack trace response back to the client
  }

  // Handles the 'variables' request from the debugger client
  protected override variablesRequest(
    response: DebugProtocol.VariablesResponse,
    _args: DebugProtocol.VariablesArguments
  ): void {
    const variables = new Array<DebugProtocol.Variable>();
    // Copy variables from _currentVariables to the response
    for (let i = 0; i < this._currentVariables.length; i++) {
      const variable = this._currentVariables[i];
      if (variable) {
        variables.push(variable);
      }
    }
    // Configure the response body with information about the variables
    response.body = {
      variables: variables,
    };
    this.sendResponse(response); // Send the variables response back to the client
  }

  //Handles the 'scopes' request from the debugger client
  protected override scopesRequest(
    response: DebugProtocol.ScopesResponse,
    _args: DebugProtocol.ScopesArguments
  ): void {
    const scopes = new Array<Scope>();
    scopes.push(new Scope('Local', PrologDebugSession.SCOPEREF++, false)); // Add a Local scope to the response
    // Configure the response body with information about the scopes
    response.body = {
      scopes: scopes,
    };
    this.sendResponse(response); // Send the scopes response back to the client
  }

  // Handles the 'continue' request from the debugger client
  protected override continueRequest(
    response: DebugProtocol.ContinueResponse,
    _args: DebugProtocol.ContinueArguments
  ): void {
    if (this._prologDebugger && this._traceCmds?.continue?.[1]) {
      this._prologDebugger.query(`cmd:${this._traceCmds.continue[1]}\n`); // Send a continue command to the Prolog debugger
    }
    this.sendResponse(response); // Send the continue response back to the client
  }

  // Handles the 'next' request from the debugger client
  protected override nextRequest(
    response: DebugProtocol.NextResponse,
    _args: DebugProtocol.NextArguments
  ): void {
    if (this._prologDebugger && this._traceCmds?.stepover?.[1]) {
      this._prologDebugger.query(`cmd:${this._traceCmds.stepover[1]}\n`); // Send a step-over command to the Prolog debugger
    }
    this.sendResponse(response); // Send the next response back to the client
  }

  // Handles the 'stepIn' request from the debugger client
  protected override stepInRequest(
    response: DebugProtocol.StepInResponse,
    _args: DebugProtocol.StepInArguments
  ): void {
    if (this._prologDebugger && this._traceCmds?.stepinto?.[1]) {
      this._prologDebugger.query(`cmd:${this._traceCmds.stepinto[1]}\n`); // Send a step-into command to the Prolog debugger
    }
    this.sendResponse(response); // Send the step-in response back to the client
  }

  // Handles the 'stepOut' request from the debugger client
  protected override stepOutRequest(
    response: DebugProtocol.StepOutResponse,
    _args: DebugProtocol.StepOutArguments
  ): void {
    if (this._prologDebugger && this._traceCmds?.stepout?.[1]) {
      this._prologDebugger.query(`cmd:${this._traceCmds.stepout[1]}\n`); // Send a step-out command to the Prolog debugger
    }
    this.sendResponse(response); // Send the step-out response back to the client
  }

  // Handles the 'disconnect' request from the debugger client
  protected override disconnectRequest(
    response: DebugProtocol.DisconnectResponse,
    _args: DebugProtocol.DisconnectArguments
  ): void {
    this._debugging = false; // Mark the end of the debugging session
    if (this._prologDebugger) {
      if (this._prologDebugger) {
        this._prologDebugger.dispose(); // Dispose of the Prolog debugger
      }
    }
    this.shutdown(); // Shutdown the debugger
    this.sendResponse(response); // Send the disconnect response back to the client
  }

  // Handles the 'restart' request from the debugger client
  protected override restartRequest(
    response: DebugProtocol.RestartResponse,
    _args: DebugProtocol.RestartArguments
  ): void {
    this._debugging = false; // Mark the end of the debugging session
    this._prologDebugger.dispose(); // Dispose of the Prolog debugger
    this.shutdown(); // Shutdown the debugger
    this.sendResponse(response); // Send the restart response back to the client
  }
  // Sends an error response back to the debugger client
  protected override sendErrorResponse(
    response: DebugProtocol.Response,
    error: Error,
    dest?: ErrorDestination
  ): void;

  // Sends an error response back to the debugger client
  protected override sendErrorResponse(
    response: DebugProtocol.Response,
    codeOrMessage: number | DebugProtocol.Message,
    format?: string,
    variables?: any,
    dest?: ErrorDestination
  ): void;

  // Sends an error response back to the debugger client
  protected override sendErrorResponse(response: DebugProtocol.Response) {
    // Check if the second argument is an instance of Error
    if (arguments[1] instanceof Error) {
      // Extract error information from the second and third arguments
      const error = arguments[1] as Error & {
        code?: number | string;
        errno?: number;
      };
      const dest = arguments[2] as ErrorDestination;
      // Determine the error code
      let code: number;
      if (typeof error.code === 'number') {
        code = error.code;
      } else if (typeof error.errno === 'number') {
        code = error.errno;
      } else {
        // Default to 0 if no valid code is found
        code = 0;
      }
      super.sendErrorResponse(response, code, error.message, dest); // Forward the error response to the superclass method
    } else {
      // If the second argument is not an instance of Error, forward the response as is
      super.sendErrorResponse(response, arguments[1], arguments[2], arguments[3], arguments[4]);
    }
  }

  // Sends a debug output event with the specified message to the debugger client
  public debugOutput(msg: string) {
    // Send a debug output event with the specified message
    this.sendEvent(new OutputEvent(msg));
  }

  // Evaluates the specified Prolog expression by spawning a new process
  private evaluate(expression: string) {
    // Get the runtime executable, program arguments, and input for the Prolog process
    const exec = this._runtimeExecutable;
    const args = ['-q', `${this._startFile}`];
    const input = `
    call((${expression})).
    halt.
    `;
    // Configure spawn options with the current working directory
    const spawnOptions: SpawnOptions = {
      cwd: this._cwd,
    };
    // Spawn a new process for Prolog execution
    spawn(exec, args, spawnOptions)
      // If the process has a valid PID, write input to its stdin and end the input stream
      .on('process', (proc: any) => {
        if (proc.pid) {
          proc.stdin.write(input);
          proc.stdin.end();
        }
      })
      .on('stdout', (data: any) => {
        // Handle standard output by sending it as debug output
        this.debugOutput('\n' + data);
      })
      .on('stderr', (err: any) => {
        // Handle standard error by sending it as debug output
        this.debugOutput('\n' + err);
      })
      .catch((err: any) => {
        // Handle any errors by sending the error message as debug output
        this.debugOutput(err.message);
      });
  }
}

