import { InstallationGuide } from './installationGuide';

export class PrologFormatter
  implements DocumentRangeFormattingEditProvider, DocumentFormattingEditProvider {
  private _section: WorkspaceConfiguration;
  private _tabSize!: number;
  private _insertSpaces!: boolean;
  private _tabDistance!: number;
  private _executable: string;
  private _args: string[];
  private _outputChannel: OutputChannel;
  private _textEdits: TextEdit[] = [];
  private _startChars!: number;

  // Constructor for the PrologFormatter class
  constructor() {
    this._section = workspace.getConfiguration('prolog');
    const execPath = this._section.get('executablePath', PlatformUtils.getDefaultExecutablePath());
    this._executable = PlatformUtils.normalizePath(execPath);
    this._args = [];
    this._outputChannel = window.createOutputChannel('PrologFormatter');

    // Initialize with better executable resolution
    this.initializeExecutable();
  }

  /**
   * Initialize executable with enhanced detection and permission checking
   */
  private async initializeExecutable(): Promise<void> {
    const configuredPath = this._section.get<string>(
      'executablePath',
      PlatformUtils.getDefaultExecutablePath()
    );

    // Check if configured path exists and is executable
    if (await PlatformUtils.pathExists(configuredPath)) {
      if (await PlatformUtils.isExecutable(configuredPath)) {
        this._executable = PlatformUtils.normalizePath(configuredPath);
        return;
      } else {
        // Path exists but lacks execute permissions
        const platform = PlatformUtils.getPlatform();
        if (platform !== 'windows') {
          window
            .showWarningMessage(
              `SWI-Prolog executable at '${configuredPath}' lacks execute permissions. Try: chmod +x "${configuredPath}"`,
              'Fix Permissions'
            )
            .then(action => {
              if (action === 'Fix Permissions') {
                const terminal = window.createTerminal('Fix Prolog Permissions');
                terminal.sendText(`chmod +x "${configuredPath}"`);
                terminal.show();
              }
            });
        }
      }
    }

    // Try to find executable using comprehensive detection
    const executableFinder = new ExecutableFinder();
    const detectionResult = await executableFinder.findSwiplExecutable();

    if (detectionResult.found && detectionResult.path) {
      if (detectionResult.permissions?.executable) {
        this._executable = detectionResult.path;

        // Update configuration if we found a different path
        if (detectionResult.path !== configuredPath) {
          window
            .showInformationMessage(
              `Found SWI-Prolog at '${detectionResult.path}' via ${detectionResult.detectionMethod}. Update configuration?`,
              'Yes',
              'No'
            )
            .then(action => {
              if (action === 'Yes') {
                this._section.update('executablePath', detectionResult.path, true);
              }
            });
        }
      } else {
        // Found executable but has permission issues
        const platform = PlatformUtils.getPlatform();
        if (platform !== 'windows') {
          window
            .showWarningMessage(
              `Found SWI-Prolog at '${detectionResult.path}' but it lacks execute permissions. Try: chmod +x "${detectionResult.path}"`,
              'Fix Permissions'
            )
            .then(action => {
              if (action === 'Fix Permissions') {
                const terminal = window.createTerminal('Fix Prolog Permissions');
                terminal.sendText(`chmod +x "${detectionResult.path}"`);
                terminal.show();
              }
            });
        }
        this._executable = detectionResult.path; // Use it anyway, will fail with better error
      }
    }
  }

  // Enhanced error handling for SWI-Prolog executable issues
  private async handleExecutableError(error: any): Promise<void> {
    if (error.code === 'ENOENT') {
      const action = await window.showErrorMessage(
        'SWI-Prolog executable not found. The formatter requires SWI-Prolog to format your code properly.',
        'Install SWI-Prolog',
        'Setup Wizard',
        'Configure Path',
        'Dismiss'
      );

      const installationGuide = InstallationGuide.getInstance();
      switch (action) {
        case 'Install SWI-Prolog':
          await installationGuide.showInstallationGuideDialog();
          break;
        case 'Setup Wizard':
          await commands.executeCommand('prolog.setupWizard');
          break;
        case 'Configure Path':
          await commands.executeCommand('workbench.action.openSettings', 'prolog.executablePath');
          break;
        default:
          break;
      }
    } else {
      const message =
        error.message || `Failed to run SWI-Prolog formatter using path: ${this._executable}`;
      window.showErrorMessage(`Prolog formatting failed: ${message}`);
    }
  }
  public provideDocumentRangeFormattingEdits(
    doc: TextDocument,
    range: Range,
    _options: FormattingOptions,
    _token: CancellationToken
  ): ProviderResult<TextEdit[]> {
    const docContent = doc.getText(range); // Get the content of the document;
    const offset = doc.getText(new Range(doc.positionAt(0), range.start)).length;
    let regexp = /%.*|\/\*[\w\W]*?\*\//gm; // Define regular expression for matching comments
    const array = [...docContent.matchAll(regexp)]; // Match all occurrences of comments in the substring
    // Replace comments with placeholder characters in the clause
    let clauseComment = docContent;
    array.forEach(Comment => {
      clauseComment = clauseComment.replace(Comment[0], new Array(Comment[0].length + 1).join(''));
    });
    regexp = /^\s*([a-z][a-zA-Z0-9_]*)/gm; // Define a regular expression for identifying Prolog clauses
    const arrayStart = [...clauseComment.matchAll(regexp)]; // Match all occurrences of Prolog clauses in the document
    regexp = /\.\s*$/gm; // Define a regular expression for identifying Prolog clauses end
    const arrayEnd = [...clauseComment.matchAll(regexp)]; // Match all occurrences of Prolog clauses in the document
    let min = 0;
    const clausesArray = [];
    for (let i = 0; i < arrayStart.length; i++) {
      if (arrayStart[i] && typeof arrayStart[i].index === 'number' && arrayStart[i].index >= min) {
        for (let j = 0; j < arrayEnd.length; j++) {
          if (
            arrayEnd[j] &&
            typeof arrayEnd[j].index === 'number' &&
            arrayEnd[j].index > (arrayStart[i]?.index ?? 0)
          ) {
            min = arrayEnd[j].index;
            clausesArray.push([
              (arrayStart[i]?.index ?? 0) + (typeof offset === 'number' ? offset : 0),
              (arrayEnd[j]?.index ?? 0) + (typeof offset === 'number' ? offset : 0),
            ]);
            break;
          }
        }
      }
    }
    let result: TextEdit[] = [];
    // Iterate over each matched clause and format it
    clausesArray.forEach(clause => {
      const clauseArray = this.getClauseString(doc, clause);
      clauseArray[0] = this.formatClause(clauseArray[0]);
      result = result.concat(TextEdit.replace(clauseArray[1], clauseArray[0]));
    });

    return result; // Return the formatted result
  }

  // Implementation of the provideDocumentFormattingEdits method required by DocumentFormattingEditProvider
  public provideDocumentFormattingEdits(
    document: TextDocument,
    _options: FormattingOptions,
    _token: CancellationToken
  ): ProviderResult<TextEdit[]> {
    const docContent = document.getText(); // Get the content of the document
    let regexp = /%.*|\/\*[\w\W]*?\*\//gm; // Define regular expression for matching comments
    const array = [...docContent.matchAll(regexp)]; // Match all occurrences of comments in the substring
    // Replace comments with placeholder characters in the clause
    let clauseComment = docContent;
    array.forEach(Comment => {
      clauseComment = clauseComment.replace(Comment[0], new Array(Comment[0].length + 1).join(''));
    });
    regexp = /^\s*([a-z][a-zA-Z0-9_]*)/gm; // Define a regular expression for identifying Prolog clauses
    const arrayStart = [...clauseComment.matchAll(regexp)]; // Match all occurrences of Prolog clauses in the document
    regexp = /\.\s*$/gm; // Define a regular expression for identifying Prolog clauses end
    const arrayEnd = [...clauseComment.matchAll(regexp)]; // Match all occurrences of Prolog clauses in the document
    let min = 0;
    const clausesArray = [];

    for (let i = 0; i < arrayStart.length; i++) {
      if (arrayStart[i] && typeof arrayStart[i].index === 'number' && arrayStart[i].index >= min) {
        for (let j = 0; j < arrayEnd.length; j++) {
          if (
            arrayEnd[j] &&
            typeof arrayEnd[j].index === 'number' &&
            arrayEnd[j].index > (arrayStart[i]?.index ?? 0)
          ) {
            min = arrayEnd[j].index;
            clausesArray.push([
              arrayStart[i]?.index ?? 0,
              arrayEnd[j]?.index ?? 0,
            ]);
            break;
          }
        }
      }
    }
    let result: TextEdit[] = [];
    // Iterate over each matched clause and format it
    clausesArray.forEach(clause => {
      const clauseArray = this.getClauseString(document, clause);
      clauseArray[0] = this.formatClause(clauseArray[0]);
      result = result.concat(TextEdit.replace(clauseArray[1], clauseArray[0]));
    });

    return result; // Return the formatted result
  }

  // Helper method to get the clause string and its range from the document
  private getClauseString(doc: TextDocument, range: number[]): [string, Range] {
    const docContent = doc.getText();
    if (
      Array.isArray(range) &&
      range.length === 2 &&
      typeof range[0] === 'number' &&
      typeof range[1] === 'number'
    ) {
      let sub = docContent.substring(range[0], range[1] + 1); // Extract the substring from the starting position to the end of the document
      const regexp = /^\s+/gm; // Define regular expression for matching comments
      const array = [...sub.matchAll(regexp)]; // Match all occurrences of starting spaces in the substring
      if (array.length !== 0 && array[0]) {
        sub = sub.slice(array[0][0].length);
        return [
          sub,
          new Range(doc.positionAt(range[0] + array[0][0].length), doc.positionAt(range[1] + 1)),
        ]; // Return the clause string and its range
      }
      return [sub, new Range(doc.positionAt(range[0]), doc.positionAt(range[1] + 1))]; // Return the clause string and its range
    }
    // fallback for invalid range
    return ['', new Range(0, 0, 0, 0)];
  }

  // Helper method to format a Prolog clause
  private formatClause(clause: string): string {
    // COMMENT
    let regexpComment = /%.*|\/\*[\w\W]*?\*\//gm;
    let arrayComment = [...clause.matchAll(regexpComment)];
    let clauseComment = clause;
    // Replace comments with placeholder characters in the clause
    arrayComment.forEach(Comment => {
      clauseComment = clauseComment.replace(Comment[0], new Array(Comment[0].length).join('') + '');
    });
    // STRING
    let regexpString = /\"((\\\(?:[^"])*\")/gm;
    let arrayString = [...clauseComment.matchAll(regexpString)];
    // Replace strings with placeholder characters in the clause
    arrayString.forEach(String => {
      clauseComment = clauseComment.replace(String[0], new Array(String[0].length + 1).join(''));
    });
    //EXTRACT HEAD
    let regexpHead = /^\s*[a-z][a-zA-Z0-9_]*(\\\(?([^.]|\.[^\s])*?(:-|=>|-->)|[^(,\n]*?(\.\s*?$)|\\\(([^.]*|\.[^\s]*?)\\\)\s*\.\s*?)$/gm;
    let arrayHead = [...clauseComment.matchAll(regexpHead)];
    if (arrayHead.length === 0 || !arrayHead[0]) {
      return clause; // Return original clause if no head found
    }
    let headComment = arrayHead[0][0];
    let head = clause.slice(0, (arrayHead[0].index ?? 0) + arrayHead[0][0].length);
    // Remove head from the clause and clauseComment
    clause = clause.slice((arrayHead[0].index ?? 0) + arrayHead[0][0].length);
    clauseComment = clauseComment.replace(headComment, '');
    //CONDENSATE
    let regexp = /(?<!\sis|mod|div|rem|xor|rdiv|in)\s(?!is\s|:-|mod|div|rem|xor|rdiv|in)/gm;
    let array = [...clauseComment.matchAll(regexp)];
    let offset = 0;
    // Remove unnecessary spaces in the clause and clauseComment
    array.forEach(space => {
      clause = [
        clause.slice(0, space.index + offset),
        clause.slice(space.index + space[0].length + offset),
      ].join('');
      clauseComment = [
        clauseComment.slice(0, space.index + offset),
        clauseComment.slice(space.index + space[0].length + offset),
      ].join('');
      offset -= space[0].length;
    });
    array = [...headComment.matchAll(regexp)];
    offset = 0;
    array.forEach(space => {
      head = [
        head.slice(0, space.index + offset),
        head.slice(space.index + space[0].length + offset),
      ].join('');
      headComment = [
        headComment.slice(0, space.index + offset),
        headComment.slice(space.index + space[0].length + offset),
      ].join('');
      offset -= space[0].length;
    });

    //OPERATOR
    let regexpOperator = /(?<=[\]\)\}])ins|(?<=[\]\)\}])in|[-*]?->|=>|\?-|:-|=?:=|\\\+|(?:<|=|@|@=||:|>:)<|(?:\\?)(?<![><#])=(?:\.\.|@=|=|\\=|)|@?>(?:=|>|)|:|\+|-|\\\/|\/\\|#=|#>|#\\=|#<==>|#/gm;
    let arrayOperator = [...clauseComment.matchAll(regexpOperator)];
    offset = 0;
    // Add spaces around operators in the clause and clauseComment
    arrayOperator.forEach(operator => {
      clause = [
        clause.slice(0, operator.index + offset),
        ' ' + operator[0] + ' ',
        clause.slice(operator.index + operator[0].length + offset),
      ].join('');
      clauseComment = [
        clauseComment.slice(0, operator.index + offset),
        ' ' + operator[0] + ' ',
        clauseComment.slice(operator.index + operator[0].length + offset),
      ].join('');
      offset += 2;
    });
    // Special case for '->'
    regexp = /^(\s*).*->\s/gm;
    array = [...clauseComment.matchAll(regexp)];
    offset = 0;
    // Split lines with '->' and add indentation
    array.forEach(l => {
      regexp = /->\s/gm;
      const array2 = [...l[0].matchAll(regexp)];
      array2.forEach(f => {
        const indentLength = l[1]?.length ?? 0;
        clause = [
          clause.slice(0, (l.index ?? 0) + (f.index ?? 0) + offset),
          '->\n' + new Array(indentLength + 2).join('\t'),
          clause.slice((l.index ?? 0) + (f.index ?? 0) + f[0].length + offset),
        ].join('');
        clauseComment = [
          clauseComment.slice(0, (l.index ?? 0) + (f.index ?? 0) + offset),
          '->\n' + new Array(indentLength + 2).join('\t'),
          clauseComment.slice((l.index ?? 0) + (f.index ?? 0) + f[0].length + offset),
        ].join('');
        offset += indentLength + 1;
      });
    });
    // Special case for ';'
    regexp = /^(\s*).*;(?=\S)/gm;
    array = [...clauseComment.matchAll(regexp)];
    offset = 0;
    array.forEach(l => {
      regexp = /;/gm;
      const array2 = [...l[0].matchAll(regexp)];
      array2.forEach(f => {
        const indentLength = l[1]?.length ?? 0;
        clause = [
          clause.slice(0, (l.index ?? 0) + (f.index ?? 0) + offset),
          ';\n' + new Array(indentLength + 1).join('\t'),
          clause.slice((l.index ?? 0) + (f.index ?? 0) + f[0].length + offset),
        ].join('');
        clauseComment = [
          clauseComment.slice(0, (l.index ?? 0) + (f.index ?? 0) + offset),
          ';\n' + new Array(indentLength + 1).join('\t'),
          clauseComment.slice((l.index ?? 0) + (f.index ?? 0) + f[0].length + offset),
        ].join('');
        offset += indentLength + 1;
      });
    });
    //NESTED
    const result = this.formatNested(clause, clauseComment);
    clause = result[0];
    clauseComment = result[1];
    //COMMAS
    const formatConfig = this._section.get('format') as { addSpace?: boolean } | undefined;
    if (formatConfig?.addSpace) {
      regexp = /,(?!\s)/gm;
      array = [...clauseComment.matchAll(regexp)];
      offset = 0;
      // Add space after commas in the clause and clauseComment
      array.forEach(comma => {
        clause = [
          clause.slice(0, comma.index + offset),
          comma[0] + ' ',
          clause.slice(comma.index + comma[0].length + offset),
        ].join('');
        clauseComment = [
          clauseComment.slice(0, comma.index + offset),
          comma[0] + ' ',
          clauseComment.slice(comma.index + comma[0].length + offset),
        ].join('');
        offset += 1;
      });

      array = [...headComment.matchAll(regexp)];
      offset = 0;
      array.forEach(comma => {
        head = [
          head.slice(0, comma.index + offset),
          comma[0] + ' ',
          head.slice(comma.index + comma[0].length + offset),
        ].join('');
        headComment = [
          headComment.slice(0, comma.index + offset),
          comma[0] + ' ',
          headComment.slice(comma.index + comma[0].length + offset),
        ].join('');
        offset += 1;
      });
    }
    //REPLACE COMMENT
    regexp = /^(\s*).*()/gm;
    array = [...clauseComment.matchAll(regexp)];
    let offset2 = 0;
    array.forEach(Comment => {
      regexp = //gm;
      const array2 = [...Comment[0].matchAll(regexp)];
      // Add newline and indentation after comments in the clause
      array2.forEach(h => {
        clause = [
          clause.slice(0, (Comment.index ?? 0) + (h.index ?? 0) + 1 + offset2),
          '\n' + new Array((Comment[1]?.length ?? 0) + 1).join('\t'),
          clause.slice((Comment.index ?? 0) + (h.index ?? 0) + 1 + offset2),
        ].join('');
        offset2 += 1 + (Comment[1]?.length ?? 0);
      });
    });

    array = [...headComment.matchAll(regexp)];
    let offset3 = 0;
    array.forEach(Comment => {
      regexp = //gm;
      const array2 = [...Comment[0].matchAll(regexp)];
      // Add newline and indentation after comments in the clause
      array2.forEach(h => {
        head = [
          head.slice(0, (Comment.index ?? 0) + (h.index ?? 0) + 1 + offset3),
          '\n' + new Array((Comment[1]?.length ?? 0) + 1).join(' '),
          head.slice((Comment.index ?? 0) + (h.index ?? 0) + 1 + offset3),
        ].join('');
        offset3 += 1 + (Comment[1]?.length ?? 0);
      });
    });
    if (clause !== '') {
      head = head + '\n\t';
      return head + clause; // Return the formatted clause
    } else {
      return head; // Return the formatted clause
    }
  }

  // Helper method to format nested expressions within a Prolog clause
  private formatNested(clause: string, clauseComment: string): [string, string] {
    let regexpNested = new RegExp('\\[[^\\[\\]]*?\\]|\\([^()]*?\\)|{\\|[\\w\\W]*?\\|}', 'gm'); // Define regular expression to find 0 deep expressions
    const array0deep = [...clauseComment.matchAll(regexpNested)]; // Find all occurrences of 0 deep expressions
    regexpNested = new RegExp('.(?=},?)|,|{|\\[', 'gm'); // Define regular expression to find 0 deep expressions
    const endLine = [...clauseComment.matchAll(regexpNested)]; // Find all end line

    let deep = 1;
    let offset = 0;
    const arrayDeep = [];

    // Deepness calculation per index
    for (let i = 0; i < clauseComment.length; i++) {
      const char = clauseComment[i];
      if (char && ['{', '[', '('].includes(char)) {
        deep = deep + 1;
      }
      if (char && ['}', ']', ')'].includes(char)) {
        deep = deep - 1;
      }
      arrayDeep.push(deep);
    }
    // For each line end verify if it's inside a 0 deep element
    for (let i = 0; i < endLine.length; i++) {
      let verif = true;
      for (let j = 0; j < array0deep.length; j++) {
        const endLineIndex = endLine[i]?.index ?? 0;
        const endLineLength = endLine[i]?.[0]?.length ?? 0;
        const array0deepIndex = array0deep[j]?.index ?? 0;
        const array0deepLength = array0deep[j]?.[0]?.length ?? 0;

        if (
          endLineIndex >= array0deepIndex &&
          endLineIndex + endLineLength < array0deepIndex + array0deepLength
        ) {
          verif = false;
        }
      }
      // If verif add line breaks and indentation depending of the deepness
      if (verif) {
        const endLineIndex = endLine[i]?.index ?? 0;
        const endLineLength = endLine[i]?.[0]?.length ?? 0;
        deep = arrayDeep[endLineIndex] ?? 1;

        clauseComment = [
          clauseComment.slice(0, endLineIndex + endLineLength + offset),
          '\n' + new Array(deep + 1).join('\t'),
          clauseComment.slice(endLineIndex + endLineLength + offset),
        ].join('');
        clause = [
          clause.slice(0, endLineIndex + endLineLength + offset),
          '\n' + new Array(deep + 1).join('\t'),
          clause.slice(endLineIndex + endLineLength + offset),
        ].join('');

        offset += 1 + deep;
      }
    }
    return [clause, clauseComment]; // Return the formatted clause and clauseComment
  }
}

import {
  LanguageClient,
  LanguageClientOptions,
  ServerOptions,
  TransportKind,
  RevealOutputChannelOn,
} from 'vscode-languageclient/node';
import { ExtensionContext, workspace, window } from 'vscode';
import * as path from 'path';

export class PrologLSPClient {
  private client: LanguageClient | null = null;
  private context: ExtensionContext;

  constructor(context: ExtensionContext) {
    this.context = context;
  }

  public async start(): Promise<void> {
    if (this.client) {
      return; // Already started
    }

    try {
      // The server is implemented as a separate Node.js module
      const serverModule = this.context.asAbsolutePath(
        path.join('out', 'pub', 'features', 'prologLSPServer.js')
      );

      // The debug options for the server
      const debugOptions = { execArgv: ['--nolazy', '--inspect=6009'] };

      // If the extension is launched in debug mode then the debug server options are used
      // Otherwise the run options are used
      const serverOptions: ServerOptions = {
        run: { module: serverModule, transport: TransportKind.ipc },
        debug: {
          module: serverModule,
          transport: TransportKind.ipc,
          options: debugOptions,
        },
      };

      // Options to control the language client
      const clientOptions: LanguageClientOptions = {
        // Register the server for Prolog documents
        documentSelector: [
          { scheme: 'file', language: 'prolog' },
          { scheme: 'untitled', language: 'prolog' },
        ],
        synchronize: {
          // Notify the server about file changes to '.pl', '.pro', '.prolog' files contained in the workspace
          fileEvents: workspace.createFileSystemWatcher('**/*.{pl,pro,prolog,plt,ecl}'),
        },
        revealOutputChannelOn: RevealOutputChannelOn.Never,
        initializationOptions: {
          // Pass configuration to the server
          settings: workspace.getConfiguration('prolog'),
        },
        middleware: {
          // Add custom middleware for enhanced functionality
          provideCompletionItem: async (document, position, context, token, next) => {
            const result = await next(document, position, context, token);

            // Enhance completions with custom logic if needed
            if (Array.isArray(result)) {
              return this.enhanceCompletions(result, document, position);
            }

            return result;
          },

          provideHover: async (document, position, token, next) => {
            const result = await next(document, position, token);

            // Enhance hover information
            if (result) {
              return this.enhanceHover(result, document, position);
            }

            return result;
          },
        },
      };

      // Create the language client and start the client.
      this.client = new LanguageClient(
        'prologLSP',
        'Prolog Language Server',
        serverOptions,
        clientOptions
      );

      // Register client event handlers
      this.registerEventHandlers();

      // Start the client. This will also launch the server
      await this.client.start();

      window.showInformationMessage('Prolog LSP Server started successfully');
    } catch (error) {
      window.showErrorMessage(`Failed to start Prolog LSP Server: ${error}`);
      throw error;
    }
  }

  public async stop(): Promise<void> {
    if (this.client) {
      await this.client.stop();
      this.client = null;
    }
  }

  public async restart(): Promise<void> {
    await this.stop();
    await this.start();
  }

  public isRunning(): boolean {
    return this.client !== null && this.client.state === 2; // Running state
  }

  private registerEventHandlers(): void {
    if (!this.client) {
      return;
    }

    // Handle server ready - use the client.start() promise instead of onReady()
    // The onReady() method has been removed in newer versions of vscode-languageclient
    // We'll register custom handlers after the client starts successfully

    // Handle server errors
    this.client.onDidChangeState(event => {
      console.log(`LSP Client state changed: ${event.oldState} -> ${event.newState}`);

      if (event.newState === 2) {
        // Running state - register handlers when client becomes ready
        console.log('Prolog LSP Server is ready');
        this.registerCustomHandlers();
      } else if (event.newState === 3) {
        // Stopped state
        window.showWarningMessage('Prolog LSP Server stopped unexpectedly');
      }
    });
  }

  private registerCustomHandlers(): void {
    if (!this.client) {
      return;
    }

    // Register custom request handlers for enhanced functionality
    this.client.onRequest('prolog/queryResult', params => {
      // Handle query results from server
      return this.handleQueryResult(params);
    });

    this.client.onRequest('prolog/helpResult', params => {
      // Handle help results from server
      return this.handleHelpResult(params);
    });

    this.client.onNotification('prolog/diagnosticsUpdate', params => {
      // Handle diagnostic updates
      this.handleDiagnosticsUpdate(params);
    });
  }

  private enhanceCompletions(completions: any[], document: any, position: any): any[] {
    // Add context-aware completions
    const text = document.getText();
    const line = text.split('\n')[position.line];

    // Add N3/RDF completions if in semantic web context
    if (this.isSemanticWebContext(text)) {
      const semanticCompletions = this.getSemanticWebCompletions();
      completions.push(...semanticCompletions);
    }

    // Add project-specific completions
    const projectCompletions = this.getProjectSpecificCompletions(document);
    completions.push(...projectCompletions);

    return completions;
  }

  private enhanceHover(hover: any, document: any, position: any): any {
    // Enhance hover with additional information
    const word = this.getWordAtPosition(document.getText(), position);

    if (word && this.isBuiltinPredicate(word)) {
      // Add links to documentation
      if (hover.contents) {
        const enhanced =
          hover.contents +
          '\n\n[ View full documentation](https://www.swi-prolog.org/pldoc/man?predicate=' +
          word +
          ')';
        return { ...hover, contents: enhanced };
      }
    }

    return hover;
  }

  private isSemanticWebContext(text: string): boolean {
    return (
      text.includes('@prefix') ||
      text.includes('rdf:') ||
      text.includes('rdfs:') ||
      text.includes('owl:')
    );
  }

  private getSemanticWebCompletions(): any[] {
    return [
      {
        label: 'rdf_load',
        kind: 3, // Function
        detail: 'rdf_load/2',
        documentation: 'Load RDF data from file or URL',
      },
      {
        label: 'rdf_assert',
        kind: 3,
        detail: 'rdf_assert/3',
        documentation: 'Assert RDF triple',
      },
      {
        label: 'rdf_retract',
        kind: 3,
        detail: 'rdf_retract/3',
        documentation: 'Retract RDF triple',
      },
    ];
  }

  private getProjectSpecificCompletions(document: any): any[] {
    // Analyze project structure and provide relevant completions
    const workspaceFolder = workspace.getWorkspaceFolder(document.uri);
    if (!workspaceFolder) {
      return [];
    }

    // This could be enhanced to scan project files for predicates
    return [];
  }

  private getWordAtPosition(text: string, position: any): string | null {
    const lines = text.split('\n');
    const line = lines[position.line];
    if (!line) {
      return null;
    }

    const char = position.character;
    let start = char;
    let end = char;

    while (start > 0 && /[a-zA-Z0-9_]/.test(line[start - 1])) {
      start--;
    }
    while (end < line.length && /[a-zA-Z0-9_]/.test(line[end])) {
      end++;
    }

    return start < end ? line.substring(start, end) : null;
  }

  private isBuiltinPredicate(predicate: string): boolean {
    const builtins = [
      'member',
      'append',
      'length',
      'reverse',
      'sort',
      'findall',
      'bagof',
      'setof',
      'assert',
      'retract',
      'write',
      'writeln',
      'nl',
      'is',
      'var',
      'nonvar',
    ];
    return builtins.includes(predicate);
  }

  private handleQueryResult(params: any): any {
    // Handle query results from server
    console.log('Query result received:', params);
    return null;
  }

  private handleHelpResult(params: any): any {
    // Handle help results from server
    console.log('Help result received:', params);
    return null;
  }

  private handleDiagnosticsUpdate(params: any): void {
    // Handle diagnostic updates
    console.log('Diagnostics updated:', params);
  }

  // Public API for external use
  public async executeQuery(query: string): Promise<any> {
    if (!this.client) {
      throw new Error('LSP client not started');
    }

    return await this.client.sendRequest('prolog/executeQuery', { query });
  }

  public async getHelp(predicate: string): Promise<any> {
    if (!this.client) {
      throw new Error('LSP client not started');
    }

    return await this.client.sendRequest('prolog/getHelp', { predicate });
  }

  public async consultFile(filePath: string): Promise<any> {
    if (!this.client) {
      throw new Error('LSP client not started');
    }

    return await this.client.sendRequest('prolog/consultFile', { filePath });
  }
}

import {
  ExtensionContext,
  languages,
  workspace,
  window,
  commands,
  Uri,
  Range,
  Position,
  TextDocument,
  CancellationToken,
  ProviderResult,
  CodeAction,
  CodeActionKind,
  CodeActionContext,
  Diagnostic,
  DiagnosticSeverity,
  DiagnosticCollection,
  CompletionItem,
  CompletionItemKind,
  MarkdownString,
} from 'vscode';
import { PrologBackend } from '../prologBackend';

/**
 * LSP Extension for Prolog that provides custom requests for queries, help, and N3 diagnostics
 * This works alongside existing language providers to enhance the development experience
 */
export class PrologLSPExtension {
  private backend: PrologBackend | null = null;
  private diagnosticCollection: DiagnosticCollection;
  private context: ExtensionContext;

  constructor(context: ExtensionContext, backend: PrologBackend | null) {
    this.context = context;
    this.backend = backend;
    this.diagnosticCollection = languages.createDiagnosticCollection('prolog-lsp');
    context.subscriptions.push(this.diagnosticCollection);
  }

  /**
   * Register all LSP extension features
   */
  public registerFeatures() {
    // Register custom commands for LSP-style operations
    this.registerCustomCommands();

    // Register enhanced code actions
    this.registerCodeActionProvider();

    // Register enhanced completion provider
    this.registerEnhancedCompletionProvider();

    // Register N3 diagnostics
    this.registerN3DiagnosticsProvider();
  }

  /**
   * Register custom LSP-style commands
   */
  private registerCustomCommands() {
    // Custom query execution command
    const queryCommand = commands.registerCommand(
      'prolog.lsp.executeQuery',
      async (query?: string) => {
        if (!query) {
          query = await window.showInputBox({
            prompt: 'Enter Prolog query',
            placeHolder: 'member(X, [1,2,3])',
          });
        }

        if (query && this.backend?.isRunning()) {
          try {
            const response = await this.backend.sendRequest('query', {
              goal: query,
              timeoutMs: 10000,
            });

            if (response.status === 'ok') {
              this.showQueryResults(response.results || []);
            } else {
              window.showErrorMessage(`Query failed: ${response.error}`);
            }
          } catch (error) {
            window.showErrorMessage(`Query error: ${error}`);
          }
        }
      }
    );

    // Custom help lookup command
    const helpCommand = commands.registerCommand(
      'prolog.lsp.getHelp',
      async (predicate?: string) => {
        if (!predicate) {
          predicate = await window.showInputBox({
            prompt: 'Enter predicate name',
            placeHolder: 'member/2',
          });
        }

        if (predicate && this.backend?.isRunning()) {
          try {
            const response = await this.backend.sendRequest('help', {
              predicate,
              timeoutMs: 5000,
            });

            if (response.status === 'ok' && response.doc) {
              this.showHelpDocumentation(response.doc);
            } else {
              window.showInformationMessage(`No help found for ${predicate}`);
            }
          } catch (error) {
            window.showErrorMessage(`Help lookup error: ${error}`);
          }
        }
      }
    );

    // N3 diagnostics command
    const n3DiagnosticsCommand = commands.registerCommand(
      'prolog.lsp.runN3Diagnostics',
      async (uri?: Uri) => {
        const document = uri
          ? await workspace.openTextDocument(uri)
          : window.activeTextEditor?.document;

        if (document && document.languageId === 'prolog') {
          await this.runN3Diagnostics(document);
        }
      }
    );

    this.context.subscriptions.push(queryCommand, helpCommand, n3DiagnosticsCommand);
  }

  /**
   * Register enhanced code action provider
   */
  private registerCodeActionProvider() {
    const provider = languages.registerCodeActionsProvider('prolog', {
      provideCodeActions: (
        document: TextDocument,
        range: Range,
        context: CodeActionContext,
        token: CancellationToken
      ): ProviderResult<CodeAction[]> => {
        const actions: CodeAction[] = [];

        // Add query execution action
        const queryAction = new CodeAction('Execute as Query', CodeActionKind.Empty);
        queryAction.command = {
          command: 'prolog.lsp.executeQuery',
          title: 'Execute as Query',
          arguments: [document.getText(range)],
        };
        actions.push(queryAction);

        // Add help lookup action for predicates
        const text = document.getText(range);
        const predicateMatch = text.match(/([a-z][a-zA-Z0-9_]*)\s*\(/);
        if (predicateMatch) {
          const helpAction = new CodeAction('Get Help', CodeActionKind.Empty);
          helpAction.command = {
            command: 'prolog.lsp.getHelp',
            title: 'Get Help',
            arguments: [predicateMatch[1]],
          };
          actions.push(helpAction);
        }

        // Add N3 diagnostics action
        if (this.isN3Content(document.getText())) {
          const n3Action = new CodeAction('Run N3 Diagnostics', CodeActionKind.Source);
          n3Action.command = {
            command: 'prolog.lsp.runN3Diagnostics',
            title: 'Run N3 Diagnostics',
            arguments: [document.uri],
          };
          actions.push(n3Action);
        }

        return actions;
      },
    });

    this.context.subscriptions.push(provider);
  }

  /**
   * Register enhanced completion provider with backend integration
   */
  private registerEnhancedCompletionProvider() {
    const provider = languages.registerCompletionItemProvider(
      'prolog',
      {
        provideCompletionItems: async (
          document: TextDocument,
          position: Position,
          token: CancellationToken
        ): Promise<CompletionItem[]> => {
          const items: CompletionItem[] = [];

          // Get context around cursor
          const line = document.lineAt(position);
          const prefix = line.text.substring(0, position.character);

          // Add built-in predicate completions
          if (this.backend?.isRunning()) {
            try {
              // This would require extending the backend to support predicate listing
              const builtins = await this.getBuiltinPredicates();
              builtins.forEach(predicate => {
                const item = new CompletionItem(predicate.name, CompletionItemKind.Function);
                item.detail = predicate.arity
                  ? `${predicate.name}/${predicate.arity}`
                  : predicate.name;
                item.documentation = new MarkdownString(
                  predicate.description || 'Built-in predicate'
                );
                items.push(item);
              });
            } catch (_error) {
              // Fallback to static completions
            }
          }

          // Add N3 specific completions if in N3 context
          if (this.isN3Content(document.getText())) {
            items.push(...this.getN3Completions());
          }

          return items;
        },
      },
      '(',
      ',',
      ' '
    );

    this.context.subscriptions.push(provider);
  }

  /**
   * Register N3 diagnostics provider
   */
  private registerN3DiagnosticsProvider() {
    // Run diagnostics on document changes
    const disposable = workspace.onDidChangeTextDocument(async event => {
      if (event.document.languageId === 'prolog' && this.isN3Content(event.document.getText())) {
        await this.runN3Diagnostics(event.document);
      }
    });

    this.context.subscriptions.push(disposable);
  }

  /**
   * Run N3 diagnostics on a document
   */
  private async runN3Diagnostics(document: TextDocument) {
    if (!this.backend?.isRunning()) {
      return;
    }

    try {
      const diagnostics: Diagnostic[] = [];
      const content = document.getText();

      // Try to load N3 content and check for errors
      const response = await this.backend.sendRequest('n3_load', {
        content,
        validate: true,
        timeoutMs: 5000,
      });

      if (response.status === 'error') {
        // Parse error information and create diagnostics
        const diagnostic = new Diagnostic(
          new Range(0, 0, 0, 0), // Would need better error location parsing
          response.error || 'N3 validation error',
          DiagnosticSeverity.Error
        );
        diagnostic.source = 'prolog-n3';
        diagnostics.push(diagnostic);
      }

      // Check for common N3 issues
      diagnostics.push(...this.checkN3CommonIssues(document));

      this.diagnosticCollection.set(document.uri, diagnostics);
    } catch (error) {
      console.error('N3 diagnostics error:', error);
    }
  }

  /**
   * Check for common N3 issues
   */
  private checkN3CommonIssues(document: TextDocument): Diagnostic[] {
    const diagnostics: Diagnostic[] = [];
    const text = document.getText();
    const lines = text.split('\n');

    lines.forEach((line, index) => {
      // Check for missing prefixes
      if (
        line.includes(':') &&
        !line.includes('@prefix') &&
        !text.includes(`@prefix ${line.split(':')[0]}:`)
      ) {
        const diagnostic = new Diagnostic(
          new Range(index, 0, index, line.length),
          `Undefined prefix: ${line.split(':')[0]}`,
          DiagnosticSeverity.Warning
        );
        diagnostic.source = 'prolog-n3';
        diagnostics.push(diagnostic);
      }

      // Check for malformed triples
      if (line.trim().endsWith('.') && !line.includes('@') && line.split(/\s+/).length < 3) {
        const diagnostic = new Diagnostic(
          new Range(index, 0, index, line.length),
          'Incomplete triple statement',
          DiagnosticSeverity.Error
        );
        diagnostic.source = 'prolog-n3';
        diagnostics.push(diagnostic);
      }
    });

    return diagnostics;
  }

  /**
   * Show query results in a user-friendly way
   */
  private showQueryResults(results: any[]) {
    if (results.length === 0) {
      window.showInformationMessage('Query succeeded with no results');
      return;
    }

    // Format results for display
    const formatted = results
      .map((result, index) => {
        if (typeof result === 'object' && result !== null) {
          const bindings = Object.entries(result)
            .map(([key, value]) => `${key} = ${value}`)
            .join(', ');
          return `Solution ${index + 1}: ${bindings}`;
        }
        return `Solution ${index + 1}: ${JSON.stringify(result)}`;
      })
      .join('\n');

    // Show in output channel or information message
    window.showInformationMessage(`Query Results:\n${formatted}`, { modal: true });
  }

  /**
   * Show help documentation
   */
  private showHelpDocumentation(doc: any) {
    const content = `# ${doc.name}/${doc.arity}\n\n${doc.summary || 'No description available'}`;

    // Could create a webview or show in information message
    window.showInformationMessage(content, { modal: true });
  }

  /**
   * Check if document contains N3 content
   */
  private isN3Content(text: string): boolean {
    return (
      text.includes('@prefix') ||
      text.includes('@base') ||
      text.includes('rdf:') ||
      text.includes('rdfs:')
    );
  }

  /**
   * Get built-in predicates (would need backend support)
   */
  private async getBuiltinPredicates(): Promise<
    Array<{ name: string; arity?: number; description?: string }>
  > {
    // This would require extending the backend to list predicates
    // For now, return a static list of common predicates
    return [
      { name: 'member', arity: 2, description: 'True if Elem is a member of List' },
      {
        name: 'append',
        arity: 3,
        description: 'True if List3 is the concatenation of List1 and List2',
      },
      { name: 'findall', arity: 3, description: 'Find all solutions to Goal' },
      { name: 'bagof', arity: 3, description: 'Collect solutions to Goal' },
      { name: 'setof', arity: 3, description: 'Collect unique solutions to Goal' },
      { name: 'length', arity: 2, description: 'True if Length is the length of List' },
      { name: 'reverse', arity: 2, description: 'True if List2 is the reverse of List1' },
      { name: 'sort', arity: 2, description: 'True if Sorted is the sorted version of List' },
    ];
  }

  /**
   * Get N3-specific completions
   */
  private getN3Completions(): CompletionItem[] {
    const items: CompletionItem[] = [];

    // Common N3 prefixes
    const prefixes = [
      { name: '@prefix rdf:', detail: 'RDF namespace' },
      { name: '@prefix rdfs:', detail: 'RDF Schema namespace' },
      { name: '@prefix owl:', detail: 'OWL namespace' },
      { name: '@prefix xsd:', detail: 'XML Schema namespace' },
    ];

    prefixes.forEach(prefix => {
      const item = new CompletionItem(prefix.name, CompletionItemKind.Keyword);
      item.detail = prefix.detail;
      items.push(item);
    });

    // Common N3 properties
    const properties = [
      'rdf:type',
      'rdfs:label',
      'rdfs:comment',
      'owl:sameAs',
      'owl:differentFrom',
    ];

    properties.forEach(prop => {
      const item = new CompletionItem(prop, CompletionItemKind.Property);
      item.detail = 'N3 property';
      items.push(item);
    });

    return items;
  }

  /**
   * Dispose of resources
   */
  public dispose() {
    this.diagnosticCollection.dispose();
  }
}

