const defaultSettings: PrologSettings = {
  executablePath: 'swipl',
  dialect: 'swi',
  linter: {
    run: 'onType',
    delay: 500,
    enableMsgInOutput: false,
  },
  format: {
    addSpace: true,
  },
  terminal: {
    runtimeArgs: [],
  },
};

let globalSettings: PrologSettings = defaultSettings;
const documentSettings: Map<string, Promise<PrologSettings>> = new Map();

// Semantic tokens legend
const semanticTokensLegend: SemanticTokensLegend = {
  tokenTypes: [
    'namespace',
    'type',
    'class',
    'enum',
    'interface',
    'struct',
    'typeParameter',
    'parameter',
    'variable',
    'property',
    'enumMember',
    'event',
    'function',
    'method',
    'macro',
    'keyword',
    'modifier',
    'comment',
    'string',
    'number',
    'regexp',
    'operator',
    'decorator',
  ],
  tokenModifiers: [
    'declaration',
    'definition',
    'readonly',
    'static',
    'deprecated',
    'abstract',
    'async',
    'modification',
