'use strict';

import { Utils } from '../utils/utils';
import {
  Terminal,
  window,
  workspace,
  TextDocument,
  Disposable,
  OutputChannel,
  TextEditor,
  commands,
} from 'vscode';
import jsesc from 'jsesc';
import { InstallationGuide } from './installationGuide';
import { PlatformUtils, PlatformType } from '../utils/platformUtils';
import { ExecutableFinder } from '../utils/executableFinder';
import { spawn } from 'child_process';
import * as os from 'os';

/**
 * Shell information interface
 */
interface ShellInfo {
  name: string;
  path: string;
  args: string[];
  supportsColors: boolean;
  requiresQuoting: boolean;
}

/**
 * Terminal configuration interface
 */
interface TerminalConfig {
  executable: string;
  args: string[];
  shell?: ShellInfo;
  env?: Record<string, string>;
  cwd?: string;
}

export default class PrologTerminal {
  private static _terminal: Terminal;
  private static _document: TextDocument;
  private static _platform: PlatformType;
  private static _shellInfo: ShellInfo | null = null;

  constructor() {
    // Static terminal class - no instance initialization needed
  }
  // Initialize the Prolog terminal
  public static init(): Disposable {
    PrologTerminal._platform = PlatformUtils.getPlatform();
    return (<any>window).onDidCloseTerminal((terminal: any) => {
      PrologTerminal._terminal = null as any;
      terminal.dispose();
    });
  }

  /**
   * Detect the default shell for the current platform
   */
  private static async detectShell(): Promise<ShellInfo> {
    if (PrologTerminal._shellInfo) {
      return PrologTerminal._shellInfo;
    }

    let shellInfo: ShellInfo;

    switch (PrologTerminal._platform) {
      case 'windows': {
        shellInfo = await PrologTerminal.detectWindowsShell();
        break;
      }
      case 'macos': {
        shellInfo = await PrologTerminal.detectMacOSShell();
        break;
      }
      case 'linux': {
        shellInfo = await PrologTerminal.detectLinuxShell();
        break;
      }
      default:
        // Fallback to basic shell
        shellInfo = {
          name: 'sh',
          path: '/bin/sh',
          args: [],
          supportsColors: false,
          requiresQuoting: true,
        };
        break;
    }

    PrologTerminal._shellInfo = shellInfo;
    return shellInfo;
  }

  /**
   * Detect Windows shell (PowerShell, Command Prompt, WSL)
   */
  private static async detectWindowsShell(): Promise<ShellInfo> {
    // Check for PowerShell Core first
    try {
      await PrologTerminal.testCommand('pwsh', ['--version']);
      return {
        name: 'PowerShell Core',
        path: 'pwsh',
        args: ['-NoLogo'],
        supportsColors: true,
        requiresQuoting: true,
      };
    } catch (_error) {
      // Ignore shell detection failure
    }

    // Check for Windows PowerShell
    try {
      await PrologTerminal.testCommand('powershell', ['-Version']);
      return {
        name: 'Windows PowerShell',
        path: 'powershell',
        args: ['-NoLogo'],
        supportsColors: true,
        requiresQuoting: true,
      };
    } catch (_error) {
      // Ignore shell detection failure
    }

    // Check for WSL
    try {
      await PrologTerminal.testCommand('wsl', ['--version']);
      return {
        name: 'WSL',
        path: 'wsl',
        args: [],
        supportsColors: true,
        requiresQuoting: true,
      };
    } catch (_error) {
      // Ignore bash detection failure
    }

    // Fallback to Command Prompt
    return {
      name: 'Command Prompt',
      path: 'cmd',
      args: ['/K'],
      supportsColors: false,
      requiresQuoting: true,
    };
  }

  /**
   * Detect macOS shell (zsh, bash)
   */
  private static async detectMacOSShell(): Promise<ShellInfo> {
    // Check for zsh (default on macOS Catalina+)
    try {
      await PrologTerminal.testCommand('zsh', ['--version']);
      return {
        name: 'Zsh',
        path: '/bin/zsh',
        args: [],
        supportsColors: true,
        requiresQuoting: true,
      };
    } catch (_error) {
      // Ignore zsh detection failure
    }

    // Check for bash
    try {
      await PrologTerminal.testCommand('bash', ['--version']);
      return {
        name: 'Bash',
        path: '/bin/bash',
        args: [],
        supportsColors: true,
        requiresQuoting: true,
      };
    } catch (_error) {
      // Ignore WSL detection failure
    }

    // Fallback to sh
    return {
      name: 'Shell',
      path: '/bin/sh',
      args: [],
      supportsColors: false,
      requiresQuoting: true,
    };
  }

  /**
   * Detect Linux shell (bash, zsh, fish, etc.)
   */
  private static async detectLinuxShell(): Promise<ShellInfo> {
    // Get user's default shell from environment
    const userShell = process.env.SHELL;
    if (userShell) {
      const shellName = PlatformUtils.basename(userShell);
      try {
        await PrologTerminal.testCommand(userShell, ['--version']);
        return {
          name: shellName,
          path: userShell,
          args: [],
          supportsColors: true,
          requiresQuoting: true,
        };
      } catch (_error) {
        // Ignore Windows PowerShell detection failure
      }
    }

    // Check for common shells
    const shells = [
      { name: 'bash', path: '/bin/bash' },
      { name: 'zsh', path: '/bin/zsh' },
      { name: 'fish', path: '/usr/bin/fish' },
      { name: 'dash', path: '/bin/dash' },
    ];

    for (const shell of shells) {
      try {
        await PrologTerminal.testCommand(shell.path, ['--version']);
        return {
          name: shell.name,
          path: shell.path,
          args: [],
          supportsColors: shell.name !== 'dash',
          requiresQuoting: true,
        };
      } catch (_error) {
        // Ignore PowerShell Core detection failure
      }
    }

    // Fallback to sh
    return {
      name: 'Shell',
      path: '/bin/sh',
      args: [],
      supportsColors: false,
      requiresQuoting: true,
    };
  }

  /**
   * Test if a command is available
   */
  private static async testCommand(command: string, args: string[]): Promise<boolean> {
    return new Promise(resolve => {
      const process = spawn(command, args, {
        stdio: ['ignore', 'ignore', 'ignore'],
        timeout: 3000,
      });

      process.on('close', code => {
        resolve(code === 0);
      });

      process.on('error', () => {
        resolve(false);
      });

      setTimeout(() => {
        process.kill();
        resolve(false);
      }, 3000);
    });
  }

  /**
   * Get platform-specific terminal configuration
   */
  private static async getTerminalConfig(): Promise<TerminalConfig> {
    const section = workspace.getConfiguration('prolog');
    let executable = section.get<string>(
      'executablePath',
      PlatformUtils.getDefaultExecutablePath()
    );

    // Enhanced executable resolution
    if (
      !(await PlatformUtils.pathExists(executable)) ||
      !(await PlatformUtils.isExecutable(executable))
    ) {
      const executableFinder = new ExecutableFinder();
      const detectionResult = await executableFinder.findSwiplExecutable();
      if (detectionResult.found && detectionResult.path) {
        executable = detectionResult.path;
      }
    }

    executable = PlatformUtils.normalizePath(executable);

    // Get platform-specific runtime arguments
    const args =
      section.get<string[]>('terminal.runtimeArgs') || PlatformUtils.getDefaultRuntimeArgs();

    // Detect shell for better integration
    const shell = await PrologTerminal.detectShell();

    // Platform-specific environment variables
    const env: Record<string, string> = {};

    switch (PrologTerminal._platform) {
      case 'windows': {
        // Windows-specific environment setup
        env.TERM = 'xterm-256color';
        break;
      }
      case 'macos': {
        // macOS-specific environment setup
        env.TERM = 'xterm-256color';
        env.LANG = process.env.LANG || 'en_US.UTF-8';
        break;
      }
      case 'linux': {
        // Linux-specific environment setup
        env.TERM = 'xterm-256color';
        env.LANG = process.env.LANG || 'en_US.UTF-8';
        break;
      }
    }

    return {
      executable,
      args,
      shell,
      env,
      cwd: workspace.workspaceFolders?.[0]?.uri.fsPath || process.cwd(),
    };
  }

  /**
   * Escape command arguments for the detected shell
   */
  private static escapeForShell(text: string, shell: ShellInfo): string {
    if (!shell.requiresQuoting) {
      return text;
    }

    switch (PrologTerminal._platform) {
      case 'windows': {
        // Windows shell escaping
        if (shell.name.includes('PowerShell')) {
          // PowerShell escaping
          return `'${text.replace(/'/g, "''")}'`;
        } else {
          // Command Prompt escaping
          return `"${text.replace(/"/g, '""')}"`;
        }
      }
      case 'macos':
      case 'linux': {
        // Unix shell escaping
        if (text.includes(' ') || text.includes('"') || text.includes("'")) {
          return `'${text.replace(/'/g, "'\"'\"'")}'`;
        }
        return text;
      }
      default:
        return text;
    }
  }

  // Create a Prolog terminal instance
  private static async createPrologTerm() {
    if (PrologTerminal._terminal) {
      return;
    }

    try {
      const config = await PrologTerminal.getTerminalConfig();
      const title = 'Prolog';

      // Validate executable exists and has proper permissions
      if (!(await PlatformUtils.pathExists(config.executable))) {
        throw new Error(`Executable not found: ${config.executable}`);
      }

      if (!(await PlatformUtils.isExecutable(config.executable))) {
        const platform = PlatformUtils.getPlatform();
        let permissionError = `Executable lacks execute permissions: ${config.executable}`;
        if (platform !== 'windows') {
          permissionError += `\n\nTry fixing permissions with: chmod +x "${config.executable}"`;
        }
        throw new Error(permissionError);
      }

      // Create terminal with enhanced configuration
      const terminalOptions: any = {
        name: title,
        shellPath: config.executable,
        shellArgs: config.args,
        cwd: config.cwd,
        env: {
          ...process.env,
          ...config.env,
        },
      };

      // Add platform-specific terminal options
      switch (PrologTerminal._platform) {
        case 'windows': {
          // Windows-specific terminal options
          terminalOptions.hideFromUser = false;
          break;
        }
        case 'macos': {
          // macOS-specific terminal options
          terminalOptions.strictEnv = false;
          break;
        }
        case 'linux': {
          // Linux-specific terminal options
          terminalOptions.strictEnv = false;
          break;
        }
      }

      PrologTerminal._terminal = window.createTerminal(terminalOptions);

      // Send initial setup commands based on platform and shell
      setTimeout(async () => {
        await PrologTerminal.sendInitialSetupCommands(config);
      }, 1000);
    } catch (error: any) {
      await PrologTerminal.handleTerminalCreationError(error);
      throw error;
    }
  }

  /**
   * Send initial setup commands to configure the terminal environment
   */
  private static async sendInitialSetupCommands(config: TerminalConfig) {
    if (!PrologTerminal._terminal) return;

    const shell = config.shell;
    if (!shell) return;

    // Platform-specific setup commands
    const setupCommands: string[] = [];

    switch (PrologTerminal._platform) {
      case 'windows': {
        if (shell.name.includes('PowerShell')) {
          setupCommands.push('$Host.UI.RawUI.WindowTitle = "Prolog Terminal"');
          if (shell.supportsColors) {
            setupCommands.push('$PSStyle.OutputRendering = "Host"');
          }
        } else if (shell.name === 'Command Prompt') {
          setupCommands.push('title Prolog Terminal');
        }
        break;
      }
      case 'macos':
      case 'linux': {
        if (shell.supportsColors) {
          setupCommands.push('export TERM=xterm-256color');
        }
        // Set terminal title
        setupCommands.push('echo -ne "\\033]0;Prolog Terminal\\007"');
        break;
      }
    }

    // Send setup commands
    for (const command of setupCommands) {
      PrologTerminal._terminal.sendText(command);
    }
  }

  /**
   * Handle terminal creation errors with enhanced error messages and recovery options
   */
  private static async handleTerminalCreationError(error: any) {
    let errorMessage = 'Failed to create Prolog terminal';
    let isExecutableError = false;

    if (error.code === 'ENOENT' || error.message?.includes('not found')) {
      errorMessage =
        'SWI-Prolog executable not found. The terminal requires SWI-Prolog to run interactive Prolog sessions.';
      isExecutableError = true;
    } else if (error.message?.includes('permission')) {
      errorMessage = `Permission error: ${error.message}`;
      isExecutableError = true;
    } else {
      errorMessage = `Failed to create Prolog terminal: ${error.message || error}`;
    }

    if (isExecutableError) {
      const action = await window.showErrorMessage(
        errorMessage,
        'Install with Package Manager',
        'Installation Guide',
        'Setup Wizard',
        'Configure Path',
        'Dismiss'
      );

      const installationGuide = InstallationGuide.getInstance();
      switch (action) {
        case 'Install with Package Manager': {
          const { PackageManagerIntegration } = await import('./packageManagerIntegration');
          const packageManager = PackageManagerIntegration.getInstance();
          await packageManager.showInstallationDialog();
          break;
        }
        case 'Installation Guide': {
          await installationGuide.showInstallationGuideDialog();
          break;
        }
        case 'Setup Wizard': {
          await commands.executeCommand('prolog.setupWizard');
          break;
        }
        case 'Configure Path': {
          await commands.executeCommand('workbench.action.openSettings', 'prolog.executablePath');
          break;
        }
      }
    } else {
      window.showErrorMessage(errorMessage);
    }
  }
  // Send a string to the Prolog terminal
  public static async sendString(text: string) {
    try {
      await PrologTerminal.createPrologTerm();

      // Get shell info for proper escaping
      const shell = await PrologTerminal.detectShell();

      // finish goal by .
      if (!text.endsWith('.')) {
        text += '.';
      }

      // Escape text for the detected shell if needed
      const escapedText = PrologTerminal.escapeForShell(text, shell);

      PrologTerminal._terminal.sendText(escapedText);
      PrologTerminal._terminal.show(false);
    } catch (error) {
      // Error already handled in createPrologTerm
      console.error('Failed to send string to Prolog terminal:', error);
    }
  }
  // load the prolog file
  public static async loadDocument() {
    if (!window.activeTextEditor) {
      return;
    }
    try {
      PrologTerminal._document = window.activeTextEditor.document; // Get the active Prolog document
      await PrologTerminal.createPrologTerm(); // Create the Prolog terminal
      // Get the file name and escape it using jsesc
      const fname = jsesc(PlatformUtils.normalizePath(PrologTerminal._document.fileName), {
        quotes: 'single',
      });
      const goals = `['${fname}']`; // Define the goals to load the Prolog file
      // load the file into swipl with a goal
      if (PrologTerminal._document.isDirty) {
        PrologTerminal._document.save().then(_ => {
          PrologTerminal.sendString(goals);
        });
      } else {
        await PrologTerminal.sendString(goals);
      }
    } catch (error) {
      // Error already handled in createPrologTerm
      console.error('Failed to load document in Prolog terminal:', error);
    }
  }
  // query the goal under the cursor command
  public static queryGoalUnderCursor() {
    // Get the active text editor and document
    if (!window.activeTextEditor) {
      return;
    }
    const editor: TextEditor = window.activeTextEditor;
    const doc: TextDocument = editor.document;
    const pred = Utils.getPredicateUnderCursor(doc, editor.selection.active); // Get the predicate under the cursor using utility function
    // if no predicate under cursor
    if (!pred) {
      return;
    }
    PrologTerminal.loadDocument(); // Load the current Prolog document into the Prolog terminal
    let goal = pred?.wholePred || ''; // Extract the goal from the predicate
    // Separate the module if present
    if (goal.indexOf(':') > -1) {
      const parts = goal.split(':');
      goal = parts.length > 1 ? parts[1] : goal;
    }
    PrologTerminal.sendString(goal);
  }
}

import { EventEmitter } from 'events';
import * as fs from 'fs';
import * as path from 'path';
import { QueryStatus } from './queryNotificationManager';

export interface QueryHistoryEntry {
  id: string;
  cmd: string;
  params: Record<string, unknown>;
  status: 'pending' | 'running' | 'completed' | 'error' | 'cancelled' | 'timeout';
  startTime: number;
  endTime?: number;
  duration?: number;
  results?: unknown;
  error?: unknown;
  priority?: string;
  deleted?: boolean;
  resourceUsage?: {
    memoryMB?: number;
    cpuPercent?: number;
  };
  metadata?: {
    userAgent?: string;
    sessionId?: string;
    workspaceId?: string;
    tags?: string[];
  };
}

export interface QueryHistoryFilter {
  status?: string[];
  startDate?: Date;
  endDate?: Date;
  cmd?: string;
  priority?: string[];
  tags?: string[];
  limit?: number;
  offset?: number;
  sortBy?: 'startTime' | 'endTime' | 'duration' | 'cmd';
  sortOrder?: 'asc' | 'desc';
}

export interface QueryHistoryStats {
  totalQueries: number;
  completedQueries: number;
  errorQueries: number;
  cancelledQueries: number;
  timeoutQueries: number;
  averageDuration: number;
  totalDuration: number;
  queriesByStatus: Record<string, number>;
  queriesByPriority: Record<string, number>;
  queriesByCmd: Record<string, number>;
  dailyStats: Array<{
    date: string;
    count: number;
    avgDuration: number;
  }>;
}

export interface QueryHistoryOptions {
  storageDir: string;
  maxHistorySize: number;
  compressionEnabled: boolean;
  retentionDays: number;
  autoCleanup: boolean;
  batchSize: number;
}

/**
 * Persistent query history storage and management system
 */
export class QueryHistoryManager extends EventEmitter {
  private static instance: QueryHistoryManager;
  private options: QueryHistoryOptions;
  private historyFile: string;
  private indexFile: string;
  private memoryCache: Map<string, QueryHistoryEntry> = new Map();
  private writeQueue: QueryHistoryEntry[] = [];
  private writeInterval?: ReturnType<typeof setInterval>;
  private cleanupInterval?: ReturnType<typeof setInterval>;
  private isInitialized: boolean = false;

  /**
   * Get singleton instance of QueryHistoryManager
   */
  public static getInstance(options?: Partial<QueryHistoryOptions>): QueryHistoryManager {
    if (!QueryHistoryManager.instance) {
      QueryHistoryManager.instance = new QueryHistoryManager(options);
    }
    return QueryHistoryManager.instance;
  }

  constructor(options: Partial<QueryHistoryOptions> = {}) {
    super();

    this.options = {
      storageDir: path.join(process.cwd(), '.prolog-history'),
      maxHistorySize: 10000,
      compressionEnabled: true,
      retentionDays: 30,
      autoCleanup: true,
      batchSize: 100,
      ...options,
    };

    this.historyFile = path.join(this.options.storageDir, 'query-history.jsonl');
    this.indexFile = path.join(this.options.storageDir, 'query-index.json');

    this.initialize();
  }

  /**
   * Initialize the history manager
   */
  private async initialize(): Promise<void> {
    try {
      // Ensure storage directory exists
      if (!fs.existsSync(this.options.storageDir)) {
        fs.mkdirSync(this.options.storageDir, { recursive: true });
      }

      // Load existing history into memory cache
      await this.loadHistoryFromDisk();

      // Start background processes
      this.startWriteProcessor();
      if (this.options.autoCleanup) {
        this.startCleanupProcessor();
      }

      this.isInitialized = true;
      this.emit('initialized');
      console.log(`[QueryHistoryManager] Initialized with ${this.memoryCache.size} entries`);
    } catch (error: unknown) {
      console.error('[QueryHistoryManager] Initialization failed:', error);
      this.emit('error', error);
    }
  }

  /**
   * Add a query to the history
   */
  async addQuery(entry: Omit<QueryHistoryEntry, 'duration'>): Promise<void> {
    if (!this.isInitialized) {
      throw new Error('QueryHistoryManager not initialized');
    }

    const historyEntry: QueryHistoryEntry = {
      ...entry,
    };

    // Only set duration if endTime is provided
    if (entry.endTime) {
      historyEntry.duration = entry.endTime - entry.startTime;
    }

    // Add to memory cache
    this.memoryCache.set(entry.id, historyEntry);

    // Add to write queue for persistent storage
    this.writeQueue.push(historyEntry);

    // Emit event
    this.emit('queryAdded', historyEntry);

    // Check if we need to enforce size limits
    if (this.memoryCache.size > this.options.maxHistorySize) {
      await this.enforceHistoryLimit();
    }

    console.log(`[QueryHistoryManager] Added query ${entry.id} to history`);
  }

  /**
   * Update an existing query in the history
   */
  async updateQuery(queryId: string, updates: Partial<QueryHistoryEntry>): Promise<void> {
    if (!this.isInitialized) {
      throw new Error('QueryHistoryManager not initialized');
    }

    const existingEntry = this.memoryCache.get(queryId);
    if (!existingEntry) {
      console.warn(`[QueryHistoryManager] Query ${queryId} not found for update`);
      return;
    }

    const updatedEntry: QueryHistoryEntry = {
      ...existingEntry,
      ...updates,
    };

    // Calculate duration if endTime is provided in updates
    if (updates.endTime) {
      updatedEntry.duration = updates.endTime - existingEntry.startTime;
    }

    // Update memory cache
    this.memoryCache.set(queryId, updatedEntry);

    // Add to write queue
    this.writeQueue.push(updatedEntry);

    this.emit('queryUpdated', updatedEntry);
    console.log(`[QueryHistoryManager] Updated query ${queryId} in history`);
  }

  /**
   * Get query history with filtering and pagination
   */
  async getHistory(filter: QueryHistoryFilter = {}): Promise<{
    entries: QueryHistoryEntry[];
    total: number;
    hasMore: boolean;
  }> {
    if (!this.isInitialized) {
      throw new Error('QueryHistoryManager not initialized');
    }

    let entries = Array.from(this.memoryCache.values());

    // Apply filters
    if (filter.status && filter.status.length > 0) {
      entries = entries.filter(entry => filter.status!.includes(entry.status));
    }

    if (filter.startDate) {
      entries = entries.filter(entry => entry.startTime >= filter.startDate!.getTime());
    }

    if (filter.endDate) {
      entries = entries.filter(entry => entry.startTime <= filter.endDate!.getTime());
    }

    if (filter.cmd) {
      entries = entries.filter(entry => entry.cmd.includes(filter.cmd!));
    }

    if (filter.priority && filter.priority.length > 0) {
      entries = entries.filter(
        entry => entry.priority && filter.priority!.includes(entry.priority)
      );
    }

    if (filter.tags && filter.tags.length > 0) {
      entries = entries.filter(
        entry =>
          entry.metadata?.tags && filter.tags!.some(tag => entry.metadata!.tags!.includes(tag))
      );
    }

    // Sort entries
    const sortBy = filter.sortBy || 'startTime';
    const sortOrder = filter.sortOrder || 'desc';

    entries.sort((a, b) => {
      let aValue: unknown, bValue: unknown;

      switch (sortBy) {
        case 'startTime': {
          aValue = a.startTime;
          bValue = b.startTime;
          break;
        }
        case 'endTime': {
          aValue = a.endTime || 0;
          bValue = b.endTime || 0;
          break;
        }
        case 'duration': {
          aValue = a.duration || 0;
          bValue = b.duration || 0;
          break;
        }
        case 'cmd': {
          aValue = a.cmd;
          bValue = b.cmd;
          break;
        }
        default: {
          aValue = a.startTime;
          bValue = b.startTime;
        }
      }

      if (sortOrder === 'asc') {
        return aValue < bValue ? -1 : aValue > bValue ? 1 : 0;
      } else {
        return aValue > bValue ? -1 : aValue < bValue ? 1 : 0;
      }
    });

    const total = entries.length;
    const offset = filter.offset || 0;
    const limit = filter.limit || 100;

    // Apply pagination
    const paginatedEntries = entries.slice(offset, offset + limit);
    const hasMore = offset + limit < total;

    return {
      entries: paginatedEntries,
      total,
      hasMore,
    };
  }

  /**
   * Get a specific query by ID
   */
  async getQuery(queryId: string): Promise<QueryHistoryEntry | undefined> {
    if (!this.isInitialized) {
      throw new Error('QueryHistoryManager not initialized');
    }

    return this.memoryCache.get(queryId);
  }

  /**
   * Delete a query from history
   */
  async deleteQuery(queryId: string): Promise<boolean> {
    if (!this.isInitialized) {
      throw new Error('QueryHistoryManager not initialized');
    }

    const deleted = this.memoryCache.delete(queryId);
    if (deleted) {
      // Mark for deletion in persistent storage
      this.writeQueue.push({ id: queryId, deleted: true } as any);
      this.emit('queryDeleted', queryId);
      console.log(`[QueryHistoryManager] Deleted query ${queryId} from history`);
    }

    return deleted;
  }

  /**
   * Get comprehensive statistics about query history
   */
  async getStatistics(): Promise<QueryHistoryStats> {
    if (!this.isInitialized) {
      throw new Error('QueryHistoryManager not initialized');
    }

    const entries = Array.from(this.memoryCache.values());
    const completedEntries = entries.filter(e => e.status === 'completed' && e.duration);

    const stats: QueryHistoryStats = {
      totalQueries: entries.length,
      completedQueries: entries.filter(e => e.status === 'completed').length,
      errorQueries: entries.filter(e => e.status === 'error').length,
      cancelledQueries: entries.filter(e => e.status === 'cancelled').length,
      timeoutQueries: entries.filter(e => e.status === 'timeout').length,
      averageDuration:
        completedEntries.length > 0
          ? completedEntries.reduce((sum, e) => sum + (e.duration || 0), 0) /
            completedEntries.length
          : 0,
      totalDuration: completedEntries.reduce((sum, e) => sum + (e.duration || 0), 0),
      queriesByStatus: {},
      queriesByPriority: {},
      queriesByCmd: {},
      dailyStats: [],
    };

    // Count by status
    entries.forEach(entry => {
      stats.queriesByStatus[entry.status] = (stats.queriesByStatus[entry.status] || 0) + 1;
    });

    // Count by priority
    entries.forEach(entry => {
      if (entry.priority) {
        stats.queriesByPriority[entry.priority] =
          (stats.queriesByPriority[entry.priority] || 0) + 1;
      }
    });

    // Count by command
    entries.forEach(entry => {
      stats.queriesByCmd[entry.cmd] = (stats.queriesByCmd[entry.cmd] || 0) + 1;
    });

    // Generate daily stats for the last 30 days
    const now = new Date();
    for (let i = 29; i >= 0; i--) {
      const date = new Date(now);
      date.setDate(date.getDate() - i);
      const dateStr = date.toISOString().split('T')[0] || date.toISOString().substring(0, 10);

      const dayStart = new Date(date);
      dayStart.setHours(0, 0, 0, 0);
      const dayEnd = new Date(date);
      dayEnd.setHours(23, 59, 59, 999);

      const dayEntries = entries.filter(
        entry => entry.startTime >= dayStart.getTime() && entry.startTime <= dayEnd.getTime()
      );

      const dayCompletedEntries = dayEntries.filter(e => e.status === 'completed' && e.duration);

      stats.dailyStats.push({
        date: dateStr,
        count: dayEntries.length,
        avgDuration:
          dayCompletedEntries.length > 0
            ? dayCompletedEntries.reduce((sum, e) => sum + (e.duration || 0), 0) /
              dayCompletedEntries.length
            : 0,
      });
    }

    return stats;
  }

  /**
   * Get recent queries with limit
   */
  async getRecentQueries(limit: number = 10): Promise<
    Array<{
      query: string;
      success: boolean;
      timestamp: number;
      result?: unknown;
    }>
  > {
    if (!this.isInitialized) {
      throw new Error('QueryHistoryManager not initialized');
    }

    const entries = Array.from(this.memoryCache.values())
      .filter(entry => entry.status === 'completed' || entry.status === 'error')
      .sort((a, b) => b.startTime - a.startTime)
      .slice(0, limit);

    return entries.map(entry => ({
      query: entry.cmd,
      success: entry.status === 'completed',
      timestamp: entry.startTime,
      result: entry.results,
    }));
  }

  /**
   * Clear all history
   */
  async clearHistory(): Promise<void> {
    if (!this.isInitialized) {
      throw new Error('QueryHistoryManager not initialized');
    }

    this.memoryCache.clear();
    this.writeQueue = [];

    // Clear persistent storage
    try {
      if (fs.existsSync(this.historyFile)) {
        fs.unlinkSync(this.historyFile);
      }
      if (fs.existsSync(this.indexFile)) {
        fs.unlinkSync(this.indexFile);
      }
    } catch (error: unknown) {
      console.error('[QueryHistoryManager] Error clearing persistent storage:', error);
    }

    this.emit('historyCleared');
    console.log('[QueryHistoryManager] History cleared');
  }

  /**
   * Load history from disk into memory cache
   */
  private async loadHistoryFromDisk(): Promise<void> {
    if (!fs.existsSync(this.historyFile)) {
      return;
    }

    try {
      const data = fs.readFileSync(this.historyFile, 'utf8');
      const lines = data
        .trim()
        .split('\n')
        .filter(line => line.trim());

      for (const line of lines) {
        try {
          const entry: QueryHistoryEntry = JSON.parse(line);
          if (!entry.deleted) {
            this.memoryCache.set(entry.id, entry);
          }
        } catch (error: unknown) {
          console.warn('[QueryHistoryManager] Invalid history entry:', line);
        }
      }

      console.log(`[QueryHistoryManager] Loaded ${this.memoryCache.size} entries from disk`);
    } catch (error: unknown) {
      console.error('[QueryHistoryManager] Error loading history from disk:', error);
    }
  }

  /**
   * Start the background write processor
   */
  private startWriteProcessor(): void {
    this.writeInterval = setInterval(() => {
      this.processWriteQueue();
    }, 5000); // Write every 5 seconds
  }

  /**
   * Process the write queue to persist entries to disk
   */
  private async processWriteQueue(): Promise<void> {
    if (this.writeQueue.length === 0) {
      return;
    }

    const batch = this.writeQueue.splice(0, this.options.batchSize);

    try {
      const lines = batch.map(entry => JSON.stringify(entry)).join('\n') + '\n';
      fs.appendFileSync(this.historyFile, lines, 'utf8');

      console.log(`[QueryHistoryManager] Persisted ${batch.length} entries to disk`);
    } catch (error: unknown) {
      console.error('[QueryHistoryManager] Error writing to disk:', error);
      // Put entries back in queue for retry
      this.writeQueue.unshift(...batch);
    }
  }

  /**
   * Start the cleanup processor for old entries
   */
  private startCleanupProcessor(): void {
    this.cleanupInterval = setInterval(
      () => {
        this.cleanupOldEntries();
      },
      24 * 60 * 60 * 1000
    ); // Run daily
  }

  /**
   * Clean up old entries based on retention policy
   */
  private async cleanupOldEntries(): Promise<void> {
    const cutoffTime = Date.now() - this.options.retentionDays * 24 * 60 * 60 * 1000;
    let removedCount = 0;

    for (const [id, entry] of this.memoryCache.entries()) {
      if (entry.startTime < cutoffTime) {
        this.memoryCache.delete(id);
        removedCount++;
      }
    }

    if (removedCount > 0) {
      // Rewrite the history file without old entries
      await this.rewriteHistoryFile();
      this.emit('historyCleanedUp', { removedCount });
      console.log(`[QueryHistoryManager] Cleaned up ${removedCount} old entries`);
    }
  }

  /**
   * Enforce history size limits
   */
  private async enforceHistoryLimit(): Promise<void> {
    if (this.memoryCache.size <= this.options.maxHistorySize) {
      return;
    }

    // Remove oldest entries
    const entries = Array.from(this.memoryCache.entries()).sort(
      ([, a], [, b]) => a.startTime - b.startTime
    );

    const toRemove = entries.slice(0, this.memoryCache.size - this.options.maxHistorySize);

    for (const [id] of toRemove) {
      this.memoryCache.delete(id);
    }

    await this.rewriteHistoryFile();
    console.log(`[QueryHistoryManager] Enforced history limit, removed ${toRemove.length} entries`);
  }

  /**
   * Rewrite the entire history file (used for cleanup)
   */
  private async rewriteHistoryFile(): Promise<void> {
    try {
      const entries = Array.from(this.memoryCache.values());
      const lines = entries.map(entry => JSON.stringify(entry)).join('\n');

      fs.writeFileSync(this.historyFile, lines + '\n', 'utf8');
      console.log(`[QueryHistoryManager] Rewrote history file with ${entries.length} entries`);
    } catch (error: unknown) {
      console.error('[QueryHistoryManager] Error rewriting history file:', error);
    }
  }

  /**
   * Dispose of the history manager
   */
  dispose(): void {
    if (this.writeInterval) {
      clearInterval(this.writeInterval);
    }
    if (this.cleanupInterval) {
      clearInterval(this.cleanupInterval);
    }

    // Process any remaining writes
    this.processWriteQueue();

    this.removeAllListeners();
    console.log('[QueryHistoryManager] Disposed');
  }
}

import { EventEmitter } from 'events';
import { ConcurrencyManager, QueryPriority } from './concurrencyManager';
import { QueryHistoryManager } from './queryHistoryManager';

export interface ScheduledQuery {
  id: string;
  cmd: string;
  params: Record<string, unknown>;
  scheduleType: 'immediate' | 'delayed' | 'recurring' | 'conditional';
  scheduleConfig: {
    executeAt?: number; // timestamp for delayed execution
    interval?: number; // milliseconds for recurring queries
    maxExecutions?: number; // limit for recurring queries
    condition?: string; // condition expression for conditional execution
    dependencies?: string[]; // query IDs that must complete first
  };
  priority: QueryPriority;
  createdAt: number;
  lastExecutedAt?: number;
  executionCount: number;
  status: 'scheduled' | 'running' | 'completed' | 'failed' | 'cancelled' | 'paused';
  metadata?: {
    tags?: string[];
    description?: string;
    createdBy?: string;
  };
}

export interface SchedulerOptions {
  maxScheduledQueries: number;
  checkInterval: number;
  enableRecurring: boolean;
  enableConditional: boolean;
  enableDependencies: boolean;
  defaultPriority: QueryPriority;
}

export interface SchedulerStats {
  totalScheduled: number;
  activeScheduled: number;
  completedScheduled: number;
  failedScheduled: number;
  recurringQueries: number;
  conditionalQueries: number;
  dependentQueries: number;
  nextExecutionTime?: number;
}

/**
 * Advanced query scheduling and queuing system
 */
export class QueryScheduler extends EventEmitter {
  private options: SchedulerOptions;
  private concurrencyManager: ConcurrencyManager;
  private historyManager?: QueryHistoryManager;
  private scheduledQueries: Map<string, ScheduledQuery> = new Map();
  private schedulerInterval?: ReturnType<typeof setInterval>;
  private dependencyGraph: Map<string, Set<string>> = new Map(); // queryId -> dependents
  private conditionEvaluator: Map<string, () => boolean> = new Map();

  constructor(
    concurrencyManager: ConcurrencyManager,
    historyManager?: QueryHistoryManager,
    options: Partial<SchedulerOptions> = {}
  ) {
    super();

    this.concurrencyManager = concurrencyManager;
    this.historyManager = historyManager;

    this.options = {
      maxScheduledQueries: 1000,
      checkInterval: 1000, // Check every second
      enableRecurring: true,
      enableConditional: true,
      enableDependencies: true,
      defaultPriority: {
        level: 'normal',
        weight: 10,
        timeout: 30000,
      },
      ...options,
    };

    this.startScheduler();
    this.setupConcurrencyManagerListeners();
  }

  /**
   * Schedule a query for execution
   */
  async scheduleQuery(
    id: string,
    cmd: string,
    params: Record<string, unknown> = {},
    scheduleType: ScheduledQuery['scheduleType'] = 'immediate',
    scheduleConfig: ScheduledQuery['scheduleConfig'] = {},
    priority: Partial<QueryPriority> = {},
    metadata?: ScheduledQuery['metadata']
  ): Promise<void> {
    if (this.scheduledQueries.size >= this.options.maxScheduledQueries) {
      throw new Error('Maximum number of scheduled queries reached');
    }

    if (this.scheduledQueries.has(id)) {
      throw new Error(`Query with ID ${id} is already scheduled`);
    }

    const scheduledQuery: ScheduledQuery = {
      id,
      cmd,
      params,
      scheduleType,
      scheduleConfig,
      priority: { ...this.options.defaultPriority, ...priority },
      createdAt: Date.now(),
      executionCount: 0,
      status: 'scheduled',
      metadata,
    };

    // Validate schedule configuration
    this.validateScheduleConfig(scheduledQuery);

    // Set up dependencies if specified
    if (scheduleConfig.dependencies && this.options.enableDependencies) {
      this.setupDependencies(id, scheduleConfig.dependencies);
    }

    // Set up condition evaluator if specified
    if (scheduleConfig.condition && this.options.enableConditional) {
      this.setupConditionEvaluator(id, scheduleConfig.condition);
    }

    this.scheduledQueries.set(id, scheduledQuery);

    this.emit('queryScheduled', scheduledQuery);
    console.log(`[QueryScheduler] Scheduled query ${id} (type: ${scheduleType})`);

    // If it's an immediate query, try to execute it right away
    if (scheduleType === 'immediate') {
      await this.tryExecuteQuery(scheduledQuery);
    }
  }

  /**
   * Cancel a scheduled query
   */
  async cancelScheduledQuery(queryId: string): Promise<boolean> {
    const scheduledQuery = this.scheduledQueries.get(queryId);
    if (!scheduledQuery) {
      return false;
    }

    // If the query is currently running, cancel it in the concurrency manager
    if (scheduledQuery.status === 'running') {
      this.concurrencyManager.cancelQuery(queryId);
    }

    scheduledQuery.status = 'cancelled';
    this.scheduledQueries.delete(queryId);

    // Clean up dependencies
    this.cleanupDependencies(queryId);

    this.emit('queryScheduleCancelled', queryId);
    console.log(`[QueryScheduler] Cancelled scheduled query ${queryId}`);
    return true;
  }

  /**
   * Pause a recurring query
   */
  async pauseRecurringQuery(queryId: string): Promise<boolean> {
    const scheduledQuery = this.scheduledQueries.get(queryId);
    if (!scheduledQuery || scheduledQuery.scheduleType !== 'recurring') {
      return false;
    }

    scheduledQuery.status = 'paused';
    this.emit('querySchedulePaused', queryId);
    console.log(`[QueryScheduler] Paused recurring query ${queryId}`);
    return true;
  }

  /**
   * Resume a paused recurring query
   */
  async resumeRecurringQuery(queryId: string): Promise<boolean> {
    const scheduledQuery = this.scheduledQueries.get(queryId);
    if (!scheduledQuery || scheduledQuery.status !== 'paused') {
      return false;
    }

    scheduledQuery.status = 'scheduled';
    this.emit('queryScheduleResumed', queryId);
    console.log(`[QueryScheduler] Resumed recurring query ${queryId}`);
    return true;
  }

  /**
   * Get all scheduled queries with optional filtering
   */
  getScheduledQueries(filter?: {
    status?: ScheduledQuery['status'][];
    scheduleType?: ScheduledQuery['scheduleType'][];
    tags?: string[];
  }): ScheduledQuery[] {
    let queries = Array.from(this.scheduledQueries.values());

    if (filter?.status) {
      queries = queries.filter(q => filter.status!.includes(q.status));
    }

    if (filter?.scheduleType) {
      queries = queries.filter(q => filter.scheduleType!.includes(q.scheduleType));
    }

    if (filter?.tags) {
      queries = queries.filter(
        q => q.metadata?.tags && filter.tags!.some(tag => q.metadata!.tags!.includes(tag))
      );
    }

    return queries.sort((a, b) => a.createdAt - b.createdAt);
  }

  /**
   * Get scheduler statistics
   */
  getStatistics(): SchedulerStats {
    const queries = Array.from(this.scheduledQueries.values());

    const nextExecution = queries
      .filter(q => q.scheduleType === 'delayed' && q.scheduleConfig.executeAt)
      .map(q => q.scheduleConfig.executeAt!)
      .sort((a, b) => a - b)[0];

    return {
      totalScheduled: queries.length,
      activeScheduled: queries.filter(q => ['scheduled', 'running'].includes(q.status)).length,
      completedScheduled: queries.filter(q => q.status === 'completed').length,
      failedScheduled: queries.filter(q => q.status === 'failed').length,
      recurringQueries: queries.filter(q => q.scheduleType === 'recurring').length,
      conditionalQueries: queries.filter(q => q.scheduleType === 'conditional').length,
      dependentQueries: queries.filter(q => q.scheduleConfig.dependencies?.length).length,
      nextExecutionTime: nextExecution,
    };
  }

  /**
   * Register a custom condition evaluator
   */
  registerConditionEvaluator(queryId: string, evaluator: () => boolean): void {
    this.conditionEvaluator.set(queryId, evaluator);
  }

  /**
   * Validate schedule configuration
   */
  private validateScheduleConfig(query: ScheduledQuery): void {
    const { scheduleType, scheduleConfig } = query;

    switch (scheduleType) {
      case 'delayed': {
        if (!scheduleConfig.executeAt || scheduleConfig.executeAt <= Date.now()) {
          throw new Error('Delayed queries must have a future executeAt timestamp');
        }
        break;
      }
      case 'recurring': {
        if (!this.options.enableRecurring) {
          throw new Error('Recurring queries are disabled');
        }
        if (!scheduleConfig.interval || scheduleConfig.interval < 1000) {
          throw new Error('Recurring queries must have an interval of at least 1000ms');
        }
        break;
      }
      case 'conditional': {
        if (!this.options.enableConditional) {
          throw new Error('Conditional queries are disabled');
        }
        if (!scheduleConfig.condition) {
          throw new Error('Conditional queries must have a condition');
        }
        break;
      }
    }

    if (scheduleConfig.dependencies && !this.options.enableDependencies) {
      throw new Error('Query dependencies are disabled');
    }
  }

  /**
   * Set up query dependencies
   */
  private setupDependencies(queryId: string, dependencies: string[]): void {
    for (const depId of dependencies) {
      if (!this.dependencyGraph.has(depId)) {
        this.dependencyGraph.set(depId, new Set());
      }
      this.dependencyGraph.get(depId)!.add(queryId);
    }
  }

  /**
   * Clean up dependencies for a query
   */
  private cleanupDependencies(queryId: string): void {
    // Remove this query as a dependent of others
    for (const [depId, dependents] of this.dependencyGraph.entries()) {
      dependents.delete(queryId);
      if (dependents.size === 0) {
        this.dependencyGraph.delete(depId);
      }
    }

    // Remove this query's own dependencies
    this.dependencyGraph.delete(queryId);
  }

  /**
   * Set up condition evaluator
   */
  private setupConditionEvaluator(queryId: string, condition: string): void {
    // Simple condition evaluator - in a real implementation, this would be more sophisticated
    try {
      const evaluator = new Function('return ' + condition) as () => boolean;
      this.conditionEvaluator.set(queryId, evaluator);
    } catch (_error: unknown) {
      throw new Error(`Invalid condition expression: ${condition}`);
    }
  }

  /**
   * Start the scheduler loop
   */
  private startScheduler(): void {
    this.schedulerInterval = setInterval(() => {
      this.processScheduledQueries();
    }, this.options.checkInterval);
  }

  /**
   * Process all scheduled queries
   */
  private async processScheduledQueries(): Promise<void> {
    const now = Date.now();
    const queries = Array.from(this.scheduledQueries.values());

    for (const query of queries) {
      if (query.status !== 'scheduled') {
        continue;
      }

      try {
        if (await this.shouldExecuteQuery(query, now)) {
          await this.tryExecuteQuery(query);
        }
      } catch (error: unknown) {
        console.error(`[QueryScheduler] Error processing query ${query.id}:`, error);
        query.status = 'failed';
        this.emit('queryScheduleError', { queryId: query.id, error });
      }
    }

    // Clean up completed non-recurring queries
    this.cleanupCompletedQueries();
  }

  /**
   * Check if a query should be executed
   */
  private async shouldExecuteQuery(query: ScheduledQuery, now: number): Promise<boolean> {
    switch (query.scheduleType) {
      case 'immediate': {
        return true;
      }
      case 'delayed': {
        return query.scheduleConfig.executeAt! <= now;
      }
      case 'recurring': {
        if (query.status === 'paused') {
          return false;
        }

        if (!query.lastExecutedAt) {
          return true; // First execution
        }

        const timeSinceLastExecution = now - query.lastExecutedAt;
        const shouldExecute = timeSinceLastExecution >= query.scheduleConfig.interval!;

        // Check max executions limit
        if (shouldExecute && query.scheduleConfig.maxExecutions) {
          return query.executionCount < query.scheduleConfig.maxExecutions;
        }

        return shouldExecute;
      }
      case 'conditional': {
        const evaluator = this.conditionEvaluator.get(query.id);
        if (!evaluator) {
          return false;
        }

        try {
          return evaluator();
        } catch (error: unknown) {
          console.error(`[QueryScheduler] Condition evaluation error for ${query.id}:`, error);
          return false;
        }
      }
      default: {
        return false;
      }
    }
  }

  /**
   * Try to execute a scheduled query
   */
  private async tryExecuteQuery(query: ScheduledQuery): Promise<void> {
    // Check dependencies
    if (
      query.scheduleConfig.dependencies &&
      !this.areDependenciesSatisfied(query.scheduleConfig.dependencies)
    ) {
      return; // Dependencies not satisfied yet
    }

    try {
      query.status = 'running';
      query.lastExecutedAt = Date.now();
      query.executionCount++;

      this.emit('queryScheduleExecutionStarted', query);

      // Queue the query in the concurrency manager
      await this.concurrencyManager.queueQuery(query.id, query.cmd, query.params, query.priority);
    } catch (error: unknown) {
      query.status = 'failed';
      this.emit('queryScheduleExecutionFailed', { query, error });
      console.error(`[QueryScheduler] Failed to execute query ${query.id}:`, error);
    }
  }

  /**
   * Check if all dependencies are satisfied
   */
  private areDependenciesSatisfied(dependencies: string[]): boolean {
    return dependencies.every(depId => {
      const depQuery = this.scheduledQueries.get(depId);
      return depQuery && depQuery.status === 'completed';
    });
  }

  /**
   * Set up listeners for concurrency manager events
   */
  private setupConcurrencyManagerListeners(): void {
    this.concurrencyManager.on('queryCompleted', event => {
      this.handleQueryCompletion(event.queryId, event.success, event.error);
    });

    this.concurrencyManager.on('queryCancelled', event => {
      this.handleQueryCancellation(event.queryId);
    });
  }

  /**
   * Handle query completion from concurrency manager
   */
  private handleQueryCompletion(queryId: string, success: boolean, error?: string): void {
    const query = this.scheduledQueries.get(queryId);
    if (!query) {
      return;
    }

    if (success) {
      // Handle recurring queries
      if (query.scheduleType === 'recurring') {
        // Check if we've reached max executions
        if (
          query.scheduleConfig.maxExecutions &&
          query.executionCount >= query.scheduleConfig.maxExecutions
        ) {
          query.status = 'completed';
          this.emit('queryScheduleCompleted', query);
        } else {
          // Reset to scheduled for next execution
          query.status = 'scheduled';
          this.emit('queryScheduleRecurring', query);
        }
      } else {
        query.status = 'completed';
        this.emit('queryScheduleCompleted', query);
      }

      // Trigger dependent queries
      this.triggerDependentQueries(queryId);
    } else {
      query.status = 'failed';
      this.emit('queryScheduleFailed', { query, error });
    }

    // Add to history if history manager is available
    if (this.historyManager) {
      this.historyManager.addQuery({
        id: queryId,
        cmd: query.cmd,
        params: query.params,
        status: success ? 'completed' : 'error',
        startTime: query.lastExecutedAt!,
        endTime: Date.now(),
        error: error,
        priority: query.priority.level,
        metadata: {
          tags: query.metadata?.tags,
          sessionId: 'scheduler',
        },
      });
    }
  }

  /**
   * Handle query cancellation from concurrency manager
   */
  private handleQueryCancellation(queryId: string): void {
    const query = this.scheduledQueries.get(queryId);
    if (query) {
      query.status = 'cancelled';
      this.emit('queryScheduleCancelled', queryId);
    }
  }

  /**
   * Trigger queries that depend on the completed query
   */
  private triggerDependentQueries(completedQueryId: string): void {
    const dependents = this.dependencyGraph.get(completedQueryId);
    if (!dependents) {
      return;
    }

    for (const dependentId of dependents) {
      const dependentQuery = this.scheduledQueries.get(dependentId);
      if (dependentQuery && dependentQuery.status === 'scheduled') {
        // Check if all dependencies are now satisfied
        if (this.areDependenciesSatisfied(dependentQuery.scheduleConfig.dependencies || [])) {
          this.tryExecuteQuery(dependentQuery);
        }
      }
    }
  }

  /**
   * Clean up completed non-recurring queries
   */
  private cleanupCompletedQueries(): void {
    const toRemove: string[] = [];

    for (const [id, query] of this.scheduledQueries.entries()) {
      if (
        ['completed', 'failed', 'cancelled'].includes(query.status) &&
        query.scheduleType !== 'recurring'
      ) {
        // Keep completed queries for a while before cleanup
        const timeSinceCompletion = Date.now() - (query.lastExecutedAt || query.createdAt);
        if (timeSinceCompletion > 5 * 60 * 1000) {
          // 5 minutes
          toRemove.push(id);
        }
      }
    }

    for (const id of toRemove) {
      this.scheduledQueries.delete(id);
      this.cleanupDependencies(id);
      this.conditionEvaluator.delete(id);
    }

    if (toRemove.length > 0) {
      console.log(`[QueryScheduler] Cleaned up ${toRemove.length} completed queries`);
    }
  }

  /**
   * Dispose of the scheduler
   */
  dispose(): void {
    if (this.schedulerInterval) {
      clearInterval(this.schedulerInterval);
    }

    // Cancel all scheduled queries
    for (const [id] of this.scheduledQueries.entries()) {
      this.cancelScheduledQuery(id);
    }

    this.scheduledQueries.clear();
    this.dependencyGraph.clear();
    this.conditionEvaluator.clear();
    this.removeAllListeners();

    console.log('[QueryScheduler] Disposed');
  }
}

import { EventEmitter } from 'events';
import { spawn, ChildProcess } from 'child_process';
import * as path from 'path';
import * as fs from 'fs';

export interface SecurityConfig {
  sandboxing: {
    enabled: boolean;
    isolatedProcesses: boolean;
    fileSystemRestrictions: string[];
    networkAccess: boolean;
    dangerousPredicates: string[];
  };
  resourceLimits: {
    maxInferenceSteps: number;
    maxCallDepth: number;
    maxChoicePoints: number;
    maxMemoryPerQuery: number; // in MB
    maxExecutionTime: number; // in milliseconds
  };
  queryValidation: {
    syntaxChecking: boolean;
    predicateBlacklist: string[];
    maxQueryLength: number;
  };
}

export interface QuerySecurityContext {
  userId: string;
  role: string;
  permissions: string[];
  trusted: boolean;
  resourceQuota: ResourceQuota;
}

export interface ResourceQuota {
  maxConcurrentQueries: number;
  maxSessionsPerUser: number;
  maxQueryTime: number;
  maxMemoryPerQuery: number;
  maxResultsPerQuery: number;
  rateLimitPerMinute: number;
}

export interface SecurityViolation {
  type: 'dangerous_predicate' | 'resource_limit' | 'syntax_error' | 'permission_denied';
  message: string;
  query?: string;
  userId?: string;
  timestamp: Date;
  severity: 'low' | 'medium' | 'high' | 'critical';
}

/**
 * Security Manager for Prolog query execution
 * Handles sandboxing, resource quotas, and query validation
 */
export class SecurityManager extends EventEmitter {
  private config: SecurityConfig;
  private activeProcesses: Map<string, ChildProcess> = new Map();
  private userResourceUsage: Map<
    string,
    {
      activeQueries: number;
      activeSessions: number;
      lastActivity: Date;
      requestCount: number;
      requestWindow: Date;
    }
  > = new Map();

  constructor(config: SecurityConfig) {
    super();
    this.config = config;
    this.setupCleanupInterval();
  }

  /**
   * Validate a query before execution
   */
  async validateQuery(
    query: string,
    context: QuerySecurityContext
  ): Promise<{
    valid: boolean;
    violations: SecurityViolation[];
  }> {
    const violations: SecurityViolation[] = [];

    // Check query length
    if (query.length > this.config.queryValidation.maxQueryLength) {
      violations.push({
        type: 'syntax_error',
        message: `Query exceeds maximum length of ${this.config.queryValidation.maxQueryLength} characters`,
        query,
        userId: context.userId,
        timestamp: new Date(),
        severity: 'medium',
      });
    }

    // Check for dangerous predicates
    if (!context.trusted) {
      const dangerousPredicates = this.config.sandboxing.dangerousPredicates;
      for (const predicate of dangerousPredicates) {
        if (query.includes(predicate)) {
          violations.push({
            type: 'dangerous_predicate',
            message: `Query contains dangerous predicate: ${predicate}`,
            query,
            userId: context.userId,
            timestamp: new Date(),
            severity: 'high',
          });
        }
      }
    }

    // Check resource quotas
    const resourceViolations = await this.checkResourceQuotas(context);
    violations.push(...resourceViolations);

    // Syntax validation (if enabled)
    if (this.config.queryValidation.syntaxChecking) {
      const syntaxViolations = await this.validateSyntax(query);
      violations.push(...syntaxViolations);
    }

    // Emit security events
    for (const violation of violations) {
      this.emit('securityViolation', violation);
    }

    return {
      valid: violations.length === 0,
      violations,
    };
  }

  /**
   * Execute query in sandbox if required
   */
  async executeSecureQuery(
    query: string,
    context: QuerySecurityContext,
    originalExecutor: (query: string) => Promise<any>
  ): Promise<any> {
    // Validate query first
    const validation = await this.validateQuery(query, context);
    if (!validation.valid) {
      throw new Error(
        `Security validation failed: ${validation.violations.map(v => v.message).join(', ')}`
      );
    }

    // Update resource usage tracking
    this.updateResourceUsage(context.userId);

    // Execute in sandbox if required for untrusted users
    if (this.config.sandboxing.enabled && !context.trusted) {
      return await this.executeSandboxedQuery(query, context);
    } else {
      // Execute normally with resource limits
      return await this.executeWithResourceLimits(query, context, originalExecutor);
    }
  }

  /**
   * Execute query in isolated sandbox process
   */
  private async executeSandboxedQuery(query: string, context: QuerySecurityContext): Promise<any> {
    const queryId = `sandbox_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

    return new Promise((resolve, reject) => {
      // Create sandbox configuration
      const sandboxConfig = {
        query,
        resourceLimits: this.config.resourceLimits,
        allowedPredicates: this.getAllowedPredicates(context),
        timeout: context.resourceQuota.maxQueryTime,
      };

      // Spawn isolated Prolog process
      const sandboxProcess = spawn(
        'swipl',
        ['-g', 'halt', '--quiet', '--no-tty', '-t', 'halt(1)'],
        {
          stdio: ['pipe', 'pipe', 'pipe'],
          timeout: context.resourceQuota.maxQueryTime,
          env: {
            ...process.env,
            PROLOG_STACK_LIMIT: `${context.resourceQuota.maxMemoryPerQuery}M`,
            PROLOG_TABLE_SPACE: `${Math.floor(context.resourceQuota.maxMemoryPerQuery / 4)}M`,
          },
        }
      );

      this.activeProcesses.set(queryId, sandboxProcess);

      let output = '';
      let errorOutput = '';

      sandboxProcess.stdout?.on('data', data => {
        output += data.toString();
      });

      sandboxProcess.stderr?.on('data', data => {
        errorOutput += data.toString();
      });

      sandboxProcess.on('close', code => {
        this.activeProcesses.delete(queryId);

        if (code === 0) {
          try {
            const result = this.parseSandboxOutput(output);
            resolve(result);
          } catch (error) {
            reject(new Error(`Failed to parse sandbox output: ${error}`));
          }
        } else {
          reject(new Error(`Sandbox execution failed: ${errorOutput}`));
        }
      });

      sandboxProcess.on('error', error => {
        this.activeProcesses.delete(queryId);
        reject(new Error(`Sandbox process error: ${error.message}`));
      });

      // Send query to sandbox process
      const sandboxQuery = this.buildSandboxQuery(query, sandboxConfig);
      sandboxProcess.stdin?.write(sandboxQuery);
      sandboxProcess.stdin?.end();

      // Set timeout
      setTimeout(() => {
        if (this.activeProcesses.has(queryId)) {
          sandboxProcess.kill('SIGKILL');
          this.activeProcesses.delete(queryId);
          reject(new Error('Query execution timeout in sandbox'));
        }
      }, context.resourceQuota.maxQueryTime);
    });
  }

  /**
   * Execute query with resource limits but not in sandbox
   */
  private async executeWithResourceLimits(
    query: string,
    context: QuerySecurityContext,
    originalExecutor: (query: string) => Promise<any>
  ): Promise<any> {
    const startTime = Date.now();
    const queryId = `limited_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

    // Set up timeout
    const timeoutPromise = new Promise((_, reject) => {
      setTimeout(() => {
        reject(new Error(`Query execution timeout: ${context.resourceQuota.maxQueryTime}ms`));
      }, context.resourceQuota.maxQueryTime);
    });

    // Execute with resource monitoring
    const executionPromise = originalExecutor(query).then(result => {
      const executionTime = Date.now() - startTime;

      // Log resource usage
      this.emit('resourceUsage', {
        queryId,
        userId: context.userId,
        executionTime,
        memoryUsage: process.memoryUsage(),
        resultCount: Array.isArray(result.results) ? result.results.length : 0,
      });

      // Check result limits
      if (
        Array.isArray(result.results) &&
        result.results.length > context.resourceQuota.maxResultsPerQuery
      ) {
        result.results = result.results.slice(0, context.resourceQuota.maxResultsPerQuery);
        result.truncated = true;
        result.truncatedAt = context.resourceQuota.maxResultsPerQuery;
      }

      return result;
    });

    return Promise.race([executionPromise, timeoutPromise]);
  }

  /**
   * Check resource quotas for user
   */
  private async checkResourceQuotas(context: QuerySecurityContext): Promise<SecurityViolation[]> {
    const violations: SecurityViolation[] = [];
    const usage = this.getUserResourceUsage(context.userId || 'anonymous');
    const quota = context.resourceQuota;

    // Check concurrent queries
    if (usage.activeQueries >= quota.maxConcurrentQueries) {
      violations.push({
        type: 'resource_limit',
        message: `Maximum concurrent queries exceeded: ${usage.activeQueries}/${quota.maxConcurrentQueries}`,
        userId: context.userId,
        timestamp: new Date(),
        severity: 'medium',
      });
    }

    // Check session limit
    if (usage.activeSessions >= quota.maxSessionsPerUser) {
      violations.push({
        type: 'resource_limit',
        message: `Maximum sessions exceeded: ${usage.activeSessions}/${quota.maxSessionsPerUser}`,
        userId: context.userId,
        timestamp: new Date(),
        severity: 'medium',
      });
    }

    // Check rate limiting
    const now = new Date();
    const windowStart = new Date(now.getTime() - 60000); // 1 minute window

    if (usage.requestWindow < windowStart) {
      // Reset window
      usage.requestCount = 0;
      usage.requestWindow = now;
    }

    if (usage.requestCount >= quota.rateLimitPerMinute) {
      violations.push({
        type: 'resource_limit',
        message: `Rate limit exceeded: ${usage.requestCount}/${quota.rateLimitPerMinute} requests per minute`,
        userId: context.userId,
        timestamp: new Date(),
        severity: 'high',
      });
    }

    return violations;
  }

  /**
   * Validate Prolog syntax
   */
  private async validateSyntax(query: string): Promise<SecurityViolation[]> {
    const violations: SecurityViolation[] = [];

    try {
      // Basic syntax checks
      const balanced = this.checkBalancedParentheses(query);
      if (!balanced) {
        violations.push({
          type: 'syntax_error',
          message: 'Unbalanced parentheses in query',
          query,
          timestamp: new Date(),
          severity: 'medium',
        });
      }

      // Check for valid Prolog structure
      if (!this.isValidPrologStructure(query)) {
        violations.push({
          type: 'syntax_error',
          message: 'Invalid Prolog syntax structure',
          query,
          timestamp: new Date(),
          severity: 'medium',
        });
      }
    } catch (error) {
      violations.push({
        type: 'syntax_error',
        message: `Syntax validation error: ${error}`,
        query,
        timestamp: new Date(),
        severity: 'low',
      });
    }

    return violations;
  }

  /**
   * Get user resource usage, creating if not exists
   */
  private getUserResourceUsage(userId: string) {
    if (!this.userResourceUsage.has(userId)) {
      this.userResourceUsage.set(userId, {
        activeQueries: 0,
        activeSessions: 0,
        lastActivity: new Date(),
        requestCount: 0,
        requestWindow: new Date(),
      });
    }
    return this.userResourceUsage.get(userId)!;
  }

  /**
   * Update resource usage for user
   */
  private updateResourceUsage(userId: string): void {
    const usage = this.getUserResourceUsage(userId);
    usage.activeQueries++;
    usage.requestCount++;
    usage.lastActivity = new Date();
  }

  /**
   * Get allowed predicates for security context
   */
  private getAllowedPredicates(context: QuerySecurityContext): string[] {
    const basePredicate = [
      'member/2',
      'append/3',
      'length/2',
      'reverse/2',
      'sort/2',
      'findall/3',
      'bagof/3',
      'setof/3',
      'forall/2',
      'between/3',
      'succ/2',
      'plus/3',
      'is/2',
      '=/2',
      '\\=/2',
      '==/2',
      '\\==/2',
      '@</2',
      '@>/2',
      'var/1',
      'nonvar/1',
      'atom/1',
      'number/1',
      'compound/1',
      'functor/3',
      'arg/3',
      '=../2',
      'copy_term/2',
    ];

    if (context.role === 'admin') {
      return ['*']; // Admin can use any predicate
    }

    if (context.trusted) {
      return [...basePredicate, 'assert/1', 'retract/1', 'retractall/1'];
    }

    return basePredicate;
  }

  /**
   * Build sandbox query with security constraints
   */
  private buildSandboxQuery(query: string, config: any): string {
    return `
      % Set resource limits
      set_prolog_flag(stack_limit, ${config.resourceLimits.maxMemoryPerQuery * 1024 * 1024}).
      set_prolog_flag(table_space, ${Math.floor(config.resourceLimits.maxMemoryPerQuery / 4) * 1024 * 1024}).

      % Disable dangerous predicates
      :- abolish(shell/1).
      :- abolish(system/1).
      :- abolish(halt/0).
      :- abolish(halt/1).
      :- abolish(abort/0).

      % Execute query with error handling
      sandbox_query :-
        catch(
          (${query}),
          Error,
          (write('ERROR: '), write(Error), nl)
        ).

      % Run query and output results
      :- sandbox_query.
    `;
  }

  /**
   * Parse output from sandbox execution
   */
  private parseSandboxOutput(output: string): any {
    try {
      // Simple parsing - in production, this would be more sophisticated
      const lines = output.trim().split('\n');
      const results = [];

      for (const line of lines) {
        if (line.startsWith('ERROR:')) {
          throw new Error(line.substring(6).trim());
        }
        if (line.trim() && !line.startsWith('%')) {
          results.push({ solution: line.trim() });
        }
      }

      return {
        status: 'ok',
        results,
        sandboxed: true,
      };
    } catch (error) {
      throw new Error(`Failed to parse sandbox output: ${error}`);
    }
  }

  /**
   * Check if parentheses are balanced
   */
  private checkBalancedParentheses(query: string): boolean {
    let count = 0;
    for (const char of query) {
      if (char === '(') count++;
      if (char === ')') count--;
      if (count < 0) return false;
    }
    return count === 0;
  }

  /**
   * Basic Prolog structure validation
   */
  private isValidPrologStructure(query: string): boolean {
    // Very basic validation - in production, use proper Prolog parser
    const trimmed = query.trim();
    if (!trimmed) return false;

    // Check for basic Prolog patterns
    const prologPattern =
      /^[a-zA-Z_][a-zA-Z0-9_]*(\([^)]*\))?(,\s*[a-zA-Z_][a-zA-Z0-9_]*(\([^)]*\))?)*\.?$/;
    return prologPattern.test(trimmed.replace(/\s+/g, ' '));
  }

  /**
   * Clean up inactive processes and old usage data
   */
  private setupCleanupInterval(): void {
    setInterval(() => {
      const now = new Date();
      const maxAge = 5 * 60 * 1000; // 5 minutes

      // Clean up old resource usage data
      for (const [userId, usage] of this.userResourceUsage.entries()) {
        if (now.getTime() - usage.lastActivity.getTime() > maxAge) {
          this.userResourceUsage.delete(userId);
        }
      }

      // Clean up zombie processes
      for (const [queryId, process] of this.activeProcesses.entries()) {
        if (!process.killed && process.exitCode === null) {
          const age = now.getTime() - parseInt(queryId.split('_')[1]);
          if (age > maxAge) {
            process.kill('SIGTERM');
            this.activeProcesses.delete(queryId);
          }
        }
      }
    }, 60000); // Run every minute
  }

  /**
   * Get security statistics
   */
  getSecurityStatistics(): {
    activeProcesses: number;
    trackedUsers: number;
    totalViolations: number;
    resourceUsage: Map<string, any>;
  } {
    return {
      activeProcesses: this.activeProcesses.size,
      trackedUsers: this.userResourceUsage.size,
      totalViolations: this.listenerCount('securityViolation'),
      resourceUsage: this.userResourceUsage,
    };
  }

  /**
   * Kill all active sandbox processes
   */
  async shutdown(): Promise<void> {
    for (const [queryId, process] of this.activeProcesses.entries()) {
      if (!process.killed) {
        process.kill('SIGTERM');
      }
    }
    this.activeProcesses.clear();
    this.userResourceUsage.clear();
  }
}

