import { TelemetryCollector, TelemetryData } from './telemetryCollector';

export class ChatHandler {
  private prologBackend: PrologBackend | null = null;
  private telemetry: TelemetryCollector;

  constructor(prologBackend: PrologBackend | null, telemetry: TelemetryCollector) {
    this.prologBackend = prologBackend;
    this.telemetry = telemetry;
  }

  // Update backend reference
  updateBackend(backend: PrologBackend | null) {
    this.prologBackend = backend;
  }

  // Chat participant handler
  async handleChatRequest(
    request: ChatRequest,
    context: any, // ChatContext type not available in current vscode API
    stream: ChatResponseStream,
    _token: CancellationToken
  ): Promise<ChatResult> {
    const startTime = Date.now();
    let command = 'unknown';

    try {
      // Ensure backend is running
      if (!this.prologBackend) {
        stream.markdown(
          ' **Backend Error**: Prolog backend not initialized. Please restart VS Code.'
        );
        this.telemetry.collect({
          command: 'init_error',
          success: false,
          error: 'backend_not_initialized',
          timestamp: startTime,
        });
        return { metadata: { command: 'error' } };
      }

      if (!this.prologBackend.isRunning()) {
        stream.markdown(' **Starting Prolog backend...**\n\n');
        await this.startBackendWithProgress(stream);
      }

      // Parse command from request
      const message = request.prompt.trim();
      const parts = message.split(/\s+/);
      command = parts[0]?.toLowerCase() || 'unknown';

      // Handle cancellation
      if (_token.isCancellationRequested) {
        stream.markdown(' **Request cancelled**');
        this.telemetry.collect({ command, success: false, error: 'cancelled', timestamp: startTime });
        return { metadata: { command: 'cancelled' } };
      }

      // Route to appropriate handler
      switch (command) {
        case '/query':
        case 'query':
          await this.handleQueryCommand(parts.slice(1).join(' '), stream, _token);
          break;
        case '/consult':
        case 'consult':
          await this.handleConsultCommand(parts.slice(1).join(' '), stream, _token);
          break;
        case '/help':
        case 'help':
          await this.handleHelpCommand(parts.slice(1).join(' '), stream, _token);
          break;
        case '/status':
        case 'status':
          await this.handleStatusCommand(stream);
          break;
        case '/n3_load':
        case 'n3_load':
          await this.handleN3LoadCommand(parts.slice(1).join(' '), stream, _token);
          break;
        case '/n3_list':
        case 'n3_list':
          await this.handleN3ListCommand(parts.slice(1).join(' '), stream, _token);
          break;
        case '/n3_reason':
        case 'n3_reason':
          await this.handleN3ReasonCommand(parts.slice(1).join(' '), stream, _token);
          break;
        case '/n3_explain':
        case 'n3_explain':
          await this.handleN3ExplainCommand(parts.slice(1).join(' '), stream, _token);
          break;
        default:
          // Treat as a general query if no command prefix
          if (message.trim()) {
            await this.handleQueryCommand(message, stream, _token);
          } else {
            stream.markdown(this.getHelpMessage());
          }
          break;
      }

      this.telemetry.collect({ command, success: true, timestamp: startTime });
      return { metadata: { command } };
    } catch (error) {
      const errorMsg = error instanceof Error ? error.message : String(error);
      stream.markdown(` **Error**: ${errorMsg}`);
      this.telemetry.collect({ command, success: false, error: errorMsg, timestamp: startTime });
      return { metadata: { command: 'error' } };
    }
  }

  // Start backend with progress indication
  private async startBackendWithProgress(stream: ChatResponseStream): Promise<void> {
    return new Promise((resolve, reject) => {
      if (!this.prologBackend) {
        reject(new Error('Backend not initialized'));
        return;
      }

      const timeout = setTimeout(() => {
        reject(new Error('Backend startup timeout (30s)'));
      }, 30000);

      const onReady = () => {
        clearTimeout(timeout);
        this.prologBackend?.off('ready', onReady);
        this.prologBackend?.off('error', onError);
        stream.markdown(' **Prolog backend ready**\n\n');
        resolve();
      };

      const onError = (error: any) => {
        clearTimeout(timeout);
        this.prologBackend?.off('ready', onReady);
        this.prologBackend?.off('error', onError);
        reject(new Error(`Backend startup failed: ${error}`));
      };

      this.prologBackend.on('ready', onReady);
      this.prologBackend.on('error', onError);
      this.prologBackend.start();
    });
  }

  // Handle query commands with retry logic and progress indicators
  private async handleQueryCommand(
    query: string,
    stream: ChatResponseStream,
    _token: CancellationToken
  ) {
    if (!query.trim()) {
      stream.markdown(' **Error**: Please provide a query. Example: `/query member(X, [1,2,3])`');
      return;
    }

    stream.markdown(` **Querying**: \`${query}\`\n\n`);

    const maxRetries = 3;
    let lastError: any = null;

    for (let attempt = 1; attempt <= maxRetries; attempt++) {
      if (_token.isCancellationRequested) {
        stream.markdown(' **Query cancelled**');
        return;
      }

      try {
        if (!this.prologBackend?.isRunning()) {
          throw new Error('Backend not running');
        }

        // Show progress for potentially long-running queries
        stream.progress(' Executing query...');

        // Use streaming request for better performance with large result sets
        const response = await this.prologBackend.sendStreamingRequest(
          'query',
          {
            goal: query,
            timeoutMs: 30000, // Increased timeout for large queries
            streaming: true,
            max_results_per_chunk: 50,
          },
          (chunk, isFirst, isLast) => {
            // Handle streaming chunks
            if (isFirst) {
              stream.progress(' Processing results...');
            }
            if (!isLast) {
              stream.progress(` Processing chunk ${chunk.chunk_info?.chunk_index + 1}...`);
            }
          }
        );

        if (response.status === 'ok') {
          if (response.results) {
            // Check if this is a large result set
            const isLargeResult =
              response.streaming_info?.is_large_result || response.total_chunks > 1;

            if (isLargeResult) {
              const totalResults = response.streaming_info?.total_count || response.results.length;
              const chunkSize = response.streaming_info?.chunk_size || response.results.length;

              stream.markdown(
                ` **Large Result Set** (${totalResults} total results, showing first ${chunkSize}):\n\n`
              );
              stream.markdown(
                '* Tip: Results are automatically chunked for better performance. Use pagination commands for more results.*\n\n'
              );
            } else {
              stream.markdown(' **Results**:\n\n');
            }

            this.formatQueryResults(response.results, stream, response.streaming_info);

            // Show pagination info if available
            if (response.streaming_info?.has_more) {
              stream.markdown(
                `\n **Pagination**: Showing ${response.streaming_info.chunk_size} of ${response.streaming_info.total_count} results`
              );
              stream.markdown(
                '\n*Use `/query_next` to see more results or increase chunk size with `/query --chunk-size 100 your_query`*'
              );
            }
          } else if (response.output) {
            stream.markdown(' **Output**:\n\n');
            stream.markdown(`\`\`\`\n${response.output}\n\`\`\``);
          } else {
            stream.markdown(' **Query succeeded** (no output)');
          }
          return;
        } else {
          throw new Error(response.error || 'Query failed');
        }
      } catch (error) {
        lastError = error;
        const errorMsg = error instanceof Error ? error.message : String(error);

        if (attempt < maxRetries && !_token.isCancellationRequested) {
          stream.markdown(` **Attempt ${attempt} failed**: ${errorMsg}. Retrying...\n\n`);
          stream.progress(` Retrying (${attempt + 1}/${maxRetries})...`);
          await new Promise(resolve => setTimeout(resolve, 1000 * attempt)); // Exponential backoff
        }
      }
    }

    // All retries failed
    const errorMsg = lastError instanceof Error ? lastError.message : String(lastError);
    stream.markdown(` **Query failed after ${maxRetries} attempts**: ${errorMsg}`);

    // Offer fallback
    stream.markdown(
      '\n **Fallback**: You can try running the query directly in a Prolog terminal.'
    );
  }

  // Handle consult commands
  private async handleConsultCommand(
    filePath: string,
    stream: ChatResponseStream,
    __token: CancellationToken
  ) {
    if (!filePath.trim()) {
      stream.markdown(' **Error**: Please provide a file path. Example: `/consult myfile.pl`');
      return;
    }

    stream.markdown(` **Consulting**: \`${filePath}\`\n\n`);

    try {
      if (!this.prologBackend?.isRunning()) {
        throw new Error('Backend not running');
      }

      const response = await this.prologBackend.sendRequest('consult', {
        file: filePath,
        timeoutMs: 15000,
      });

      if (response.status === 'ok') {
        stream.markdown(` **File consulted successfully**: \`${filePath}\``);
      } else {
        throw new Error(response.error || 'Consult failed');
      }
    } catch (error) {
      const errorMsg = error instanceof Error ? error.message : String(error);
      stream.markdown(` **Consult failed**: ${errorMsg}`);

      // Offer fallback
      stream.markdown(
        '\n **Fallback**: You can try loading the file using the "Prolog: load document" command.'
      );
    }
  }

  // Handle help commands
  private async handleHelpCommand(
    predicate: string,
    stream: ChatResponseStream,
    __token: CancellationToken
  ) {
    if (!predicate.trim()) {
      stream.markdown(this.getHelpMessage());
      return;
    }

    stream.markdown(` **Getting help for**: \`${predicate}\`\n\n`);

    try {
      if (!this.prologBackend?.isRunning()) {
        throw new Error('Backend not running');
      }

      const response = await this.prologBackend.sendRequest('help', {
        predicate: predicate,
        timeoutMs: 5000,
      });

      if (response.status === 'ok' && response.doc) {
        this.formatHelpResponse(response.doc, stream);
      } else {
        throw new Error(response.error || 'Help not found');
      }
    } catch (error) {
      const errorMsg = error instanceof Error ? error.message : String(error);
      stream.markdown(` **Help lookup failed**: ${errorMsg}`);
      stream.markdown(
        `\n **Fallback**: Try searching the SWI-Prolog documentation online for \`${predicate}\`.`
      );
    }
  }

  // Handle status command
  private async handleStatusCommand(stream: ChatResponseStream) {
    stream.markdown(' **Prolog Extension Status**\n\n');

    if (this.prologBackend) {
      const isRunning = this.prologBackend.isRunning();
      stream.markdown(`- **Backend**: ${isRunning ? ' Running' : ' Stopped'}\n`);

      if (isRunning) {
        try {
          const response = await this.prologBackend.sendRequest('version', { timeoutMs: 2000 });
          if (response.status === 'ok') {
            stream.markdown(`- **SWI-Prolog Version**: ${response.version}\n`);
          }
        } catch (error) {
          const errorMsg = error instanceof Error ? error.message : String(error);
          console.warn('[Extension] Version check failed:', errorMsg);
          stream.markdown(`- **Version Check**:  Failed (${errorMsg})\n`);
        }
      }
    } else {
      stream.markdown('- **Backend**:  Not initialized\n');
    }

    const config = workspace.getConfiguration('prolog');
    stream.markdown(`- **Executable Path**: \`${config.get('executablePath', 'swipl')}\`\n`);
    stream.markdown(`- **Dialect**: ${config.get('dialect', 'swi')}\n`);

    const stats = this.telemetry.getStats();
    if (stats) {
      stream.markdown(`- **Commands Used**: ${stats.totalCommands}\n`);
    }
  }

  // Handle N3 load commands
  private async handleN3LoadCommand(
    args: string,
    stream: ChatResponseStream,
    __token: CancellationToken
  ) {
    const parts = args.trim().split(/\s+/);

    if (parts.length === 0 || !parts[0]) {
      stream.markdown(
        ' **Error**: Please provide a file path or N3 content. Examples:\n- `/n3_load sample.n3`\n- `/n3_load --content "@prefix : <http://example.org/> . :socrates a :Person ."`'
      );
      return;
    }

    try {
      if (!this.prologBackend?.isRunning()) {
        throw new Error('Backend not running');
      }

      let requestParams: any;

      if (parts[0] === '--content') {
        const content = parts.slice(1).join(' ');
        stream.markdown(` **Loading N3 content**...\n\n`);
        requestParams = { content, timeoutMs: 15000 };
      } else {
        const filePath = parts[0];
        stream.markdown(` **Loading N3 file**: \`${filePath}\`\n\n`);
        requestParams = { file: filePath, timeoutMs: 15000 };
      }

      const response = await this.prologBackend.sendRequest('n3_load', requestParams);

      if (response.status === 'ok') {
        stream.markdown(
          ` **N3 data loaded successfully**\n- **Triples loaded**: ${response.triples_count}\n`
        );
      } else {
        throw new Error(response.error || 'N3 load failed');
      }
    } catch (error) {
      const errorMsg = error instanceof Error ? error.message : String(error);
      stream.markdown(` **N3 load failed**: ${errorMsg}`);
    }
  }

  // Handle N3 list commands with pagination
  private async handleN3ListCommand(
    args: string,
    stream: ChatResponseStream,
    __token: CancellationToken
  ) {
    try {
      if (!this.prologBackend?.isRunning()) {
        throw new Error('Backend not running');
      }

      const parts = args.trim().split(/\s+/);
      let limit = 50;
      let offset = 0;
      let format = 'readable';

      // Parse arguments
      for (let i = 0; i < parts.length; i++) {
        if (parts[i] === '--limit' && i + 1 < parts.length) {
          const nextPart = parts[i + 1];
          limit = nextPart ? parseInt(nextPart) || 50 : 50;
        } else if (parts[i] === '--offset' && i + 1 < parts.length) {
          const nextPart = parts[i + 1];
          offset = nextPart ? parseInt(nextPart) || 0 : 0;
        } else if (parts[i] === '--format' && i + 1 < parts.length) {
          const nextPart = parts[i + 1];
          format = nextPart || 'readable';
        }
      }

      stream.markdown(
        ` **Listing N3 triples** (limit: ${limit}, offset: ${offset}, format: ${format})...\n\n`
      );
      stream.progress(' Fetching triples...');

      const response = await this.prologBackend.sendRequest('n3_list', {
        limit,
        offset,
        format,
        streaming: true,
        timeoutMs: 15000,
      });

      if (response.status === 'ok') {
        // Handle paginated response
        if (response.pagination) {
          const { total_count, returned_count, has_more, next_offset } = response.pagination;
          stream.markdown(
            ` **Found ${total_count} triples** (showing ${returned_count}, offset: ${offset}):\n\n`
          );

          if (response.triples && response.triples.length > 0) {
            // Use compact format for large result sets
            if (returned_count > 20) {
              stream.markdown('```\n');
              response.triples.forEach((triple: any, index: number) => {
                stream.markdown(
                  `${offset + index + 1}. ${triple.subject} ${triple.predicate} ${triple.object}\n`
                );
              });
              stream.markdown('```\n');
            } else {
              stream.markdown(
                '| # | Subject | Predicate | Object |\n|---|---------|-----------|--------|\n'
              );
              response.triples.forEach((triple: any, index: number) => {
                stream.markdown(
                  `| ${offset + index + 1} | \`${triple.subject}\` | \`${triple.predicate}\` | \`${triple.object}\` |\n`
                );
              });
            }

            // Show pagination controls
            if (has_more) {
              stream.markdown(
                `\n **Pagination**: Showing ${offset + 1}-${offset + returned_count} of ${total_count} triples`
              );
              stream.markdown(
                `\n*Use \`/n3_list --offset ${next_offset} --limit ${limit}\` for next page*`
              );
            }
          } else {
            stream.markdown('*No triples found.*');
          }
        } else {
          // Legacy response format
          const total_count = response.total_count || 0;
          const returned_count = response.returned_count || 0;
          stream.markdown(` **Found ${total_count} triples** (showing ${returned_count}):\n\n`);

          if (response.triples && response.triples.length > 0) {
            stream.markdown('| Subject | Predicate | Object |\n|---------|-----------|--------|\n');
            response.triples.forEach((triple: any) => {
              stream.markdown(
                `| \`${triple.subject}\` | \`${triple.predicate}\` | \`${triple.object}\` |\n`
              );
            });
          } else {
            stream.markdown('*No triples found.*');
          }
        }
      } else {
        throw new Error(response.error || 'N3 list failed');
      }
    } catch (error) {
      const errorMsg = error instanceof Error ? error.message : String(error);
      stream.markdown(` **N3 list failed**: ${errorMsg}`);
    }
  }

  // Handle N3 reason commands
  private async handleN3ReasonCommand(
    args: string,
    stream: ChatResponseStream,
    __token: CancellationToken
  ) {
    try {
      if (!this.prologBackend?.isRunning()) {
        throw new Error('Backend not running');
      }

      const goal = args.trim();

      if (goal) {
        stream.markdown(` **N3 reasoning with goal**: \`${goal}\`\n\n`);
      } else {
        stream.markdown(` **N3 reasoning** (finding all inferred triples)...\n\n`);
      }

      const response = await this.prologBackend.sendRequest('n3_reason', {
        goal: goal || '',
        timeoutMs: 15000,
      });

      if (response.status === 'ok') {
        if (response.results) {
          stream.markdown(` **Reasoning results** (${response.count} found):\n\n`);
          response.results.forEach((result: any, index: number) => {
            stream.markdown(`**Result ${index + 1}**: \`${JSON.stringify(result)}\`\n`);
          });
        } else if (response.inferred_triples) {
          stream.markdown(` **Inferred triples** (${response.count} found):\n\n`);
          if (response.inferred_triples.length > 0) {
            stream.markdown('| Subject | Predicate | Object |\n|---------|-----------|--------|\n');
            response.inferred_triples.forEach((triple: any) => {
              stream.markdown(
                `| \`${triple.subject}\` | \`${triple.predicate}\` | \`${triple.object}\` |\n`
              );
            });
          } else {
            stream.markdown('*No new triples inferred.*');
          }
        }
      } else {
        throw new Error(response.error || 'N3 reasoning failed');
      }
    } catch (error) {
      const errorMsg = error instanceof Error ? error.message : String(error);
      stream.markdown(` **N3 reasoning failed**: ${errorMsg}`);
    }
  }

  // Handle N3 explain commands
  private async handleN3ExplainCommand(
    args: string,
    stream: ChatResponseStream,
    __token: CancellationToken
  ) {
    const goal = args.trim();

    if (!goal) {
      stream.markdown(
        ' **Error**: Please provide a goal to explain. Example: `/n3_explain rdf(socrates, type, Mortal)`'
      );
      return;
    }

    try {
      if (!this.prologBackend?.isRunning()) {
        throw new Error('Backend not running');
      }

      stream.markdown(` **Explaining goal**: \`${goal}\`\n\n`);

      const response = await this.prologBackend.sendRequest('n3_explain', {
        goal,
        timeoutMs: 15000,
      });

      if (response.status === 'ok') {
        stream.markdown(` **Proof explanation for**: \`${response.goal}\`\n\n`);
        this.formatProofTree(response.proof, stream, 0);
      } else {
        throw new Error(response.error || 'N3 explanation failed');
      }
    } catch (error) {
      const errorMsg = error instanceof Error ? error.message : String(error);
      stream.markdown(` **N3 explanation failed**: ${errorMsg}`);
    }
  }

  // Format query results with streaming info
  private formatQueryResults(results: any[], stream: ChatResponseStream, streamingInfo?: any) {
    if (results.length === 0) {
      stream.markdown(' *No results found.*');
      return;
    }

    if (results.length === 1 && Array.isArray(results[0]) && results[0].length === 0) {
      stream.markdown(' **true** (goal succeeded with no variable bindings)');
      return;
    }

    // Count total bindings for better presentation
    let totalBindings = 0;
    results.forEach(result => {
      if (Array.isArray(result)) {
        totalBindings += result.length;
      } else if (typeof result === 'object' && result !== null) {
        totalBindings += Object.keys(result).length;
      }
    });

    const displayCount = results.length;
    const totalCount = streamingInfo?.total_count || displayCount;

    if (streamingInfo?.is_large_result) {
      stream.markdown(
        ` **Showing ${displayCount} of ${totalCount} solution${totalCount > 1 ? 's' : ''}** with ${totalBindings} variable binding${totalBindings > 1 ? 's' : ''}:\n\n`
      );
    } else {
      stream.markdown(
        ` **Found ${displayCount} solution${displayCount > 1 ? 's' : ''}** with ${totalBindings} variable binding${totalBindings > 1 ? 's' : ''}:\n\n`
      );
    }

    // Use more compact format for large result sets
    const useCompactFormat = displayCount > 20 || streamingInfo?.is_large_result;

    if (useCompactFormat) {
      // Compact format for large results
      stream.markdown('```\n');
      results.forEach((result, solutionIndex) => {
        stream.markdown(`Solution ${solutionIndex + 1}:\n`);
        if (Array.isArray(result) && result.length > 0) {
          result.forEach((binding: any) => {
            if (typeof binding === 'object' && binding !== null) {
              Object.entries(binding).forEach(([variable, value]) => {
                stream.markdown(`  ${variable} = ${value}\n`);
              });
            }
          });
        } else if (typeof result === 'object' && result !== null) {
          Object.entries(result).forEach(([variable, value]) => {
            stream.markdown(`  ${variable} = ${value}\n`);
          });
        }
        stream.markdown('\n');
      });
      stream.markdown('```\n');
    } else {
      // Table format for smaller results
      stream.markdown('| Variable | Value | Solution |\n|----------|-------|----------|\n');

      results.forEach((result, solutionIndex) => {
        if (Array.isArray(result) && result.length > 0) {
          result.forEach((binding: any) => {
            if (typeof binding === 'object' && binding !== null) {
              Object.entries(binding).forEach(([variable, value]) => {
                stream.markdown(`| **${variable}** | \`${value}\` | ${solutionIndex + 1} |\n`);
              });
            }
          });
        } else if (typeof result === 'object' && result !== null) {
          Object.entries(result).forEach(([variable, value]) => {
            stream.markdown(`| **${variable}** | \`${value}\` | ${solutionIndex + 1} |\n`);
          });
        }
      });
    }
  }

  // Format help response (simplified version without problematic Unicode)
  private formatHelpResponse(doc: any, stream: ChatResponseStream) {
    stream.markdown(`# ${doc.name}/${doc.arity}\n\n`);

    // Status badges
    const badges = [];
    if (doc.is_builtin) badges.push('**Built-in**');
    if (doc.module && doc.module !== 'user') badges.push(`**Module**: \`${doc.module}\``);
    if (doc.deterministic) badges.push('**Deterministic**');

    if (badges.length > 0) {
      stream.markdown(`${badges.join(' | ')}\n\n`);
    }

    if (doc.summary) {
      stream.markdown(`## Description\n\n${doc.summary}\n\n`);
    }

    if (doc.args && doc.args.length > 0) {
      stream.markdown('## Arguments\n\n');
      doc.args.forEach((arg: any, index: number) => {
        stream.markdown(`**${index + 1}. ${arg.name}** - ${arg.description}\n\n`);
      });
    }

    if (doc.examples && doc.examples.length > 0) {
      stream.markdown('## Examples\n\n');
      doc.examples.forEach((example: string, index: number) => {
        stream.markdown(`**Example ${index + 1}:**\n\`\`\`prolog\n${example}\n\`\`\`\n\n`);
      });
    }

    if (doc.see_also && doc.see_also.length > 0) {
      stream.markdown('## See Also\n\n');
      doc.see_also.forEach((related: string) => {
        stream.markdown(`- \`${related}\`\n`);
      });
      stream.markdown('\n');
    }
  }

  // Format proof tree for display
  private formatProofTree(proof: any, stream: ChatResponseStream, depth: number) {
    const indent = '  '.repeat(depth);

    if (proof.type === 'proof') {
      stream.markdown(`${indent} **Goal**: \`${proof.goal}\`\n`);
      if (proof.subproofs && proof.subproofs.length > 0) {
        proof.subproofs.forEach((subproof: any) => {
          this.formatProofTree(subproof, stream, depth + 1);
        });
      }
    } else if (proof.type === 'fact') {
      stream.markdown(`${indent} **Fact**: \`${proof.fact}\`\n`);
    } else if (proof.type === 'inference') {
      stream.markdown(`${indent} **Inference**: \`${proof.inference}\`\n`);
    } else if (proof.type === 'builtin') {
      stream.markdown(`${indent} **Builtin**: \`${proof.builtin}\`\n`);
    }
  }

  // Get help message
  private getHelpMessage(): string {
    return `#  Prolog Assistant

Welcome! I'm your intelligent Prolog companion, ready to help with queries, file consultation, documentation lookup, and advanced N3 semantic reasoning.

---

##  **Core Commands**

### **\`/query <goal>\`**
Execute Prolog queries with intelligent result formatting
- **Example**: \`/query member(X, [1,2,3])\`
- **Example**: \`/query append([1,2], [3,4], Result)\`
- **Tip**: You can omit \`/query\` for simple queries

### **\`/consult <file>\`**
Load and consult Prolog files into the knowledge base
- **Example**: \`/consult family.pl\`
- **Example**: \`/consult ./src/rules.pl\`

### **\`/help <predicate>\`**
Get comprehensive documentation for predicates
- **Example**: \`/help member/2\`
- **Example**: \`/help findall/3\`
- **Example**: \`/help append\`

### **\`/status\`**
Check backend health and configuration
- Shows SWI-Prolog version, backend status, and usage statistics

---

##  **N3 Semantic Web Commands**

### **\`/n3_load <file|--content>\`**
Load N3/Turtle semantic data for reasoning
- **File**: \`/n3_load sample.n3\`
- **Inline**: \`/n3_load --content "@prefix : <http://example.org/> . :socrates a :Person ."\`

### **\`/n3_list [--limit N] [--format readable]\`**
Browse loaded triples with smart formatting
- **Example**: \`/n3_list --limit 20\`
- **Example**: \`/n3_list --format readable\`

### **\`/n3_reason [goal]\`**
Perform intelligent N3 reasoning and inference
- **All inferences**: \`/n3_reason\`
- **Specific goal**: \`/n3_reason rdf(X, type, Mortal)\`

### **\`/n3_explain <goal>\`**
Generate detailed proof trees with step-by-step explanations
- **Example**: \`/n3_explain rdf(socrates, type, Mortal)\`

---

##  **Pro Tips**

-  **Quick queries**: Just type your goal directly (no \`/query\` needed)
-  **Cancel operations**: Use Ctrl+C for long-running tasks
-  **Troubleshooting**: Run \`/status\` if commands aren't working
-  **Readable URIs**: N3 commands show prefixed names (e.g., \`:socrates\`)
-  **Rich results**: Variable bindings displayed in formatted tables
-  **Auto-retry**: Failed queries automatically retry with exponential backoff

---

##  **Quick Start Examples**

\`\`\`prolog
# Basic Prolog
member(X, [a,b,c])
append([1,2], [3,4], X)

# File operations
/consult family.pl
/help findall/3

# Semantic reasoning
/n3_load sample.n3
/n3_reason
/n3_explain rdf(socrates, type, Mortal)
\`\`\`

---

*Ready to explore the power of logic programming and semantic reasoning? Just ask me anything!* `;
  }
}

'use strict';

import { ExtensionContext, workspace } from 'vscode';
import * as fs from 'fs';
import * as path from 'path';
import jsesc from 'jsesc';
import { Utils } from '../utils/utils';
import { PlatformUtils, getPlatform, getPlatformDefaults } from '../utils/platformUtils';
import { AuthConfig } from '../features/apiMiddleware';

export class ConfigurationManager {
  private static instance: ConfigurationManager;

  private constructor() {}

  static getInstance(): ConfigurationManager {
    if (!ConfigurationManager.instance) {
      ConfigurationManager.instance = new ConfigurationManager();
    }
    return ConfigurationManager.instance;
  }

  // Initialization of workspace for dialect
  async initForDialect(context: ExtensionContext): Promise<void> {
    // get the user preferences for the extension
    const section = workspace.getConfiguration('prolog');
    const dialect = section.get<string>('dialect');

    // Use platform-aware executable path resolution
    let exec = section.get<string>('executablePath', '');
    if (!exec) {
      exec = getPlatformDefaults().defaultExecutablePath;
    }
    exec = PlatformUtils.normalizePath(exec);

    Utils.LINTERTRIGGER = section.get<string>('linter.run') || 'never';
    Utils.FORMATENABLED = section.get<boolean>('format.enabled') || false;
    Utils.DIALECT = dialect || 'swi';
    Utils.RUNTIMEPATH = jsesc(exec);

    // Use platform-aware path construction
    const exPath = PlatformUtils.normalizePath(context.extensionPath);
    Utils.EXPATH = exPath;

    // check if the dialect links have already been done
    const diaFile = PlatformUtils.joinPath(exPath, '.vscode', 'dialect.json');

    let lastDialect = '';
    try {
      if (await PlatformUtils.pathExists(diaFile)) {
        const dialectContent = fs.readFileSync(diaFile, 'utf8');
        lastDialect = JSON.parse(dialectContent).dialect;
      }
    } catch (error) {
      // File doesn't exist or is invalid, continue with setup
      const errorMsg = error instanceof Error ? error.message : String(error);
      console.log('[Platform] Dialect file not found or invalid, proceeding with setup:', errorMsg);
    }

    if (lastDialect === dialect) {
      return;
    }

    // creating links for the right dialect using platform-aware paths
    const symLinks = [
      {
        path: PlatformUtils.joinPath(exPath, 'syntaxes'),
        srcFile: `prolog.${dialect}.tmLanguage.json`,
        targetFile: 'prolog.tmLanguage.json',
      },
      {
        path: PlatformUtils.joinPath(exPath, 'snippets'),
        srcFile: `prolog.${dialect}.json`,
        targetFile: 'prolog.json',
      },
    ];

    await Promise.all(
      symLinks.map(async link => {
        const srcPath = PlatformUtils.joinPath(link.path, link.srcFile);
        const targetPath = PlatformUtils.joinPath(link.path, link.targetFile);

        // remove old link
        try {
          if (await PlatformUtils.pathExists(targetPath)) {
            fs.unlinkSync(targetPath);
          }
        } catch (error) {
          // Ignore errors when removing non-existent files, but log for debugging
          const errorMsg = error instanceof Error ? error.message : String(error);
          console.debug('[Platform] Failed to remove old symlink:', targetPath, errorMsg);
        }

        // make link
        try {
          // Try to create symlink, fallback to copy if symlink fails
          try {
            fs.symlinkSync(srcPath, targetPath);
          } catch (error) {
            // If symlink fails (e.g., on Windows without admin), copy the file
            const errorMsg = error instanceof Error ? error.message : String(error);
            console.debug('[Platform] Symlink failed, falling back to copy:', errorMsg);
            fs.copyFileSync(srcPath, targetPath);
          }
        } catch (err) {
          const platform = getPlatform();
          const errorMsg =
            platform === 'windows'
              ? 'VSC-Prolog failed in initialization. Try running VS Code as administrator or enable Developer Mode in Windows Settings.'
              : 'VSC-Prolog failed in initialization. Check file permissions.';
          console.error('[Extension] Initialization failed:', err);
          throw new Error(errorMsg);
        }
      })
    );

    // Ensure .vscode directory exists
    const vscodeDirPath = PlatformUtils.joinPath(exPath, '.vscode');
    if (!(await PlatformUtils.pathExists(vscodeDirPath))) {
      fs.mkdirSync(vscodeDirPath, { recursive: true });
    }

    // write the dialect to the json for later initialization
    fs.writeFileSync(diaFile, JSON.stringify({ dialect: dialect }));
  }

  // Helper function to create auth configuration from VSCode settings
  createAuthConfig(config: any): AuthConfig {
    const method = config.get('apiServer.auth.method', 'api_key') as string;
    // Ensure method is one of the valid values
    const validMethods = ['api_key', 'jwt_token', 'local_only', 'oauth2'] as const;
    const authMethod = validMethods.includes(method as any)
      ? (method as (typeof validMethods)[number])
      : 'api_key';

    return {
      method: authMethod,
      apiKeys: config.get('apiServer.auth.apiKeys', []) as string[],
      jwtSecret: config.get('apiServer.auth.jwtSecret', '') as string,
      localOnly: config.get('apiServer.auth.localOnly', true) as boolean,
      oauth2: {
        providers: config.get('apiServer.auth.oauth2.providers', ['google', 'github']) as string[],
        clientId: config.get('apiServer.auth.oauth2.clientId', '') as string,
        clientSecret: config.get('apiServer.auth.oauth2.clientSecret', '') as string,
        redirectUri: config.get('apiServer.auth.oauth2.redirectUri', '') as string,
        scope: config.get('apiServer.auth.oauth2.scope', 'read') as string,
      },
      roles: {
        admin: config.get('apiServer.auth.roles.admin', []) as string[],
        agent: config.get('apiServer.auth.roles.agent', []) as string[],
        readonly: config.get('apiServer.auth.roles.readonly', []) as string[],
        limited: config.get('apiServer.auth.roles.limited', []) as string[],
      },
      quotas: {
        admin: {
          requestsPerMinute: config.get(
            'apiServer.auth.quotas.admin.requestsPerMinute',
            1000
          ) as number,
          maxConcurrentSessions: config.get(
            'apiServer.auth.quotas.admin.maxConcurrentSessions',
            50
          ) as number,
        },
        agent: {
          requestsPerMinute: config.get(
            'apiServer.auth.quotas.agent.requestsPerMinute',
            300
          ) as number,
          maxConcurrentSessions: config.get(
            'apiServer.auth.quotas.agent.maxConcurrentSessions',
            20
          ) as number,
        },
        readonly: {
          requestsPerMinute: config.get(
            'apiServer.auth.quotas.readonly.requestsPerMinute',
            100
          ) as number,
          maxConcurrentSessions: config.get(
            'apiServer.auth.quotas.readonly.maxConcurrentSessions',
            10
          ) as number,
        },
        limited: {
          requestsPerMinute: config.get(
            'apiServer.auth.quotas.limited.requestsPerMinute',
            30
          ) as number,
          maxConcurrentSessions: config.get(
            'apiServer.auth.quotas.limited.maxConcurrentSessions',
            5
          ) as number,
        },
      },
    };
  }

  // Get current configuration values
  getConfiguration() {
    const config = workspace.getConfiguration('prolog');
    return {
      dialect: config.get('dialect', 'swi') as string,
      executablePath: config.get('executablePath', 'swipl') as string,
      linterTrigger: config.get('linter.run', 'never') as string,
      formatEnabled: config.get('format.enabled', false) as boolean,
      apiServerEnabled: config.get('apiServer.enabled', false) as boolean,
      webSocketEnabled: config.get('webSocketServer.enabled', true) as boolean,
      telemetryEnabled: config.get('telemetry.enabled', false) as boolean,
    };
  }
}

'use strict';

import {
  commands,
  DocumentFilter,
  ExtensionContext,
  window,
  languages,
  workspace,
  chat,
  ChatRequest,
  ChatResponseStream,
  CancellationToken,
  ThemeIcon,
  ChatResult,
  WorkspaceEdit,
} from 'vscode';
import * as path from 'path';
import PrologTerminal from '../features/prologTerminal';
import { loadEditHelpers } from '../features/editHelpers';
import { Utils } from '../utils/utils';
import PrologHoverProvider from '../features/hoverProvider';
import PrologDocumentHighlightProvider from '../features/documentHighlightProvider';
import {
  SnippetUpdater,
  SnippetUpdaterController,
  PrologCompletionProvider,
} from '../features/updateSnippets';
import { PrologFormatter } from '../features/prologFormatter';
import { PrologDefinitionProvider } from '../features/definitionProvider';
import { PrologReferenceProvider } from '../features/referenceProvider';
import PrologLinter from '../features/prologLinter';
import { PrologRefactor } from '../features/prologRefactor';
import { PrologBackend } from '../prologBackend';
import { PrologLSPExtension } from '../features/prologLSPExtension';
import { PrologLSPClient } from '../features/prologLSPClient';
import { MultiIDESupport } from '../features/multiIDESupport';
import { ApiServer, ApiServerConfig } from '../features/apiServer';
import {
  ExternalWebSocketManager,
  ExternalWebSocketConfig,
} from '../features/externalWebSocketManager';
import { SettingsWebviewProvider } from '../features/settingsWebviewProvider';
import { PrologActivityProvider } from '../features/prologActivityProvider';
import { PrologDashboardProvider } from '../features/prologDashboardProvider';
import { TelemetryCollector } from './telemetryCollector';
import { ChatHandler } from './chatHandler';
import { InstallationManager } from './installationManager';
import { ConfigurationManager } from './configurationManager';

export class ExtensionManager {
  private static instance: ExtensionManager;

  // Global instances
  private prologBackend: PrologBackend | null = null;
  private prologLSPClient: PrologLSPClient | null = null;
  private apiServer: ApiServer | null = null;
  private externalWebSocketManager: ExternalWebSocketManager | null = null;

  // Module instances
  private telemetry: TelemetryCollector;
  private chatHandler: ChatHandler;
  private installationManager: InstallationManager;
  private configurationManager: ConfigurationManager;

  private constructor() {
    this.telemetry = new TelemetryCollector();
    this.chatHandler = new ChatHandler(null, this.telemetry);
    this.installationManager = InstallationManager.getInstance();
    this.configurationManager = ConfigurationManager.getInstance();
  }

  static getInstance(): ExtensionManager {
    if (!ExtensionManager.instance) {
      ExtensionManager.instance = new ExtensionManager();
    }
    return ExtensionManager.instance;
  }

  // Main activation method
  async activate(context: ExtensionContext): Promise<void> {
    console.log('Congratulations, your extension "vsc-prolog" is now active! :)');

    // Check SWI-Prolog installation before proceeding
    await this.installationManager.checkAndHandleInstallation(context);

    // Initialize workspace for dialect
    await this.configurationManager.initForDialect(context);

    // Initialize core extension features
    await this.initializeCoreFeatures(context);

    // Initialize backend and services
    await this.initializeBackendServices(context);

    // Register providers and UI components
    this.registerProvidersAndUI(context);

    // Start backend automatically (with error handling)
    this.startBackend();
  }

  // Initialize core extension features
  private async initializeCoreFeatures(context: ExtensionContext): Promise<void> {
    // Filter the files to process
    const PROLOG_MODE: DocumentFilter = { language: 'prolog', scheme: 'file' };

    // Initialize utils class and load snippets file with its predicates
    Utils.init(context);

    // Automatic indent on change
    loadEditHelpers(context.subscriptions);

    // Register extension commands
    this.registerExtensionCommands(context);

    // Register installation commands
    this.installationManager.registerInstallationCommands(context);

    // Initialize linter if enabled
    this.initializeLinter(context, PROLOG_MODE);

    // Register language providers
    this.registerLanguageProviders(context, PROLOG_MODE);

    // Initialize terminal and snippets
    this.initializeTerminalAndSnippets(context, PROLOG_MODE);
  }

  // Register extension-specific commands
  private registerExtensionCommands(context: ExtensionContext): void {
    const myCommands = [
      {
        command: 'prolog.load.document',
        callback: () => {
          PrologTerminal.loadDocument();
        },
      },
      {
        command: 'prolog.query.goal',
        callback: () => {
          PrologTerminal.queryGoalUnderCursor();
        },
      },
      {
        command: 'prolog.refactorPredicate',
        callback: () => {
          new PrologRefactor().refactorPredUnderCursor();
        },
      },
      {
        command: 'prolog.openSettings',
        callback: () => {
          // This will be handled by the webview view registration
        },
      },
      {
        command: 'prolog.newFile',
        callback: async () => {
          await this.createNewPrologFile();
        },
      },
      {
        command: 'prolog.rerunQuery',
        callback: async (query: string) => {
          await this.rerunQuery(query);
        },
      },
      {
        command: 'prolog.clearQueryHistory',
        callback: async () => {
          window.showInformationMessage('Query history cleared');
        },
      },
      {
        command: 'prolog.viewLogs',
        callback: () => {
          commands.executeCommand('workbench.action.showLogs');
        },
      },
      {
        command: 'prolog.reportIssue',
        callback: () => {
          const issueUrl = 'https://github.com/mediaprophet/VSCode-Prolog-Toolkit/issues/new';
          commands.executeCommand('vscode.open', issueUrl);
        },
      },
    ];

    // Register commands
    myCommands.forEach(command => {
      context.subscriptions.push(commands.registerCommand(command.command, command.callback));
    });
  }

  // Initialize linter if enabled
  private initializeLinter(context: ExtensionContext, PROLOG_MODE: DocumentFilter): void {
    let linter: PrologLinter | undefined;
    if (Utils.LINTERTRIGGER !== 'never') {
      linter = new PrologLinter(context);
      linter.activate();

      // Register linter commands
      const linterCommands = [
        {
          command: 'prolog.linter.nextErrLine',
          callback: () => {
            linter?.nextErrLine();
          },
        },
        {
          command: 'prolog.linter.prevErrLine',
          callback: () => {
            linter?.prevErrLine();
          },
        },
      ];

      linterCommands.forEach(command => {
        context.subscriptions.push(commands.registerCommand(command.command, command.callback));
      });

      // Register code actions provider
      context.subscriptions.push(languages.registerCodeActionsProvider(PROLOG_MODE, linter));
    }
  }

  // Register language providers
  private registerLanguageProviders(context: ExtensionContext, PROLOG_MODE: DocumentFilter): void {
    // Hover provider
    context.subscriptions.push(
      languages.registerHoverProvider(PROLOG_MODE, new PrologHoverProvider())
    );

    // Highlight provider
    context.subscriptions.push(
      languages.registerDocumentHighlightProvider(PROLOG_MODE, new PrologDocumentHighlightProvider())
    );

    // Definition provider (go to definition command)
    context.subscriptions.push(
      languages.registerDefinitionProvider(PROLOG_MODE, new PrologDefinitionProvider())
    );

    // Reference provider (find all references command)
    context.subscriptions.push(
      languages.registerReferenceProvider(PROLOG_MODE, new PrologReferenceProvider())
    );

    // Auto completion provider
    context.subscriptions.push(
      languages.registerCompletionItemProvider(PROLOG_MODE, new PrologCompletionProvider())
    );

    // File formatting provider
    context.subscriptions.push(
      languages.registerDocumentRangeFormattingEditProvider(PROLOG_MODE, new PrologFormatter())
    );
    context.subscriptions.push(
      languages.registerDocumentFormattingEditProvider(PROLOG_MODE, new PrologFormatter())
    );
  }

  // Initialize terminal and snippets
  private initializeTerminalAndSnippets(context: ExtensionContext, PROLOG_MODE: DocumentFilter): void {
    // Create prolog terminal (load file command)
    context.subscriptions.push(PrologTerminal.init());

    // Add created predicate to the snippet
    const snippetUpdater = new SnippetUpdater();
    context.subscriptions.push(new SnippetUpdaterController(snippetUpdater));
    context.subscriptions.push(snippetUpdater);
  }

  // Initialize backend and services
  private async initializeBackendServices(context: ExtensionContext): Promise<void> {
    const config = workspace.getConfiguration('prolog');
    const swiplPath = config.get('executablePath', 'swipl') as string;

    // Initialize Prolog backend
    this.prologBackend = new PrologBackend({
      swiplPath,
      port: 3060,
      streamingEnabled: true,
      maxResultsPerChunk: 50,
    });

    // Update chat handler with backend reference
    this.chatHandler.updateBackend(this.prologBackend);

    // Initialize API server if enabled
    await this.initializeApiServer(config);

    // Initialize LSP services
    await this.initializeLSPServices(context);

    // Generate multi-IDE configurations
    await this.initializeMultiIDESupport();

    // Set up backend event handlers
    this.setupBackendEventHandlers();
  }

  // Initialize API server if enabled
  private async initializeApiServer(config: any): Promise<void> {
    const apiServerEnabled = config.get('apiServer.enabled', false) as boolean;
    if (apiServerEnabled && this.prologBackend) {
      try {
        const apiServerConfig: ApiServerConfig = {
          enabled: true,
          port: config.get('apiServer.port', 8080) as number,
          host: config.get('apiServer.host', 'localhost') as string,
          corsOrigins: config.get('apiServer.corsOrigins', ['http://localhost:*']) as string[],
          maxConnections: config.get('apiServer.maxConnections', 100) as number,
          requestTimeout: config.get('apiServer.requestTimeout', 60000) as number,
          rateLimiting: {
            enabled: config.get('apiServer.rateLimiting.enabled', true) as boolean,
            requestsPerMinute: config.get('apiServer.rateLimiting.requestsPerMinute', 60) as number,
            burstLimit: config.get('apiServer.rateLimiting.burstLimit', 10) as number,
          },
          auth: this.configurationManager.createAuthConfig(config),
        };

        this.apiServer = new ApiServer({
          config: apiServerConfig,
          prologBackend: this.prologBackend,
        });

        // Initialize external WebSocket manager if enabled
        const wsEnabled = config.get('webSocketServer.enabled', true) as boolean;
        if (wsEnabled) {
          const wsConfig: ExternalWebSocketConfig = {
            enabled: true,
            port: config.get('webSocketServer.port', 8081) as number,
            maxConnections: config.get('webSocketServer.maxConnections', 50) as number,
            heartbeatInterval: config.get('webSocketServer.heartbeatInterval', 30) as number,
            auth: apiServerConfig.auth,
          };

          this.externalWebSocketManager = new ExternalWebSocketManager(
            wsConfig,
            this.prologBackend.getNotificationManager()
          );
        }

        console.log('[Extension] API server and WebSocket manager initialized');
      } catch (error) {
        console.error('[Extension] Failed to initialize API server:', error);
        window.showErrorMessage(`Failed to initialize API server: ${error}`);
      }
    }
  }

  // Initialize LSP services
  private async initializeLSPServices(context: ExtensionContext): Promise<void> {
    if (!this.prologBackend) return;

    // Initialize LSP Extension (legacy support)
    const lspExtension = new PrologLSPExtension(context, this.prologBackend);
    lspExtension.registerFeatures();
    context.subscriptions.push(lspExtension);

    // Initialize full LSP Client
    this.prologLSPClient = new PrologLSPClient(context);

    // Start LSP client
    try {
      await this.prologLSPClient.start();
      console.log('[Extension] Prolog LSP Client started successfully');
    } catch (error) {
      console.error('[Extension] Failed to start Prolog LSP Client:', error);
      window.showWarningMessage(
        'Prolog LSP Client failed to start. Some features may not be available.'
      );
    }
  }

  // Initialize multi-IDE support
  private async initializeMultiIDESupport(): Promise<void> {
    const workspaceFolder = workspace.workspaceFolders?.[0];
    if (workspaceFolder) {
      try {
        await MultiIDESupport.generateIDEConfigurations(workspaceFolder.uri.fsPath);
        MultiIDESupport.generateLaunchConfigurations(workspaceFolder.uri.fsPath);

        // Detect available IDEs
        const availableIDEs = await MultiIDESupport.detectAvailableIDEs();
        if (availableIDEs.length > 1) {
          console.log('[Extension] Detected IDEs:', availableIDEs.join(', '));
        }
      } catch (error) {
        console.error('[Extension] Failed to generate multi-IDE configurations:', error);
      }
    }
  }

  // Set up backend event handlers
  private setupBackendEventHandlers(): void {
    if (!this.prologBackend) return;

    this.prologBackend.on('ready', () => {
      console.log('[Extension] Prolog backend ready');
      window.showInformationMessage('Prolog backend started successfully');
    });

    this.prologBackend.on('stopped', () => {
      console.log('[Extension] Prolog backend stopped');
      window.showWarningMessage('Prolog backend stopped');
    });

    this.prologBackend.on('restarted', () => {
      console.log('[Extension] Prolog backend restarted');
      window.showInformationMessage('Prolog backend restarted successfully');
    });

    this.prologBackend.on('error', error => {
      console.error('[Extension] Prolog backend error:', error);
      window.showErrorMessage(`Prolog backend error: ${error}`);
    });
  }

  // Register providers and UI components
  private registerProvidersAndUI(context: ExtensionContext): void {
    // Register chat participant with enhanced followup provider
    const chatParticipant = chat.createChatParticipant('prolog', 
      (request: ChatRequest, context: any, stream: ChatResponseStream, token: CancellationToken) =>
        this.chatHandler.handleChatRequest(request, context, stream, token)
    );
    chatParticipant.iconPath = new ThemeIcon('symbol-class');
    chatParticipant.followupProvider = {
      provideFollowups(result: ChatResult, _context: any, __token: CancellationToken) {
        return ExtensionManager.getInstance().getChatFollowups(result);
      },
    };
    context.subscriptions.push(chatParticipant);

    // Register settings webview provider
    const settingsProvider = new SettingsWebviewProvider(context.extensionUri);
    context.subscriptions.push(
      window.registerWebviewViewProvider(SettingsWebviewProvider.viewType, settingsProvider)
    );

    // Register activity bar providers
    const prologActivityProvider = new PrologActivityProvider(context);
    const prologDashboardProvider = new PrologDashboardProvider(context.extensionUri);

    context.subscriptions.push(
      window.registerTreeDataProvider('prologActivity', prologActivityProvider),
      window.registerTreeDataProvider('prologQueries', prologActivityProvider),
      window.registerTreeDataProvider('prologFiles', prologActivityProvider),
      window.registerWebviewViewProvider(PrologDashboardProvider.viewType, prologDashboardProvider)
    );

    // Refresh activity bar when installation status changes
    const refreshActivityBar = () => {
      prologActivityProvider.refresh();
    };

    // Listen for configuration changes to refresh activity bar
    context.subscriptions.push(
      workspace.onDidChangeConfiguration(event => {
        if (event.affectsConfiguration('prolog')) {
          refreshActivityBar();
        }
      })
    );
  }

  // Get chat followups based on result
  private getChatFollowups(result: ChatResult): any[] {
    const followups = [];

    // Context-aware followups based on command type
    if (result.metadata?.command === 'query') {
      followups.push({
        prompt: '/status',
        label: ' Check backend status',
      });
      followups.push({
        prompt: '/help findall/3',
        label: ' Learn about findall/3',
      });
    }

    if (result.metadata?.command === 'consult') {
      followups.push({
        prompt: '/query',
        label: ' Run a query',
      });
    }

    if (result.metadata?.command === 'error') {
      followups.push({
        prompt: '/status',
        label: ' Check what went wrong',
      });
      followups.push({
        prompt: '/help',
        label: ' Show help',
      });
    }

    if (result.metadata?.command === 'n3_load') {
      followups.push({
        prompt: '/n3_list --limit 10',
        label: ' List loaded triples',
      });
      followups.push({
        prompt: '/n3_reason',
        label: ' Start reasoning',
      });
    }

    // Always include help as fallback
    followups.push({
      prompt: '/help',
      label: ' Show all commands',
    });

    return followups;
  }

  // Start backend
  private startBackend(): void {
    try {
      this.prologBackend?.start();
    } catch (error) {
      console.error('[Extension] Failed to start Prolog backend:', error);
      // Don't show error immediately - let user trigger it via chat if needed
    }
  }

  // Helper method to create new Prolog file
  private async createNewPrologFile(): Promise<void> {
    const fileName = await window.showInputBox({
      prompt: 'Enter the name for the new Prolog file',
      value: 'untitled.pl',
      validateInput: value => {
        if (!value) return 'File name cannot be empty';
        if (!value.match(/\.(pl|pro|prolog|plt|ecl)$/)) {
          return 'File must have a Prolog extension (.pl, .pro, .prolog, .plt, .ecl)';
        }
        return null;
      },
    });

    if (fileName) {
      const workspaceFolder = workspace.workspaceFolders?.[0];
      if (workspaceFolder) {
        const uri = window.activeTextEditor?.document.uri || workspaceFolder.uri;
        const newFileUri = uri.with({ path: path.join(path.dirname(uri.fsPath), fileName) });
        const edit = new WorkspaceEdit();
        edit.createFile(newFileUri, { ignoreIfExists: false });
        await workspace.applyEdit(edit);
        await window.showTextDocument(newFileUri);
      } else {
        const doc = await workspace.openTextDocument({
          language: 'prolog',
          content: `% ${fileName}\n% New Prolog file\n\n`,
        });
        await window.showTextDocument(doc);
      }
    }
  }

  // Helper method to rerun query
  private async rerunQuery(query: string): Promise<void> {
    if (query && this.prologBackend?.isRunning()) {
      try {
        const response = await this.prologBackend.sendRequest('query', { goal: query });
        if (response.status === 'ok') {
          window.showInformationMessage(`Query executed: ${query}`);
        } else {
          window.showErrorMessage(`Query failed: ${response.error}`);
        }
      } catch (error) {
        window.showErrorMessage(`Query error: ${error}`);
      }
    }
  }

  // Deactivation method
  async deactivate(): Promise<void> {
    // Stop API server
    if (this.apiServer) {
      try {
        await this.apiServer.stop();
        console.log('[Extension] API server stopped');
      } catch (error) {
        console.error('[Extension] Error stopping API server:', error);
      }
      this.apiServer = null;
    }

    // Stop external WebSocket manager
    if (this.externalWebSocketManager) {
      try {
        await this.externalWebSocketManager.stop();
        console.log('[Extension] External WebSocket manager stopped');
      } catch (error) {
        console.error('[Extension] Error stopping WebSocket manager:', error);
      }
      this.externalWebSocketManager = null;
    }

    // Stop LSP client
    if (this.prologLSPClient) {
      try {
        await this.prologLSPClient.stop();
        console.log('[Extension] Prolog LSP Client stopped');
      } catch (error) {
        console.error('[Extension] Error stopping LSP Client:', error);
      }
      this.prologLSPClient = null;
    }

    // Stop backend
    if (this.prologBackend) {
      this.prologBackend.stop(true);
      this.prologBackend = null;
    }
  }
}

import { ChildProcessWithoutNullStreams, spawn, Signals } from 'child_process';
import { EventEmitter } from 'events';
import * as path from 'path';
import { v4 as uuidv4 } from 'uuid';
import axios from 'axios';
import { Timeout } from 'node:timers';
import { PlatformUtils } from './utils/platformUtils';
import { ExecutableFinder } from './utils/executableFinder';
import {
  QueryNotificationManager,
  QueryCallback,
  QueryNotificationOptions,
} from './features/queryNotificationManager';
import { ConcurrencyManager, ResourceQuota, QueryPriority } from './features/concurrencyManager';
import { QueryHistoryManager, QueryHistoryOptions } from './features/queryHistoryManager';
import { QueryScheduler, ScheduledQuery } from './features/queryScheduler';
import { SessionManager, SessionManagerOptions } from './features/sessionManager';
import { InstallationGuide } from './features/installationGuide';
import { UIHandler, defaultUIHandler } from './features/uiHandler';

export interface PrologBackendOptions {
  swiplPath?: string;
  args?: string[];
  cwd?: string;
  port?: number;
  maxResultsPerChunk?: number;
  streamingEnabled?: boolean;
  notificationOptions?: QueryNotificationOptions;
  concurrencyOptions?: {
    resourceQuota?: Partial<ResourceQuota>;
    enabled?: boolean;
  };
  historyOptions?: Partial<QueryHistoryOptions>;
  schedulerOptions?: {
    enabled?: boolean;
    maxScheduledQueries?: number;
    checkInterval?: number;
    enableRecurring?: boolean;
    enableConditional?: boolean;
    enableDependencies?: boolean;
  };
  sessionOptions?: Partial<SessionManagerOptions>;
  uiHandler?: UIHandler;
}

export class PrologBackend extends EventEmitter {
  private process: ChildProcessWithoutNullStreams | null = null;
  private options: PrologBackendOptions;
  private isReady: boolean = false;
  private pendingRequests: Map<
    string,
    { resolve: (v: any) => void; reject: (e: any) => void; timeout: Timeout }
  > = new Map();
  private intentionalStop: boolean = false;
  private _suppressStoppedEvent: boolean = false;
  private port: number;
  private maxResultsPerChunk: number;
  private streamingEnabled: boolean;
  private notificationManager: QueryNotificationManager;
  private concurrencyManager: ConcurrencyManager;
  private historyManager: QueryHistoryManager;
  private queryScheduler: QueryScheduler;
  private sessionManager: SessionManager;
  private runningQueries: Map<string, { cancel: () => void }> = new Map();
  private uiHandler: UIHandler;

  // Logging and diagnostics
  private log(msg: string) {
    // In production, use a proper logger or VS Code output channel
    console.log('[PrologBackend]', msg);
  }

  constructor(options: PrologBackendOptions = {}) {
    super();
    this.options = options;
    this.port = options.port || 3060;
    this.maxResultsPerChunk = options.maxResultsPerChunk || 50;
    this.streamingEnabled = options.streamingEnabled ?? true;
    this.uiHandler = options.uiHandler || defaultUIHandler;

    // Initialize notification manager
    this.notificationManager = new QueryNotificationManager({
      enableWebSocket: true,
      webSocketPort: (options.port || 3060) + 2, // Use port + 2 for WebSocket
      ...options.notificationOptions,
    });

    // Initialize concurrency manager
    this.concurrencyManager = new ConcurrencyManager(
      options.concurrencyOptions?.resourceQuota || {}
    );

    // Initialize history manager
    this.historyManager = new QueryHistoryManager({
      storageDir: PlatformUtils.joinPath(process.cwd(), '.prolog-history'),
      ...options.historyOptions,
    });

    // Initialize query scheduler
    this.queryScheduler = new QueryScheduler(
      this.concurrencyManager,
      this.historyManager,
      options.schedulerOptions
    );

    // Initialize session manager
    this.sessionManager = new SessionManager({
      storageDir: PlatformUtils.joinPath(process.cwd(), '.prolog-sessions'),
      ...options.sessionOptions,
    });

    // Set up integration between managers
    this.sessionManager.setIntegrationManagers(this.concurrencyManager, this.historyManager);

    // Set up event handlers
    this.setupEventHandlers();
  }

  /**
   * Set up event handlers for all managers
   */
  private setupEventHandlers(): void {
    // Notification manager events
    this.notificationManager.on('queryCancelled', (queryId: string) => {
      this.handleQueryCancellation(queryId);
    });

    // Concurrency manager events
    this.concurrencyManager.on('executeQuery', async event => {
      await this.handleConcurrencyManagerExecution(event);
    });

    this.concurrencyManager.on('queryCompleted', event => {
      this.emit('queryCompleted', event);
    });

    this.concurrencyManager.on('resourceUsageUpdated', usage => {
      this.emit('resourceUsageUpdated', usage);
    });

    // History manager events
    this.historyManager.on('queryAdded', entry => {
      this.emit('queryHistoryAdded', entry);
    });

    // Scheduler events
    this.queryScheduler.on('queryScheduleCompleted', query => {
      this.emit('queryScheduleCompleted', query);
    });

    this.queryScheduler.on('queryScheduleExecutionStarted', query => {
      this.emit('queryScheduleExecutionStarted', query);
    });

    // Session manager events
    this.sessionManager.on('sessionCreated', event => {
      this.emit('sessionCreated', event);
    });

    this.sessionManager.on('sessionSwitched', event => {
      this.emit('sessionSwitched', event);
    });

    this.sessionManager.on('sessionDeleted', event => {
      this.emit('sessionDeleted', event);
    });

    this.sessionManager.on('sessionStateSaved', event => {
      this.emit('sessionStateSaved', event);
    });

    this.sessionManager.on('sessionStateRestored', event => {
      this.emit('sessionStateRestored', event);
    });
  }

  /**
   * Handle query execution from concurrency manager
   */
  private async handleConcurrencyManagerExecution(event: any): Promise<void> {
    const { query, resolve, reject } = event;

    try {
      // Execute the actual query
      const result = await this.executeQueryDirect(query.cmd, query.params);
      resolve(result);
    } catch (error) {
      reject(error);
    }
  }

  isRunning(): boolean {
    return !!this.process;
  }

  stop(intentional = true) {
    this.log(
      '[DEBUG] stop() called. this.process=' +
        !!this.process +
        ', intentionalStop=' +
        this.intentionalStop +
        ', param=' +
        intentional
    );
    if (this.process) {
      this.intentionalStop = intentional;
      if (intentional) {
        this.log('[DEBUG] Setting intentionalStop=true and killing process');
      } else {
        this.log('[DEBUG] Killing process for restart (intentionalStop=false)');
      }
      this.process.kill();
      this.process = null;
      this.isReady = false;

      // Cancel all running queries
      this.runningQueries.forEach((query, queryId) => {
        this.notificationManager.cancelQuery(queryId);
      });
      this.runningQueries.clear();

      // Close all managers if intentional stop
      if (intentional) {
        this.notificationManager.close();
        this.concurrencyManager.dispose();
        this.historyManager.dispose();
        this.queryScheduler.dispose();
        this.sessionManager.dispose();
      }

      // Only emit 'stopped' if not in the middle of an automatic restart
      if (!this._suppressStoppedEvent) {
        this.log('[DEBUG] Emitting stopped event');
        this.emit('stopped');
      } else {
        this.log('[DEBUG] Suppressed stopped event');
      }
      this.log('Prolog backend stopped.');
    }
  }

  restart() {
    this.log('Restarting Prolog backend...');
    // Do not set intentionalStop=true for restarts
    this.stop(false);
    this.start();
  }

  /**
   * Handle query cancellation
   */
  private handleQueryCancellation(queryId: string): void {
    const query = this.runningQueries.get(queryId);
    if (query) {
      query.cancel();
      this.runningQueries.delete(queryId);
      this.log(`[DEBUG] Cancelled query ${queryId}`);
    }
  }

  async sendRequest(
    cmdOrBatch: string | Array<{ cmd: string; params?: Record<string, any>; timeoutMs?: number }>,
    params: Record<string, any> = {}
  ): Promise<any> {
    if (!this.isReady) {
      throw new Error('Prolog backend not ready');
    }
    // Single request
    if (typeof cmdOrBatch === 'string') {
      const cmd = cmdOrBatch;
      const id = uuidv4();
      const timeoutMs = typeof params.timeoutMs === 'number' ? params.timeoutMs : 10000;
      const paramsWithLimit = { ...params };
      if (typeof paramsWithLimit.time_limit === 'undefined') {
        paramsWithLimit.time_limit = Math.ceil(timeoutMs / 1000);
      }

      // Add streaming parameters if enabled
      if (this.streamingEnabled && !paramsWithLimit.disable_streaming) {
        paramsWithLimit.max_results_per_chunk =
          paramsWithLimit.max_results_per_chunk || this.maxResultsPerChunk;
        paramsWithLimit.streaming = true;
      }

      const { timeoutMs: _omit, ...paramsNoTimeout } = paramsWithLimit;
      const request = {
        id,
        cmd,
        ...paramsNoTimeout,
        protocol: 1,
      };
      const timeout = setTimeout(() => {
        throw new Error('Prolog request timeout');
      }, timeoutMs);
      try {
        const response = await axios.post(`http://localhost:${this.port}`, request);
        clearTimeout(timeout);
        return response.data;
      } catch (err) {
        clearTimeout(timeout);
        throw err;
      }
    }
    // Batch request
    const batch = cmdOrBatch.map(req => {
      const id = uuidv4();
      const paramsWithLimit = { ...(req.params || {}) };
      if (typeof req.timeoutMs === 'number' && typeof paramsWithLimit.time_limit === 'undefined') {
        paramsWithLimit.time_limit = Math.ceil(req.timeoutMs / 1000);
      }

      // Add streaming parameters if enabled
      if (this.streamingEnabled && !paramsWithLimit.disable_streaming) {
        paramsWithLimit.max_results_per_chunk =
          paramsWithLimit.max_results_per_chunk || this.maxResultsPerChunk;
        paramsWithLimit.streaming = true;
      }

      return {
        id,
        cmd: req.cmd,
        ...paramsWithLimit,
        protocol: 1,
        timeoutMs: req.timeoutMs,
      };
    });
    const batchToSend = batch.map(({ timeoutMs: _omit, ...toSend }) => toSend);
    const responses = await Promise.all(
      batch.map(async req => {
        const timeout = setTimeout(() => {
          throw new Error('Prolog request timeout in batch');
        }, req.timeoutMs || 10000);
        try {
          const response = await axios.post(`http://localhost:${this.port}`, req);
          clearTimeout(timeout);
          return response.data;
        } catch (err) {
          clearTimeout(timeout);
          throw err;
        }
      })
    );
    return responses;
  }

  /**
   * Send request with notification support
   */
  async sendRequestWithNotifications(
    cmdOrBatch: string | Array<{ cmd: string; params?: Record<string, any>; timeoutMs?: number }>,
    params: Record<string, any> = {},
    callback?: QueryCallback
  ): Promise<any> {
    if (!this.isReady) {
      throw new Error('Prolog backend not ready');
    }

    // Single request with notifications
    if (typeof cmdOrBatch === 'string') {
      const cmd = cmdOrBatch;
      const queryId = uuidv4();
      const timeoutMs = typeof params.timeoutMs === 'number' ? params.timeoutMs : 10000;

      // Register query for tracking
      this.notificationManager.registerQuery(queryId, callback);
      this.notificationManager.updateQueryStatus(queryId, { status: 'running' });

      const paramsWithLimit = { ...params };
      if (typeof paramsWithLimit.time_limit === 'undefined') {
        paramsWithLimit.time_limit = Math.ceil(timeoutMs / 1000);
      }

      // Add streaming parameters if enabled
      if (this.streamingEnabled && !paramsWithLimit.disable_streaming) {
        paramsWithLimit.max_results_per_chunk =
          paramsWithLimit.max_results_per_chunk || this.maxResultsPerChunk;
        paramsWithLimit.streaming = true;
      }

      const { timeoutMs: _omit, ...paramsNoTimeout } = paramsWithLimit;
      const request = {
        id: queryId,
        cmd,
        ...paramsNoTimeout,
        protocol: 1,
      };

      // Set up cancellation support
      let cancelled = false;
      const cancelToken = {
        cancel: () => {
          cancelled = true;
        },
      };
      this.runningQueries.set(queryId, cancelToken);

      const timeout = setTimeout(() => {
        if (!cancelled) {
          this.notificationManager.updateQueryStatus(queryId, { status: 'timeout' });
          this.runningQueries.delete(queryId);
        }
      }, timeoutMs);

      try {
        // Simulate progress updates for long-running queries
        const progressInterval = setInterval(() => {
          if (!cancelled && this.runningQueries.has(queryId)) {
            const elapsed =
              Date.now() -
              (this.notificationManager.getQueryStatus(queryId)?.startTime || Date.now());
            const progress = Math.min(90, (elapsed / timeoutMs) * 100);
            this.notificationManager.updateQueryProgress(queryId, progress, 'Processing query...');
          } else {
            clearInterval(progressInterval);
          }
        }, 1000);

        const response = await axios.post(`http://localhost:${this.port}`, request);

        clearTimeout(timeout);
        clearInterval(progressInterval);
        this.runningQueries.delete(queryId);

        if (!cancelled) {
          this.notificationManager.completeQuery(queryId, response.data);
        }

        return response.data;
      } catch (err) {
        clearTimeout(timeout);
        this.runningQueries.delete(queryId);

        if (!cancelled) {
          this.notificationManager.failQuery(queryId, err);
        }
        throw err;
      }
    }

    // Batch requests with notifications
    const batch = cmdOrBatch.map((req, index) => {
      const queryId = uuidv4();

      // Register each batch item for tracking
      this.notificationManager.registerQuery(queryId, callback, true, index, cmdOrBatch.length);

      const paramsWithLimit = { ...(req.params || {}) };
      if (typeof req.timeoutMs === 'number' && typeof paramsWithLimit.time_limit === 'undefined') {
        paramsWithLimit.time_limit = Math.ceil(req.timeoutMs / 1000);
      }

      // Add streaming parameters if enabled
      if (this.streamingEnabled && !paramsWithLimit.disable_streaming) {
        paramsWithLimit.max_results_per_chunk =
          paramsWithLimit.max_results_per_chunk || this.maxResultsPerChunk;
        paramsWithLimit.streaming = true;
      }

      return {
        id: queryId,
        cmd: req.cmd,
        ...paramsWithLimit,
        protocol: 1,
        timeoutMs: req.timeoutMs,
      };
    });

    // Execute batch with individual tracking
    const responses = await Promise.all(
      batch.map(async (req, index) => {
        const queryId = req.id;
        this.notificationManager.updateQueryStatus(queryId, { status: 'running' });

        // Set up cancellation support
        let cancelled = false;
        const cancelToken = {
          cancel: () => {
            cancelled = true;
          },
        };
        this.runningQueries.set(queryId, cancelToken);

        const timeout = setTimeout(() => {
          if (!cancelled) {
            this.notificationManager.updateQueryStatus(queryId, { status: 'timeout' });
            this.runningQueries.delete(queryId);
          }
        }, req.timeoutMs || 10000);

        try {
          // Progress simulation for batch items
          const progressInterval = setInterval(() => {
            if (!cancelled && this.runningQueries.has(queryId)) {
              const elapsed =
                Date.now() -
                (this.notificationManager.getQueryStatus(queryId)?.startTime || Date.now());
              const progress = Math.min(90, (elapsed / (req.timeoutMs || 10000)) * 100);
              this.notificationManager.updateQueryProgress(
                queryId,
                progress,
                `Processing batch item ${index + 1}/${batch.length}...`
              );
            } else {
              clearInterval(progressInterval);
            }
          }, 1000);

          const { timeoutMs: _omit, ...reqToSend } = req;
          const response = await axios.post(`http://localhost:${this.port}`, reqToSend);

          clearTimeout(timeout);
          clearInterval(progressInterval);
          this.runningQueries.delete(queryId);

          if (!cancelled) {
            this.notificationManager.completeQuery(queryId, response.data);
          }

          return response.data;
        } catch (err) {
          clearTimeout(timeout);
          this.runningQueries.delete(queryId);

          if (!cancelled) {
            this.notificationManager.failQuery(queryId, err);
          }
          throw err;
        }
      })
    );

    return responses;
  }

  /**
   * Send a streaming request that can handle large result sets
   */
  async sendStreamingRequest(
    cmd: string,
    params: Record<string, any> = {},
    onChunk?: (chunk: any, isFirst: boolean, isLast: boolean) => void
  ): Promise<any> {
    if (!this.isReady) {
      throw new Error('Prolog backend not ready');
    }

    const streamingParams = {
      ...params,
      streaming: true,
      max_results_per_chunk: params.max_results_per_chunk || this.maxResultsPerChunk,
    };

    // For now, we'll simulate streaming by chunking the response
    // In a full implementation, this would use WebSockets or Server-Sent Events
    const response = await this.sendRequest(cmd, streamingParams);

    if (response.status === 'ok' && response.results && Array.isArray(response.results)) {
      const results = response.results;
      const chunkSize = this.maxResultsPerChunk;

      if (results.length <= chunkSize) {
        // Small result set, return as single chunk
        if (onChunk) {
          onChunk(response, true, true);
        }
        return response;
      }

      // Large result set, chunk it
      const chunks = [];
      for (let i = 0; i < results.length; i += chunkSize) {
        const chunk = results.slice(i, i + chunkSize);
        const isFirst = i === 0;
        const isLast = i + chunkSize >= results.length;

        const chunkResponse = {
          ...response,
          results: chunk,
          chunk_info: {
            chunk_index: Math.floor(i / chunkSize),
            chunk_size: chunk.length,
            total_results: results.length,
            is_first: isFirst,
            is_last: isLast,
          },
        };

        chunks.push(chunkResponse);

        if (onChunk) {
          onChunk(chunkResponse, isFirst, isLast);
        }
      }

      // Return summary response
      return {
        ...response,
        results: results.slice(0, chunkSize), // First chunk
        total_chunks: chunks.length,
        streaming: true,
        chunk_info: {
          chunk_index: 0,
          chunk_size: Math.min(chunkSize, results.length),
          total_results: results.length,
          is_first: true,
          is_last: chunks.length === 1,
        },
      };
    }

    return response;
  }

  /**
   * Get streaming configuration
   */
  getStreamingConfig() {
    return {
      enabled: this.streamingEnabled,
      maxResultsPerChunk: this.maxResultsPerChunk,
    };
  }

  /**
   * Update streaming configuration
   */
  updateStreamingConfig(config: { enabled?: boolean; maxResultsPerChunk?: number }) {
    if (config.enabled !== undefined) {
      this.streamingEnabled = config.enabled;
    }
    if (config.maxResultsPerChunk !== undefined) {
      this.maxResultsPerChunk = config.maxResultsPerChunk;
    }
  }

  /**
   * Get notification manager for direct access
   */
  getNotificationManager(): QueryNotificationManager {
    return this.notificationManager;
  }

  /**
   * Cancel a running query
   */
  cancelQuery(queryId: string): boolean {
    return this.notificationManager.cancelQuery(queryId);
  }

  /**
   * Get query status
   */
  getQueryStatus(queryId: string) {
    return this.notificationManager.getQueryStatus(queryId);
  }

  /**
   * Get all active queries
   */
  getActiveQueries() {
    return this.notificationManager.getActiveQueries();
  }

  /**
   * Get query statistics
   */
  getQueryStatistics() {
    return this.notificationManager.getStatistics();
  }

  /**
   * Execute a query directly (used internally by concurrency manager)
   */
  private async executeQueryDirect(cmd: string, params: Record<string, any> = {}): Promise<any> {
    const id = uuidv4();
    const timeoutMs = typeof params.timeoutMs === 'number' ? params.timeoutMs : 10000;
    const paramsWithLimit = { ...params };
    if (typeof paramsWithLimit.time_limit === 'undefined') {
      paramsWithLimit.time_limit = Math.ceil(timeoutMs / 1000);
    }

    // Add streaming parameters if enabled
    if (this.streamingEnabled && !paramsWithLimit.disable_streaming) {
      paramsWithLimit.max_results_per_chunk =
        paramsWithLimit.max_results_per_chunk || this.maxResultsPerChunk;
      paramsWithLimit.streaming = true;
    }

    const { timeoutMs: _omit, ...paramsNoTimeout } = paramsWithLimit;
    const request = {
      id,
      cmd,
      ...paramsNoTimeout,
      protocol: 1,
    };

    const timeout = setTimeout(() => {
      throw new Error('Prolog request timeout');
    }, timeoutMs);

    try {
      const response = await axios.post(`http://localhost:${this.port}`, request);
      clearTimeout(timeout);
      return response.data;
    } catch (err) {
      clearTimeout(timeout);
      throw err;
    }
  }

  /**
   * Send request with advanced concurrency control
   */
  async sendRequestWithConcurrency(
    cmd: string,
    params: Record<string, any> = {},
    priority: Partial<QueryPriority> = {},
    resourceRequirements?: { memoryMB?: number; cpuPercent?: number }
  ): Promise<any> {
    if (!this.isReady) {
      throw new Error('Prolog backend not ready');
    }

    const queryId = uuidv4();

    // Queue the query with concurrency control
    return await this.concurrencyManager.queueQuery(
      queryId,
      cmd,
      params,
      priority,
      resourceRequirements
    );
  }

  /**
   * Schedule a query for future execution
   */
  async scheduleQuery(
    cmd: string,
    params: Record<string, any> = {},
    scheduleType: 'immediate' | 'delayed' | 'recurring' | 'conditional' = 'immediate',
    scheduleConfig: any = {},
    priority: Partial<QueryPriority> = {},
    metadata?: any
  ): Promise<string> {
    if (!this.isReady) {
      throw new Error('Prolog backend not ready');
    }

    const queryId = uuidv4();

    await this.queryScheduler.scheduleQuery(
      queryId,
      cmd,
      params,
      scheduleType,
      scheduleConfig,
      priority,
      metadata
    );

    return queryId;
  }

